  <!-- three.js and OrbitControls via CDN -->
  <script src="https://unpkg.com/three@0.157.0/build/three.min.js"></script>
  <script src="https://unpkg.com/three@0.157.0/examples/js/controls/OrbitControls.js"></script>

  <script>
    // Basic three.js setup
    const container = document.getElementById('canvas-container');
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xf0f0f0);

    const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    container.appendChild(renderer.domElement);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.enablePan = true;

    // Lighting
    const ambient = new THREE.AmbientLight(0xffffff, 0.7);
    scene.add(ambient);
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
    dirLight.position.set(3, 5, 6);
    scene.add(dirLight);

    // Global state
    let currentGroup = null;
    let currentSolidIndex = 1;
    let currentFoldT = 0; // slider value
    const unfoldedColor = new THREE.Color('#F5E0E0'); // light pink for unfolded
    const tmpColor = new THREE.Color();

    // Utility vector helper
    const V = (x, y, z = 0) => new THREE.Vector3(x, y, z);

    // Resize handling
    function onResize() {
      const { clientWidth, clientHeight } = container;
      renderer.setSize(clientWidth, clientHeight);
      camera.aspect = clientWidth / clientHeight;
      camera.updateProjectionMatrix();
      render();
    }
    window.addEventListener('resize', onResize);

    // Fit camera to object
    function fitCameraToObject(object, fitOffset = 1.2) {
      const box = new THREE.Box3().setFromObject(object);
      const size = new THREE.Vector3();
      const center = new THREE.Vector3();
      box.getSize(size);
      box.getCenter(center);

      const maxDim = Math.max(size.x, size.y, size.z);
      const fov = camera.fov * (Math.PI / 180);
      let cameraZ = Math.abs(maxDim / (2 * Math.tan(fov / 2)));
      cameraZ *= fitOffset;

      // set camera position and target
      camera.position.set(center.x + cameraZ, center.y + cameraZ, center.z + cameraZ);
      controls.target.copy(center);
      controls.update();

      // near/far
      const minZ = box.min.z;
      const maxZ = box.max.z;
      const near = Math.max(0.01, cameraZ - maxDim * 4);
      const far = cameraZ + maxDim * 6;
      camera.near = near;
      camera.far = far;
      camera.updateProjectionMatrix();
    }

    // Dispose helper
    function disposeObject3D(obj) {
      obj.traverse((child) => {
        if (child.geometry) {
          child.geometry.dispose();
        }
        if (child.material) {
          if (Array.isArray(child.material)) {
            child.material.forEach(m => m.dispose && m.dispose());
          } else {
            child.material.dispose && child.material.dispose();
          }
        }
      });
    }

    // Build a generic polygon face mesh (triangulated fan)
    function makeFaceMesh(vertices, colorHex) {
      const geom = new THREE.BufferGeometry();
      const vCount = vertices.length;
      if (vCount < 3) throw new Error('Face needs at least 3 vertices');

      // Triangulate as fan: (0, i, i+1)
      const positions = [];
      for (let i = 1; i < vCount - 1; i++) {
        const a = vertices[0], b = vertices[i], c = vertices[i + 1];
        positions.push(a.x, a.y, a.z, b.x, b.y, b.z, c.x, c.y, c.z);
      }
      const posAttr = new THREE.Float32BufferAttribute(positions, 3);
      geom.setAttribute('position', posAttr);
      geom.computeVertexNormals();

      const mat = new THREE.MeshStandardMaterial({
        color: new THREE.Color(colorHex || '#cccccc'),
        side: THREE.DoubleSide,
        flatShading: true,
        metalness: 0.05,
        roughness: 0.85
      });
      return new THREE.Mesh(geom, mat);
    }

    // Equilateral third vertex on segment p->q in XY plane.
    function equilateralThird(p, q, preferDirection = 'auto') {
      // midpoint m, vector v = q - p, perpendicular r90 = (-vy, vx)
      const m = new THREE.Vector3().addVectors(p, q).multiplyScalar(0.5);
      const v = new THREE.Vector3().subVectors(q, p);
      const len = v.length();
      if (len === 0) return m.clone();
      const r90 = new THREE.Vector3(-v.y, v.x, 0).normalize().multiplyScalar(Math.sqrt(3) / 2 * len);

      const c1 = new THREE.Vector3().addVectors(m, r90);
      const c2 = new THREE.Vector3().subVectors(m, r90);

      if (preferDirection === 'left') {
        return (c1.x < c2.x) ? c1 : c2;
      } else if (preferDirection === 'right') {
        return (c1.x > c2.x) ? c1 : c2;
      } else if (preferDirection === 'up') {
        return (c1.y > c2.y) ? c1 : c2;
      } else if (preferDirection === 'down') {
        return (c1.y < c2.y) ? c1 : c2;
      }
      // auto: pick the one farther from origin for variety
      return (c1.lengthSq() > c2.lengthSq()) ? c1 : c2;
    }

    // Builder: create solid from face definitions with hinge pivots
    function buildSolid(def) {
      // def.faces: array of {vertices: [Vector3...], color: '#hex', parent: idx|null, hinge: {a:Vector3,b:Vector3} or null, finalAngle: radians}
      const group = new THREE.Group();
      const nodes = []; // per-face container (Object3D)
      const meshes = []; // per-face mesh
      const pivots = []; // pivot containers (for child faces; base has null)
      const axes = []; // axis vectors for pivots
      const angles = []; // final angles
      const colors = []; // final colors
      const unfoldedColors = []; // unfolded color per face

      def.faces.forEach((f, i) => {
        const colorHex = f.color || '#aaaaaa';
        const mesh = makeFaceMesh(f.vertices, colorHex);
        meshes[i] = mesh;
        colors[i] = new THREE.Color(colorHex);
        unfoldedColors[i] = unfoldedColor.clone();

        if (f.parent == null) {
          // Base face: directly on group in a dedicated container
          const container = new THREE.Object3D();
          container.add(mesh);
          nodes[i] = container;
          pivots[i] = null;
          axes[i] = null;
          angles[i] = 0;
          group.add(container);
        } else {
          // Child face: create pivot on parent's container along hinge
          const parentIdx = f.parent;
          const parentContainer = nodes[parentIdx];
          const pivot = new THREE.Object3D();

          // hinge axis and position
          const a = f.hinge.a.clone();
          const b = f.hinge.b.clone();
          const axis = new THREE.Vector3().subVectors(b, a).normalize();
          pivot.position.copy(a);

          // Place mesh in pivot local space (subtract pivot origin from each vertex)
          const shiftedVerts = f.vertices.map(v => new THREE.Vector3().subVectors(v, a));
          // Rebuild mesh with shifted vertices
          const shiftedMesh = makeFaceMesh(shiftedVerts, colorHex);
          // Copy material options (we replace original mesh)
          shiftedMesh.material.side = THREE.DoubleSide;
          shiftedMesh.material.flatShading = true;

          pivot.add(shiftedMesh);
          parentContainer.add(pivot);

          nodes[i] = pivot; // for children to attach to
          meshes[i] = shiftedMesh;
          pivots[i] = pivot;
          axes[i] = axis;
          angles[i] = (typeof f.finalAngle === 'number') ? f.finalAngle : Math.PI / 2;
        }
      });

      // Store helpers on group for folding updates
      group.userData = {
        meshes,
        pivots,
        axes,
        angles,
        colors,
        unfoldedColors,
        updateFold: (t) => {
          // rotate pivots and interpolate colors
          for (let i = 0; i < pivots.length; i++) {
            const pivot = pivots[i];
            if (pivot && axes[i]) {
              const angle = angles[i] * t;
              pivot.quaternion.setFromAxisAngle(axes[i], angle);
            }
            // color interpolation on corresponding mesh
            const mesh = meshes[i];
            if (mesh && mesh.material && colors[i]) {
              tmpColor.copy(unfoldedColors[i]).lerp(colors[i], t);
              mesh.material.color.copy(tmpColor);
              mesh.material.needsUpdate = true;
            }
          }
        }
      };

      return group;
    }

    // SOLID DEFINITIONS

    // Solid 1: A net of 6 equilateral triangles around a central vertical edge
    function solid1Def() {
      const s = 1;
      const h = Math.sqrt(3) / 2 * s; // equilateral height
      // Central vertical edge A-B
      const A = V(0, -0.5, 0);
      const B = V(0,  0.5, 0);
      const C = V(-h, 0, 0); // left apex
      const D = V( h, 0, 0); // right apex

      // Colors palette
      const palette = ['#E47A55', '#A4C6E3', '#F3C76E', '#8FD3A9', '#C199E6', '#F29BB2'];

      // Triangles
      const faces = [
        // 0: Base left triangle (parent null)
        { vertices: [A.clone(), B.clone(), C.clone()], color: palette[0], parent: null, hinge: null, finalAngle: 0 },

        // 1: Right-middle triangle sharing edge A-B, child of 0
        { vertices: [A.clone(), B.clone(), D.clone()], color: palette[1], parent: 0, hinge: { a: A.clone(), b: B.clone() }, finalAngle: 1.91 },

        // 2: Left-top triangle sharing edge B-C, child of 0
        (function(){
          const p = B.clone(), q = C.clone();
          const apex = equilateralThird(p, q, 'left');
          return { vertices: [p, q, apex], color: palette[2], parent: 0, hinge: { a: p.clone(), b: q.clone() }, finalAngle: 1.91 };
        })(),

        // 3: Left-bottom triangle sharing edge A-C, child of 0
        (function(){
          const p = A.clone(), q = C.clone();
          const apex = equilateralThird(p, q, 'left');
          return { vertices: [p, q, apex], color: palette[3], parent: 0, hinge: { a: p.clone(), b: q.clone() }, finalAngle: 1.91 };
        })(),

        // 4: Right-top triangle sharing edge B-D, child of 1
        (function(){
          const p = B.clone(), q = D.clone();
          const apex = equilateralThird(p, q, 'right');
          return { vertices: [p, q, apex], color: palette[4], parent: 1, hinge: { a: p.clone(), b: q.clone() }, finalAngle: 1.91 };
        })(),

        // 5: Right-bottom triangle sharing edge A-D, child of 1
        (function(){
          const p = A.clone(), q = D.clone();
          const apex = equilateralThird(p, q, 'right');
          return { vertices: [p, q, apex], color: palette[5], parent: 1, hinge: { a: p.clone(), b: q.clone() }, finalAngle: 1.91 };
        })(),
      ];
      return { faces };
    }

    // Solid 2: Cube-like net with 6 squares (cross layout)
    function solid2Def() {
      const s = 1;
      const half = s / 2;

      // Base square S0 centered at origin
      const P1 = V(-half, -half, 0);
      const P2 = V( half, -half, 0);
      const P3 = V( half,  half, 0);
      const P4 = V(-half,  half, 0);

      const palette = ['#A4C6E3', '#E47A55', '#8FD3A9', '#F3C76E', '#C199E6', '#F29BB2'];

      const faces = [
        // 0 Base
        { vertices: [P1.clone(), P2.clone(), P3.clone(), P4.clone()], color: palette[0], parent: null, hinge: null, finalAngle: 0 },

        // 1 Right square (hinge P2->P3)
        { vertices: [V( half, -half, 0), V(1.5, -half, 0), V(1.5, half, 0), V( half, half, 0)], color: palette[1], parent: 0, hinge: { a: P2.clone(), b: P3.clone() }, finalAngle: Math.PI / 2 },

        // 2 Left square (hinge P1->P4)
        { vertices: [V(-1.5, -half, 0), V(-half, -half, 0), V(-half,  half, 0), V(-1.5, half, 0)], color: palette[2], parent: 0, hinge: { a: P1.clone(), b: P4.clone() }, finalAngle: Math.PI / 2 },

        // 3 Top square (hinge P4->P3)
        { vertices: [V(-half, half, 0), V( half, half, 0), V( half, 1.5, 0), V(-half, 1.5, 0)], color: palette[3], parent: 0, hinge: { a: P4.clone(), b: P3.clone() }, finalAngle: Math.PI / 2 },

        // 4 Bottom square (hinge P1->P2)
        { vertices: [V(-half, -1.5, 0), V( half, -1.5, 0), V( half, -half, 0), V(-half, -half, 0)], color: palette[4], parent: 0, hinge: { a: P1.clone(), b: P2.clone() }, finalAngle: Math.PI / 2 },

        // 5 Top of top (attached to 3), hinge along its top edge
        (function(){
          const A = V(-half, 1.5, 0), B = V( half, 1.5, 0);
          return { vertices: [V(-half, 1.5, 0), V( half, 1.5, 0), V( half, 2.5, 0), V(-half, 2.5, 0)], color: palette[5], parent: 3, hinge: { a: A.clone(), b: B.clone() }, finalAngle: Math.PI / 2 };
        })(),
      ];
      return { faces };
    }

    // Solid 3: Square pyramid net (square base + 4 isosceles/equilateral triangles)
    function solid3Def() {
      const s = 1;
      const half = s / 2;
      // Base square centered at origin
      const P1 = V(-half, -half, 0);
      const P2 = V( half, -half, 0);
      const P3 = V( half,  half, 0);
      const P4 = V(-half,  half, 0);

      const palette = ['#8FD3A9', '#E47A55', '#A4C6E3', '#C199E6', '#F3C76E'];

      const faces = [
        // 0 Base square
        { vertices: [P1.clone(), P2.clone(), P3.clone(), P4.clone()], color: palette[0], parent: null, hinge: null, finalAngle: 0 },

        // 1 Top triangle along P4->P3
        (function(){
          const a = P4.clone(), b = P3.clone();
          const apex = equilateralThird(a, b, 'up');
          return { vertices: [a, b, apex], color: palette[1], parent: 0, hinge: { a: a.clone(), b: b.clone() }, finalAngle: 2.0 };
        })(),

        // 2 Bottom triangle along P1->P2
        (function(){
          const a = P1.clone(), b = P2.clone();
          const apex = equilateralThird(a, b, 'down');
          return { vertices: [a, b, apex], color: palette[2], parent: 0, hinge: { a: a.clone(), b: b.clone() }, finalAngle: 2.0 };
        })(),

        // 3 Left triangle along P1->P4
        (function(){
          const a = P1.clone(), b = P4.clone();
          const apex = equilateralThird(a, b, 'left');
          return { vertices: [a, b, apex], color: palette[3], parent: 0, hinge: { a: a.clone(), b: b.clone() }, finalAngle: 2.0 };
        })(),

        // 4 Right triangle along P2->P3
        (function(){
          const a = P2.clone(), b = P3.clone();
          const apex = equilateralThird(a, b, 'right');
          return { vertices: [a, b, apex], color: palette[4], parent: 0, hinge: { a: a.clone(), b: b.clone() }, finalAngle: 2.0 };
        })(),
      ];
      return { faces };
    }

    // Create solid by index
    function createSolidByIndex(idx) {
      const def = (idx === 1) ? solid1Def() : (idx === 2) ? solid2Def() : solid3Def();
      const group = buildSolid(def);
      return group;
    }

    // Update fold for current object
    function applyFold(t) {
      currentFoldT = t;
      if (currentGroup && currentGroup.userData && currentGroup.userData.updateFold) {
        currentGroup.userData.updateFold(t);
        render();
      }
    }

    // Load (or reload) solid
    function loadSolid(idx) {
      // Remove old
      if (currentGroup) {
        scene.remove(currentGroup);
        disposeObject3D(currentGroup);
        currentGroup = null;
      }
      // Create new
      currentGroup = createSolidByIndex(idx);
      scene.add(currentGroup);
      // Apply current slider value
      applyFold(parseFloat(document.getElementById('slider-fold').value) || 0);
      // Fit and reset controls
      fitCameraToObject(currentGroup, 1.35);
      controls.saveState();
      render();
    }

    // Render loop
    function render() {
      renderer.render(scene, camera);
    }
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }

    // UI wiring
    const btn1 = document.getElementById('btn-solid-1');
    const btn2 = document.getElementById('btn-solid-2');
    const btn3 = document.getElementById('btn-solid-3');
    const sliderFold = document.getElementById('slider-fold');
    const btnReset = document.getElementById('btn-reset-view');

    function setActiveButton(idx) {
      [btn1, btn2, btn3].forEach((b, i) => {
        if (i === (idx - 1)) b.classList.add('active');
        else b.classList.remove('active');
      });
    }

    btn1.addEventListener('click', () => {
      if (currentSolidIndex !== 1) {
        currentSolidIndex = 1;
        setActiveButton(1);
        loadSolid(1);
      }
    });
    btn2.addEventListener('click', () => {
      if (currentSolidIndex !== 2) {
        currentSolidIndex = 2;
        setActiveButton(2);
        loadSolid(2);
      }
    });
    btn3.addEventListener('click', () => {
      if (currentSolidIndex !== 3) {
        currentSolidIndex = 3;
        setActiveButton(3);
        loadSolid(3);
      }
    });

    sliderFold.addEventListener('input', (e) => {
      const t = parseFloat(e.target.value) || 0;
      applyFold(t);
    });

    btnReset.addEventListener('click', () => {
      controls.reset();
      render();
    });

    // Initialize
    function init() {
      onResize();
      currentSolidIndex = 1;
      setActiveButton(1);
      loadSolid(1);
      animate();
    }
    init();
  </script>
</body>
</html>
