  </div>

  <script>
    // State and defaults
    const DEFAULTS = {
      alpha13: 0.6,  // between A and C
      alpha23: 3.93  // between B and C
    };

    const state = {
      alpha13: DEFAULTS.alpha13,
      alpha23: DEFAULTS.alpha23
    };

    // Hook up controls
    const sliderAC = document.getElementById('slider-ac');
    const sliderBC = document.getElementById('slider-bc');
    const valueAC = document.getElementById('value-ac');
    const valueBC = document.getElementById('value-bc');
    const btnReset = document.getElementById('btn-reset');

    function setValueDisplays() {
      valueAC.textContent = Number(state.alpha13).toFixed(2);
      valueBC.textContent = Number(state.alpha23).toFixed(2);
    }

    function setSlidersFromState() {
      sliderAC.value = state.alpha13;
      sliderBC.value = state.alpha23;
      setValueDisplays();
    }

    sliderAC.addEventListener('input', () => {
      state.alpha13 = parseFloat(sliderAC.value);
      setValueDisplays();
      if (p5Instance) p5Instance.redraw();
    });

    sliderBC.addEventListener('input', () => {
      state.alpha23 = parseFloat(sliderBC.value);
      setValueDisplays();
      if (p5Instance) p5Instance.redraw();
    });

    btnReset.addEventListener('click', () => {
      state.alpha13 = DEFAULTS.alpha13;
      state.alpha23 = DEFAULTS.alpha23;
      setSlidersFromState();
      if (p5Instance) p5Instance.redraw();
    });

    setSlidersFromState();

    // Prepare to replace placeholder canvas with p5-managed canvas (keeping required ID)
    document.addEventListener('DOMContentLoaded', () => {
      const placeholder = document.getElementById('ternary-plot-canvas');
      if (placeholder && placeholder.parentNode) {
        placeholder.parentNode.removeChild(placeholder);
      }
      // Start p5
      p5Instance = new p5(ternarySketch, 'visualization-container');
    });

    // p5 instance handle
    let p5Instance = null;

    // The p5 sketch implementing the ternary plot
    function ternarySketch(p) {
      // Geometry
      const CANVAS_W = 600;
      const CANVAS_H = 550;
      let P_A, P_B, P_C;  // vertices: A (top), B (bottom-left), C (bottom-right)
      const N = 10;       // subdivisions per side => 100 small triangles
      const padding = 60;

      // Gradient color stops
      const stops = [
        { pos: 0.00, col: [0x2c, 0x7b, 0xb6] }, // dark blue
        { pos: 0.25, col: [0xab, 0xd9, 0xe9] }, // light blue
        { pos: 0.50, col: [0xff, 0xff, 0xbf] }, // green-yellow
        { pos: 0.75, col: [0xfd, 0xae, 0x61] }, // orange
        { pos: 1.00, col: [0xd7, 0x19, 0x1c] }  // red
      ];

      function lerpColorRGB(c1, c2, t) {
        const r = c1[0] + (c2[0] - c1[0]) * t;
        const g = c1[1] + (c2[1] - c1[1]) * t;
        const b = c1[2] + (c2[2] - c1[2]) * t;
        return [r, g, b];
      }

      function getColorForValue(v) {
        const clamped = Math.max(0, Math.min(1, v));
        let s1 = stops[0], s2 = stops[stops.length - 1];
        for (let i = 0; i < stops.length - 1; i++) {
          if (clamped >= stops[i].pos && clamped <= stops[i + 1].pos) {
            s1 = stops[i];
            s2 = stops[i + 1];
            break;
          }
        }
        const range = s2.pos - s1.pos || 1;
        const t = (clamped - s1.pos) / range;
        return lerpColorRGB(s1.col, s2.col, t);
      }

      function computeVertices() {
        const baseY = CANVAS_H - padding;
        const side = CANVAS_W - 2 * padding; // base length
        const h = side * Math.sqrt(3) / 2;
        const topY = baseY - h;

        P_B = p.createVector(padding, baseY);
        P_C = p.createVector(CANVAS_W - padding, baseY);
        P_A = p.createVector(CANVAS_W / 2, topY);
      }

      function baryToCartesian(xA, xB, xC) {
        const x = xA * P_A.x + xB * P_B.x + xC * P_C.x;
        const y = xA * P_A.y + xB * P_B.y + xC * P_C.y;
        return p.createVector(x, y);
      }

      function countsToBary(a, b, c) {
        const sum = a + b + c;
        return { xA: a / sum, xB: b / sum, xC: c / sum };
      }

      function centroidOfTriangle(b1, b2, b3) {
        return {
          xA: (b1.xA + b2.xA + b3.xA) / 3,
          xB: (b1.xB + b2.xB + b3.xB) / 3,
          xC: (b1.xC + b2.xC + b3.xC) / 3
        };
      }

      function computeF(xA, xB, xC) {
        // Liquid compositions
        const x1 = xA, x2 = xB, x3 = xC;

        const alpha1 = state.alpha13; // A vs C
        const alpha2 = state.alpha23; // B vs C
        const alpha3 = 1.0;           // C reference

        const D = alpha1 * x1 + alpha2 * x2 + alpha3 * x3;
        const y1 = (alpha1 * x1) / D;
        const y2 = (alpha2 * x2) / D;
        const y3 = (alpha3 * x3) / D;

        const F = (x1 - y1) * (x1 - y1) + (x2 - y2) * (x2 - y2) + (x3 - y3) * (x3 - y3);
        return F;
      }

      function drawSmallTriangle(bv1, bv2, bv3, rgb) {
        const p1 = baryToCartesian(bv1.xA, bv1.xB, bv1.xC);
        const p2 = baryToCartesian(bv2.xA, bv2.xB, bv2.xC);
        const p3 = baryToCartesian(bv3.xA, bv3.xB, bv3.xC);

        p.stroke(0);
        p.strokeWeight(0.4);
        p.fill(rgb[0], rgb[1], rgb[2]);
        p.beginShape();
        p.vertex(p1.x, p1.y);
        p.vertex(p2.x, p2.y);
        p.vertex(p3.x, p3.y);
        p.endShape(p.CLOSE);
      }

      function drawGrid() {
        // Build the triangular lattice and paint the small triangles
        // Integer lattice counts (a, b, c) with a + b + c = N
        for (let u = 0; u <= N - 1; u++) {           // u corresponds to b-count
          for (let v = 0; v <= N - 1 - u; v++) {     // v corresponds to c-count
            const a = N - u - v;                     // a-count

            // Upward-pointing triangle: (a,u,v), (a-1,u+1,v), (a-1,u,v+1)
            if (a >= 1) {
              const up1 = countsToBary(a, u, v);
              const up2 = countsToBary(a - 1, u + 1, v);
              const up3 = countsToBary(a - 1, u, v + 1);

              const cUp = centroidOfTriangle(up1, up2, up3);
              const Fup = computeF(cUp.xA, cUp.xB, cUp.xC);
              const vUp = Math.max(0, Math.min(1, Fup / 2.0));
              const colUp = getColorForValue(vUp);

              drawSmallTriangle(up1, up2, up3, colUp);
            }

            // Downward-pointing triangle: (a-1,u+1,v), (a-2,u+1,v+1), (a-1,u,v+1)
            if (a >= 2) {
              const dn1 = countsToBary(a - 1, u + 1, v);
              const dn2 = countsToBary(a - 2, u + 1, v + 1);
              const dn3 = countsToBary(a - 1, u, v + 1);

              const cDn = centroidOfTriangle(dn1, dn2, dn3);
              const Fdn = computeF(cDn.xA, cDn.xB, cDn.xC);
              const vDn = Math.max(0, Math.min(1, Fdn / 2.0));
              const colDn = getColorForValue(vDn);

              drawSmallTriangle(dn1, dn2, dn3, colDn);
            }
          }
        }
      }

      function drawMainTriangleAndLabels() {
        // Outline
        p.noFill();
        p.stroke(0);
        p.strokeWeight(1.2);
        p.triangle(P_A.x, P_A.y, P_B.x, P_B.y, P_C.x, P_C.y);

        // Vertex labels
        p.noStroke();
        p.fill(0);
        p.textAlign(p.CENTER, p.BOTTOM);
        p.textSize(14);
        p.text('A', P_A.x, P_A.y - 10);
        p.textAlign(p.RIGHT, p.TOP);
        p.text('B', P_B.x - 10, P_B.y + 8);
        p.textAlign(p.LEFT, p.TOP);
        p.text('C', P_C.x + 10, P_C.y + 8);
      }

      function drawAxisTicks() {
        // Helper: draw ticks along an edge from P1 to P2, labeled 0.0..1.0
        function drawTicksOnEdge(P1, P2) {
          const cx = (P_A.x + P_B.x + P_C.x) / 3;
          const cy = (P_A.y + P_B.y + P_C.y) / 3;

          const tickLen = 7;
          p.stroke(0);
          p.strokeWeight(0.8);
          p.fill(0);
          p.textSize(10);
          p.textAlign(p.CENTER, p.CENTER);

          for (let k = 0; k <= 10; k++) {
            const t = k / 10;
            const x = P1.x + (P2.x - P1.x) * t;
            const y = P1.y + (P2.y - P1.y) * t;

            const dirToCenter = p.createVector(cx - x, cy - y);
            dirToCenter.normalize();
            const tx1 = x;
            const ty1 = y;
            const tx2 = x + dirToCenter.x * tickLen;
            const ty2 = y + dirToCenter.y * tickLen;

            p.line(tx1, ty1, tx2, ty2);

            // Place label a bit further along the same direction
            const labelOffset = 14;
            const lx = x + dirToCenter.x * (tickLen + labelOffset);
            const ly = y + dirToCenter.y * (tickLen + labelOffset);

            p.noStroke();
            p.fill(50);
            p.text((t).toFixed(1), lx, ly);
          }
        }

        // Ticks along the three edges
        drawTicksOnEdge(P_B, P_C); // "A-axis" (conceptually)
        drawTicksOnEdge(P_A, P_C); // "B-axis"
        drawTicksOnEdge(P_A, P_B); // "C-axis"
      }

      p.setup = function () {
        const cnv = p.createCanvas(CANVAS_W, CANVAS_H);
        // Place inside the visualization container and give the required ID
        cnv.parent('visualization-container');
        cnv.id('ternary-plot-canvas');

        p.pixelDensity(1);
        p.noLoop();
        computeVertices();
        p.background(255);
        p.redraw();
      };

      p.draw = function () {
        p.clear();
        p.background(255);

        // Draw filled small triangles grid first
        drawGrid();

        // Then outlines and labels
        drawMainTriangleAndLabels();
        drawAxisTicks();
      };
    }
  </script>
</body>
</html>
