  <script>
    // Utility for formatting slider display values
    function formatValueForDisplay(value, step) {
      const num = Number(value);
      const st = Number(step);
      if (Number.isFinite(st) && st % 1 !== 0) {
        return num.toFixed(1);
      }
      return String(Math.round(num));
    }

    function almostZero(x, eps = 1e-12) {
      return Math.abs(x) < eps;
    }

    function getSliderNumber(id) {
      const el = document.getElementById(id);
      return Number(el.value);
    }

    function updateValueBadge(idSlider, idBadge) {
      const slider = document.getElementById(idSlider);
      const badge = document.getElementById(idBadge);
      badge.textContent = formatValueForDisplay(slider.value, slider.step);
    }

    function buildDynamicTitle(n, BX, BY, BZ, wso) {
      const parts = [`n = ${n}`];
      if (!almostZero(BX)) parts.push(`BX = ${BX.toFixed(1)}`);
      if (!almostZero(BY)) parts.push(`BY = ${BY.toFixed(1)}`);
      if (!almostZero(BZ)) parts.push(`BZ = ${BZ.toFixed(1)}`);
      if (!almostZero(wso)) parts.push(`wso = ${wso.toFixed(1)}`);
      return parts.join(", ");
    }

    function computeBands(n, wso, BX, BY, BZ, kmin = -5, kmax = 5, dk = 0.1) {
      const k = [];
      const Eplus = [];
      const Eminus = [];
      const base = (2 * n + 1);

      for (let x = kmin; x <= kmax + 1e-12; x = +(x + dk).toFixed(12)) {
        k.push(x);
        const rad = Math.pow(BZ + wso * x, 2) + BX * BX + BY * BY;
        const root = Math.sqrt(rad);
        const k2 = x * x;
        Eplus.push(base + k2 + root);
        Eminus.push(base + k2 - root);
      }
      return { k, Eplus, Eminus };
    }

    function updatePlot() {
      const n = getSliderNumber('slider-n');
      const wso = getSliderNumber('slider-wso');
      const BX = getSliderNumber('slider-bx');
      const BY = getSliderNumber('slider-by');
      const BZ = getSliderNumber('slider-bz');

      const { k, Eplus, Eminus } = computeBands(n, wso, BX, BY, BZ);

      const isDegenerate = almostZero(wso) && almostZero(BX) && almostZero(BY) && almostZero(BZ);

      let data;
      if (isDegenerate) {
        // Degenerate: only one trace (solid red)
        data = [{
          x: k,
          y: Eplus, // Eplus == Eminus in this case
          mode: 'lines',
          name: 'degenerate',
          line: { color: 'red', dash: 'solid', width: 2 }
        }];
      } else {
        data = [
          {
            x: k,
            y: Eplus,
            mode: 'lines',
            name: "E+",
            line: { color: 'red', dash: 'solid', width: 2 }
          },
          {
            x: k,
            y: Eminus,
            mode: 'lines',
            name: "E−",
            line: { color: 'red', dash: 'dash', width: 2 }
          }
        ];
      }

      const layout = {
        title: buildDynamicTitle(n, BX, BY, BZ, wso),
        margin: { l: 60, r: 20, t: 50, b: 55 },
        xaxis: {
          title: 'normalized k length (lwky)',
          range: [-5, 5],
          zeroline: true
        },
        yaxis: {
          title: '2 E / &#x210F;&omega;',
          range: [0, 40],
          zeroline: true
        },
        showlegend: !isDegenerate,
      };

      Plotly.react('plot-container', data, layout, { responsive: true });
    }

    function attachSliderHandlers() {
      const pairs = [
        ['slider-wso', 'value-wso'],
        ['slider-n', 'value-n'],
        ['slider-bx', 'value-bx'],
        ['slider-by', 'value-by'],
        ['slider-bz', 'value-bz']
      ];
      for (const [sliderId, badgeId] of pairs) {
        const slider = document.getElementById(sliderId);
        slider.addEventListener('input', () => {
          updateValueBadge(sliderId, badgeId);
          updatePlot();
        });
        // Initialize badge display
        updateValueBadge(sliderId, badgeId);
      }
    }

    window.addEventListener('DOMContentLoaded', () => {
      attachSliderHandlers();
      updatePlot();
    });
  </script>
</body>
