        </div>

        <h4>UH streamflow rates</h4>
        <div class="slider-row">
          <label for="slider-uh-1">hour 1</label>
          <input id="slider-uh-1" type="range" min="0" max="10" step="1" value="1">
          <span id="value-uh-1">1</span>
        </div>
        <div class="slider-row">
          <label for="slider-uh-2">hour 2</label>
          <input id="slider-uh-2" type="range" min="0" max="10" step="1" value="5">
          <span id="value-uh-2">5</span>
        </div>
        <div class="slider-row">
          <label for="slider-uh-3">hour 3</label>
          <input id="slider-uh-3" type="range" min="0" max="10" step="1" value="3">
          <span id="value-uh-3">3</span>
        </div>
        <div class="slider-row">
          <label for="slider-uh-4">hour 4</label>
          <input id="slider-uh-4" type="range" min="0" max="10" step="1" value="1">
          <span id="value-uh-4">1</span>
        </div>
      </div>
    </div>

    <div id="plots-container">
      <div id="plot-canvas-1"></div>
      <div id="plot-canvas-2"></div>
      <div id="plot-canvas-3"></div>
      <div id="plot-canvas-4"></div>
      <div id="plot-canvas-5"></div>
      <div id="plot-canvas-6"></div>
    </div>
  </div>

  <script>
    // Global state arrays
    let P = [1, 0, 0];   // precipitation (3)
    let U = [1, 5, 3, 1]; // Unit Hydrograph (4)
    let C1 = [0, 0, 0, 0];
    let C2 = [0, 0, 0, 0];
    let C3 = [0, 0, 0, 0];
    let Q  = [0, 0, 0, 0, 0, 0]; // composite (6)

    // Utility to get current container size
    function getContainerSize(container) {
      const rect = container.getBoundingClientRect();
      const width = Math.max(260, Math.floor(rect.width));
      // Maintain a pleasant aspect ratio; prefer container height if provided
      const minH = 220;
      const height = Math.max(minH, Math.floor(rect.height || width * 0.66));
      return { width, height };
    }

    // Draw a step chart with axes and filled area under the curve
    function drawPlot(p, data, xLabel, yLabel, xMax, yMax, timeOffset) {
      p.push();
      p.background(255);

      // Layout
      const margin = { left: 48, right: 14, top: 16, bottom: 36 };
      const plotW = p.width - margin.left - margin.right;
      const plotH = p.height - margin.top - margin.bottom;

      function xToPx(x) {
        return margin.left + (x / xMax) * plotW;
      }
      function yToPx(y) {
        const yy = Math.max(0, Math.min(y, yMax));
        return margin.top + plotH - (yy / yMax) * plotH;
      }

      // Axes
      p.stroke(0);
      p.strokeWeight(1);
      p.line(margin.left, margin.top, margin.left, margin.top + plotH); // y
      p.line(margin.left, margin.top + plotH, margin.left + plotW, margin.top + plotH); // x

      // Ticks
      const xTick = xMax <= 5 ? 1 : 2;
      const yTick = yMax <= 10 ? 2 : 20;

      p.textAlign(p.CENTER, p.TOP);
      p.textSize(11);
      for (let xt = 0; xt <= xMax; xt += xTick) {
        const xp = xToPx(xt);
        p.stroke(0);
        p.line(xp, margin.top + plotH, xp, margin.top + plotH + 4);
        p.noStroke();
        p.fill(0);
        p.text(xt.toString(), xp, margin.top + plotH + 6);
      }

      p.textAlign(p.RIGHT, p.CENTER);
      for (let yt = 0; yt <= yMax; yt += yTick) {
        const yp = yToPx(yt);
        p.stroke(0);
        p.line(margin.left - 4, yp, margin.left, yp);
        p.noStroke();
        p.fill(0);
        p.text(yt.toString(), margin.left - 6, yp);
      }

      // Axis labels
      p.noStroke();
      p.fill(0);
      p.textAlign(p.CENTER, p.BOTTOM);
      p.text(xLabel, margin.left + plotW / 2, p.height - 4);

      p.push();
      p.translate(12, margin.top + plotH / 2);
      p.rotate(-p.HALF_PI);
      p.textAlign(p.CENTER, p.TOP);
      p.text(yLabel, 0, 0);
      p.pop();

      // Step area fill
      const n = data.length;
      const offset = timeOffset || 0;
      const fillColor = p.color('#d4e6f1');
      const strokeColor = p.color('#2e86c1');

      // Filled polygon under the step curve
      p.noStroke();
      p.fill(fillColor);
      p.beginShape();
      p.vertex(xToPx(offset), yToPx(0));
      for (let i = 0; i < n; i++) {
        const x0 = offset + i;
        const x1 = offset + i + 1;
        const y = data[i] || 0;
        p.vertex(xToPx(x0), yToPx(y));
        p.vertex(xToPx(x1), yToPx(y));
      }
      p.vertex(xToPx(offset + n), yToPx(0));
      p.endShape(p.CLOSE);

      // Step stroke on top
      p.noFill();
      p.stroke(strokeColor);
      p.strokeWeight(2);
      for (let i = 0; i < n; i++) {
        const x0 = offset + i;
        const x1 = offset + i + 1;
        const y = data[i] || 0;
        p.line(xToPx(x0), yToPx(y), xToPx(x1), yToPx(y));
        if (i < n - 1) {
          const yNext = data[i + 1] || 0;
          p.line(xToPx(x1), yToPx(y), xToPx(x1), yToPx(yNext));
        }
      }

      p.pop();
    }

    // Create a p5 sketch for a given container and config
    function makeSketch(containerId, config) {
      const container = document.getElementById(containerId);
      const sketch = (p) => {
        p.setup = function() {
          const { width, height } = getContainerSize(container);
          p.createCanvas(width, height);
          p.noLoop();
        };
        p.windowResized = function() {
          const { width, height } = getContainerSize(container);
          p.resizeCanvas(width, height);
          p.redraw();
        };
        p.draw = function() {
          const data = config.getData();
          drawPlot(
            p,
            data,
            config.xLabel,
            config.yLabel,
            config.xMax,
            config.yMax,
            config.offset || 0
          );
        };
      };
      return new p5(sketch, container);
    }

    // Update function: reads sliders, computes convolution, redraws plots
    function update() {
      // Read sliders into P and U
      P = [
        parseInt(document.getElementById('slider-precip-1').value, 10),
        parseInt(document.getElementById('slider-precip-2').value, 10),
        parseInt(document.getElementById('slider-precip-3').value, 10)
      ];
      U = [
        parseInt(document.getElementById('slider-uh-1').value, 10),
        parseInt(document.getElementById('slider-uh-2').value, 10),
        parseInt(document.getElementById('slider-uh-3').value, 10),
        parseInt(document.getElementById('slider-uh-4').value, 10)
      ];

      // Components
      C1 = U.map(u => P[0] * u);
      C2 = U.map(u => P[1] * u);
      C3 = U.map(u => P[2] * u);

      // Composite hydrograph Q (length 6) per specified formulas
      Q = [
        P[0] * U[0],
        P[0] * U[1] + P[1] * U[0],
        P[0] * U[2] + P[1] * U[1] + P[2] * U[0],
        P[0] * U[3] + P[1] * U[2] + P[2] * U[1],
        P[1] * U[3] + P[2] * U[2],
        P[2] * U[3]
      ];

      // Redraw all sketches
      sketches.forEach(s => s.redraw());
    }

    // Attach slider events
    function attachSlider(sliderId, valueId) {
      const slider = document.getElementById(sliderId);
      const valueSpan = document.getElementById(valueId);
      const setVal = () => { valueSpan.textContent = parseInt(slider.value, 10); };
      slider.addEventListener('input', () => { setVal(); update(); });
      setVal();
    }

    // Toggle controls visibility
    document.getElementById('toggle-controls').addEventListener('click', () => {
      const area = document.getElementById('sliders-area');
      if (area.style.display === 'none') {
        area.style.display = 'block';
      } else {
        area.style.display = 'none';
      }
    });

    // Initialize slider bindings
    attachSlider('slider-precip-1', 'value-precip-1');
    attachSlider('slider-precip-2', 'value-precip-2');
    attachSlider('slider-precip-3', 'value-precip-3');
