<script>
(function(){
  // State
  const state = {
    A: { a11:-5, a12:-3, a21:-1, a22:-4 },
    B: { b11:-5, b12:-3, b21:-1, b22:-4 },
    blueSegments: [],
    greenSegments: [],
    intersections: [],
    vertices: []
  };

  const EPS = 1e-9;

  // Utility: clamp
  function clamp(v, lo, hi){ return Math.max(lo, Math.min(hi, v)); }

  // Update UI value spans and payoff table
  function updateValueDisplays(){
    const ids = ['a11','a12','a21','a22','b11','b12','b21','b22'];
    ids.forEach(id=>{
      const slider = document.getElementById('slider-'+id);
      const span = document.getElementById('value-'+id);
      span.textContent = slider.value;
    });
  }

  function updateStateFromUI(){
    // Read sliders into state
    function gv(id){ return parseFloat(document.getElementById('slider-'+id).value); }
    state.A.a11 = gv('a11');
    state.A.a12 = gv('a12');
    state.A.a21 = gv('a21');
    state.A.a22 = gv('a22');
    state.B.b11 = gv('b11');
    state.B.b12 = gv('b12');
    state.B.b21 = gv('b21');
    state.B.b22 = gv('b22');
  }

  function updatePayoffTable(){
    const {A,B} = state;
    document.getElementById('cell-11').textContent = `(${A.a11}, ${B.b11})`;
    document.getElementById('cell-12').textContent = `(${A.a12}, ${B.b12})`;
    document.getElementById('cell-21').textContent = `(${A.a21}, ${B.b21})`;
    document.getElementById('cell-22').textContent = `(${A.a22}, ${B.b22})`;
  }

  // Best response segments builders
  function computePlayer1Segments(A){
    const segments = [];
    const den1 = (A.a11 - A.a12 - A.a21 + A.a22);
    const yIndiff = (den1 !== 0) ? (A.a22 - A.a12) / den1 : NaN;
    const intercept = (A.a12 - A.a22);

    if (Math.abs(den1) < EPS){
      // Dominant or weakly dominant (independent of y)
      if (Math.abs(intercept) < EPS){
        // All x in {0,1} are best responses for all y: draw both vertical borders
        segments.push({x1:0,y1:0,x2:0,y2:1,orientation:'v'});
        segments.push({x1:1,y1:0,x2:1,y2:1,orientation:'v'});
      } else if (intercept > 0){
        // Prefer strategy 1 (x=1)
        segments.push({x1:1,y1:0,x2:1,y2:1,orientation:'v'});
      } else {
        // Prefer strategy 2 (x=0)
        segments.push({x1:0,y1:0,x2:0,y2:1,orientation:'v'});
      }
    } else if (yIndiff > 0 && yIndiff < 1){
      // C-shape: horizontal indifference + verticals at sides
      segments.push({x1:0,y1:yIndiff,x2:1,y2:yIndiff,orientation:'h'});
      if (den1 > 0){
        // x=0 for y<yIndiff; x=1 for y>yIndiff
        segments.push({x1:0,y1:0,x2:0,y2:yIndiff,orientation:'v'});
        segments.push({x1:1,y1:yIndiff,x2:1,y2:1,orientation:'v'});
      } else {
        // reversed
        segments.push({x1:0,y1:yIndiff,x2:0,y2:1,orientation:'v'});
        segments.push({x1:1,y1:0,x2:1,y2:yIndiff,orientation:'v'});
      }
    } else {
      // Indifference outside [0,1] -> dominant in mixed extension
      const D = (y)=> den1*y + intercept;
      const d05 = D(0.5);
      if (Math.abs(d05) < EPS){
        // Tie everywhere -> both
        segments.push({x1:0,y1:0,x2:0,y2:1,orientation:'v'});
        segments.push({x1:1,y1:0,x2:1,y2:1,orientation:'v'});
      } else if (d05 > 0){
        segments.push({x1:1,y1:0,x2:1,y2:1,orientation:'v'});
      } else {
        segments.push({x1:0,y1:0,x2:0,y2:1,orientation:'v'});
      }
    }
    return {segments, den1, yIndiff};
  }

  function computePlayer2Segments(B){
    const segments = [];
    const den2 = (B.b11 - B.b12 - B.b21 + B.b22);
    const xIndiff = (den2 !== 0) ? (B.b22 - B.b21) / den2 : NaN;
    const intercept = (B.b21 - B.b22);

    if (Math.abs(den2) < EPS){
      // Dominant or weakly dominant (independent of x)
      if (Math.abs(intercept) < EPS){
        // Both y=0 and y=1 are best responses
        segments.push({x1:0,y1:0,x2:1,y2:0,orientation:'h'});
        segments.push({x1:0,y1:1,x2:1,y2:1,orientation:'h'});
      } else if (intercept > 0){
        // Prefer strategy 1 -> y=1
        segments.push({x1:0,y1:1,x2:1,y2:1,orientation:'h'});
      } else {
        // Prefer strategy 2 -> y=0
        segments.push({x1:0,y1:0,x2:1,y2:0,orientation:'h'});
      }
    } else if (xIndiff > 0 && xIndiff < 1){
      // C-shape rotated: vertical indifference + horizontals at top/bottom
      segments.push({x1:xIndiff,y1:0,x2:xIndiff,y2:1,orientation:'v'});
      if (den2 > 0){
        // y=0 for x<xIndiff; y=1 for x>xIndiff
        segments.push({x1:0,y1:0,x2:xIndiff,y2:0,orientation:'h'});
        segments.push({x1:xIndiff,y1:1,x2:1,y2:1,orientation:'h'});
      } else {
        // reversed
        segments.push({x1:0,y1:1,x2:xIndiff,y2:1,orientation:'h'});
        segments.push({x1:xIndiff,y1:0,x2:1,y2:0,orientation:'h'});
      }
    } else {
      // Dominant in mixed extension
      const D = (x)=> den2*x + intercept;
      const d05 = D(0.5);
      if (Math.abs(d05) < EPS){
        // Tie -> both
        segments.push({x1:0,y1:0,x2:1,y2:0,orientation:'h'});
        segments.push({x1:0,y1:1,x2:1,y2:1,orientation:'h'});
      } else if (d05 > 0){
        segments.push({x1:0,y1:1,x2:1,y2:1,orientation:'h'});
      } else {
        segments.push({x1:0,y1:0,x2:1,y2:0,orientation:'h'});
      }
    }
    return {segments, den2, xIndiff};
  }

  // Intersections of axis-aligned segments
  function getIntersections(blue, green){
    const list = [];
    const tol = 1e-9;

    function pushPoint(x,y){ list.push({x1:x,y1:y,x2:x,y2:y,orientation:'p'}); }
    function pushV(x,y1,y2){ const lo=Math.min(y1,y2), hi=Math.max(y1,y2); list.push({x1:x,y1:lo,x2:x,y2:hi,orientation:'v'}); }
    function pushH(x1,x2,y){ const lo=Math.min(x1,x2), hi=Math.max(x1,x2); list.push({x1:lo,y1:y,x2:hi,y2:y,orientation:'h'}); }

    for (const b of blue){
      for (const g of green){
        if (b.orientation==='v' && g.orientation==='h'){
          // vertical/horizontal
          const x = b.x1;
          const y = g.y1;
          const withinX = (x >= Math.min(g.x1,g.x2)-tol) && (x <= Math.max(g.x1,g.x2)+tol);
          const withinY = (y >= Math.min(b.y1,b.y2)-tol) && (y <= Math.max(b.y1,b.y2)+tol);
          if (withinX && withinY) pushPoint(clamp(x,0,1), clamp(y,0,1));
        } else if (b.orientation==='h' && g.orientation==='v'){
          const x = g.x1;
          const y = b.y1;
          const withinX = (x >= Math.min(b.x1,b.x2)-tol) && (x <= Math.max(b.x1,b.x2)+tol);
          const withinY = (y >= Math.min(g.y1,g.y2)-tol) && (y <= Math.max(g.y1,g.y2)+tol);
          if (withinX && withinY) pushPoint(clamp(x,0,1), clamp(y,0,1));
        } else if (b.orientation==='v' && g.orientation==='v'){
          if (Math.abs(b.x1 - g.x1) < tol){
            const lo = Math.max(Math.min(b.y1,b.y2), Math.min(g.y1,g.y2));
            const hi = Math.min(Math.max(b.y1,b.y2), Math.max(g.y1,g.y2));
            if (hi >= lo - tol){
              pushV(b.x1, clamp(lo,0,1), clamp(hi,0,1));
            }
          }
        } else if (b.orientation==='h' && g.orientation==='h'){
          if (Math.abs(b.y1 - g.y1) < tol){
            const lo = Math.max(Math.min(b.x1,b.x2), Math.min(g.x1,g.x2));
            const hi = Math.min(Math.max(b.x1,b.x2), Math.max(g.x1,g.x2));
            if (hi >= lo - tol){
              pushH(clamp(lo,0,1), clamp(hi,0,1), b.y1);
            }
          }
        }
      }
    }

    // Merge overlapping colinear intersections to simplify
    function mergeSegments(input){
      const points = [];
      const hv = {h:[], v:[]};
      for (const s of input){
        if (s.orientation==='p'){
          points.push(s);
        } else {
          hv[s.orientation].push(s);
        }
      }
      // Merge by grouping same coordinate (x for v, y for h)
      function mergeAxis(list, key, a1, a2, ori){
        const grouped = {};
        for (const s of list){
          const k = s[key].toFixed(9);
          if (!grouped[k]) grouped[k] = [];
          grouped[k].push(s);
        }
        const merged = [];
        for (const k in grouped){
          const arr = grouped[k];
          // sort by start
          arr.sort((u,v)=> (u[a1]-v[a1]) || (u[a2]-v[a2]));
          let curStart = arr[0][a1], curEnd = arr[0][a2];
          arr.forEach((seg,i)=>{
            const s = seg[a1], e = seg[a2];
            if (s <= curEnd + 1e-9){ // overlap or touch
              curEnd = Math.max(curEnd, e);
            } else {
              if (ori==='v'){ merged.push({x1:arr[0][key], y1:curStart, x2:arr[0][key], y2:curEnd, orientation:'v'}); }
              else { merged.push({x1:curStart, y1:arr[0][key], x2:curEnd, y2:arr[0][key], orientation:'h'}); }
              curStart = s; curEnd = e;
            }
          });
          if (ori==='v'){ merged.push({x1:arr[0][key], y1:curStart, x2:arr[0][key], y2:curEnd, orientation:'v'}); }
          else { merged.push({x1:curStart, y1:arr[0][key], x2:curEnd, y2:arr[0][key], orientation:'h'}); }
        }
        return merged;
      }
      const mergedV = mergeAxis(hv.v, 'x1', 'y1', 'y2', 'v');
      const mergedH = mergeAxis(hv.h, 'y1', 'x1', 'x2', 'h');
      return points.concat(mergedV, mergedH);
    }

    return mergeSegments(list);
  }

  // Collect vertices (unique endpoints of intersection segments)
  function collectVertices(intersections){
    const verts = [];
    function add(x,y){
      x = clamp(x,0,1); y = clamp(y,0,1);
      const key = `${Math.round(x*1e6)},${Math.round(y*1e6)}`;
      if (!seen.has(key)){
        seen.add(key);
        verts.push({x, y});
      }
    }
    const seen = new Set();
    intersections.forEach(seg=>{
      if (seg.orientation==='p'){
        add(seg.x1, seg.y1);
      } else {
        add(seg.x1, seg.y1);
        add(seg.x2, seg.y2);
      }
    });
    // Sort for consistent display (lexicographic)
    verts.sort((p,q)=> (p.x - q.x) || (p.y - q.y));
    return verts;
  }

  // Fraction formatter using continued fractions
  function toFractionString(x, tolerance=1e-8, maxDen=200){
    if (!isFinite(x)) return '?';
    if (Math.abs(x) < tolerance) return '0';
    if (Math.abs(x-1) < tolerance) return '1';
    const sign = x < 0 ? -1 : 1;
    x = Math.abs(x);

    let h1=1, h0=0, k1=0, k0=1, b = x;
    do{
      const a = Math.floor(b);
      let h2 = a*h1 + h0;
      let k2 = a*k1 + k0;
      const frac = h2 / k2;
      if (Math.abs(frac - x) <= tolerance || k2 > maxDen){
        let num = sign*h2, den = k2;
        const g = gcd(Math.round(Math.abs(num)), Math.round(den));
        num = Math.round(num / g);
        den = Math.round(den / g);
        if (den === 1) return `${num}`;
        return `\\frac{${num}}{${den}}`;
      }
      h0=h1; h1=h2; k0=k1; k1=k2;
      b = 1/(b - a);
    }while(true);

    function gcd(a,b){ while(b){ const t=a%b; a=b; b=t;} return a; }
  }

  function formatVerticesLatex(vertices){
    if (!vertices || vertices.length===0) return '\\( \\emptyset \\)';
    const items = vertices.map(p=>{
      const xs = toFractionString(p.x);
      const ys = toFractionString(p.y);
      return `(${xs},\\, ${ys})`;
    });
    return `\\( \\{ ${items.join(',\\; ')} \\} \\)`;
  }

  // Recalculate everything and redraw
  function recalcAndRedraw(){
    updateStateFromUI();
    updateValueDisplays();
    updatePayoffTable();

    const p1 = computePlayer1Segments(state.A);
    const p2 = computePlayer2Segments(state.B);
    state.blueSegments = p1.segments;
    state.greenSegments = p2.segments;

    // Intersections
    state.intersections = getIntersections(state.blueSegments, state.greenSegments);
    state.vertices = collectVertices(state.intersections);

    // Update equilibria MathJax display
    const latex = formatVerticesLatex(state.vertices);
    const eqDiv = document.getElementById('equilibria-display');
    eqDiv.innerHTML = latex;
    if (window.MathJax && MathJax.typesetPromise){
      MathJax.typesetPromise([eqDiv]);
    }

    // Redraw p5 canvas once
    if (window.redraw) window.redraw();
  }

  // Hook up slider input and +/- buttons
  function setupControls(){
    const ids = ['a11','a12','a21','a22','b11','b12','b21','b22'];
    ids.forEach(id=>{
      const slider = document.getElementById('slider-'+id);
      const minus = document.getElementById('btn-minus-'+id);
      const plus = document.getElementById('btn-plus-'+id);
      slider.addEventListener('input', recalcAndRedraw);
      minus.addEventListener('click', ()=>{
        const step = parseFloat(slider.step)||1;
        slider.value = Math.max(parseFloat(slider.min), parseFloat(slider.value) - step);
        recalcAndRedraw();
      });
      plus.addEventListener('click', ()=>{
        const step = parseFloat(slider.step)||1;
        slider.value = Math.min(parseFloat(slider.max), parseFloat(slider.value) + step);
        recalcAndRedraw();
      });
    });
  }

  // p5.js sketch
  let sketch = function(p){
    const size = 440;
    const pad = 50;
    const xmin = -0.1, xmax = 1.1;
    const ymin = -0.1, ymax = 1.1;
    const plotSize = size - 2*pad;
    const sx = plotSize / (xmax - xmin);
    const sy = plotSize / (ymax - ymin);

    function mapX(x){ return pad + (x - xmin)*sx; }
    function mapY(y){ return pad + plotSize - (y - ymin)*sy; }

    p.setup = function(){
      const c = p.createCanvas(size, size);
      c.parent('canvas-container');
      p.noLoop();
      p.pixelDensity(1);
    };

    function drawAxes(){
      p.push();
      p.stroke(0);
      p.strokeWeight(1);
      p.fill(0);
      // Axes lines
      p.line(mapX(0), mapY(0), mapX(1), mapY(0));
      p.line(mapX(0), mapY(0), mapX(0), mapY(1));
      // Box border (0..1 square)
      p.stroke(200);
      p.noFill();
      p.rect(mapX(0), mapY(1), mapX(1)-mapX(0), mapY(0)-mapY(1));
      // Ticks and labels
      p.textSize(11);
      p.textAlign(p.CENTER, p.TOP);
      for (let t=0; t<=1.0001; t+=0.2){
        const x = +(t.toFixed(1));
        // x-axis tick
        p.stroke(0); p.line(mapX(x), mapY(0)-4, mapX(x), mapY(0)+4);
        p.noStroke(); p.fill(0); p.text(x.toFixed(1), mapX(x), mapY(0)+6);
      }
      p.textAlign(p.RIGHT, p.CENTER);
      for (let t=0; t<=1.0001; t+=0.2){
        const y = +(t.toFixed(1));
        // y-axis tick
        p.stroke(0); p.line(mapX(0)-4, mapY(y), mapX(0)+4, mapY(y));
        p.noStroke(); p.fill(0); p.text(y.toFixed(1), mapX(0)-6, mapY(y));
      }
      // Axis labels
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.text('y1', mapX(0)-10, mapY(1)-6);
      p.textAlign(p.RIGHT, p.BOTTOM);
      p.text('x1', mapX(1)+12, mapY(0)+2);
      p.pop();
    }

    function drawSegments(segments, color, weight=2){
      p.push();
      p.stroke(color);
      p.strokeWeight(weight);
      segments.forEach(s=>{
        p.line(mapX(s.x1), mapY(s.y1), mapX(s.x2), mapY(s.y2));
      });
      p.pop();
    }

    function drawIntersections(intersections){
      // Draw segments in red and endpoints as circles
      p.push();
      p.stroke(getComputedStyle(document.documentElement).getPropertyValue('--red').trim() || '#d62728');
      p.strokeWeight(4);
      intersections.forEach(s=>{
        if (s.orientation==='p'){
          // draw point as small circle
          p.push();
          p.noStroke();
          p.fill(214,39,40);
          p.circle(mapX(s.x1), mapY(s.y1), 8);
          p.pop();
        } else {
          p.line(mapX(s.x1), mapY(s.y1), mapX(s.x2), mapY(s.y2));
        }
      });
      // Draw vertices as filled circles
      const verts = state.vertices || [];
      p.noStroke();
      p.fill(214,39,40);
      verts.forEach(pt=>{
        p.circle(mapX(pt.x), mapY(pt.y), 8);
      });
      p.pop();
    }

    p.draw = function(){
      p.background(255);
      drawAxes();

      // Draw blue (Player 1)
      const blueColor = getComputedStyle(document.documentElement).getPropertyValue('--blue').trim() || '#1f77b4';
      drawSegments(state.blueSegments, blueColor, 2);

      // Draw green (Player 2)
      const greenColor = getComputedStyle(document.documentElement).getPropertyValue('--green').trim() || '#2ca02c';
      drawSegments(state.greenSegments, greenColor, 2);

      // Draw intersections (red)
      drawIntersections(state.intersections);
    };
  };

  // Initialize after DOM is ready
  document.addEventListener('DOMContentLoaded', ()=>{
    setupControls();
    updateValueDisplays();
    updatePayoffTable();

    // Start p5
    new p5(sketch);

    // Initial compute and draw
    recalcAndRedraw();

    // Typeset control labels initially
    if (window.MathJax && MathJax.typesetPromise){
      MathJax.typesetPromise();
    }
  });
})();
</script>
</body>
</html>
