</head>
<body>
  <div id="demo-container">
    <div id="controls-container">
      <div id="slider-group">
        <label for="slider-t">choose quadratic</label>
        <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5" />
        <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5" />
        <button id="btn-reset" style="font-family: 'Courier New', monospace;">&#x21BB;</button>
      </div>
      <div id="checkbox-group">
        <input type="checkbox" id="checkbox-family" />
        <label for="checkbox-family">family</label>
      </div>
    </div>

    <div id="canvas-container">
      <div class="hint">Drag the 4 points • slider moves tangency t • reset ↻</div>
    </div>
  </div>

  <script>
    // Canvas and world settings
    const CANVAS_W = 600;
    const CANVAS_H = 500;
    const SCALE = 50; // pixels per unit
    const X_MIN = -6, X_MAX = 6;
    const Y_MIN = -5, Y_MAX = 5;

    // Controls
    const sliderT = document.getElementById('slider-t');
    const inputT = document.getElementById('input-t');
    const checkboxFamily = document.getElementById('checkbox-family');
    const btnReset = document.getElementById('btn-reset');

    // State
    let t = 0.5; // tangency x value
    let showFamily = false;
    let locators = [];
    let draggingIndex = -1;

    // Colors
    const colorCubic = '#3f51b5';   // blue
    const colorQuad = '#f57c00';    // orange
    const familyPalette = [
      '#e41a1c', '#377eb8', '#4daf4a', '#984ea3', '#ff7f00',
      '#a65628', '#f781bf', '#999999', '#66c2a5', '#fc8d62'
    ];

    function clamp(val, min, max) { return Math.max(min, Math.min(max, val)); }

    // Event handlers for controls
    function setT(val) {
      const v = clamp(parseFloat(val), -5, 5);
      if (isNaN(v)) return;
      t = v;
      sliderT.value = v.toFixed(2);
      inputT.value = v.toFixed(2);
    }

    sliderT.addEventListener('input', (e) => {
      setT(e.target.value);
    });
    inputT.addEventListener('input', (e) => {
      setT(e.target.value);
    });
    checkboxFamily.addEventListener('change', (e) => {
      showFamily = e.target.checked;
    });
    btnReset.addEventListener('click', () => {
      resetAll();
    });

    function resetAll() {
      // Reset controls
      setT(0.5);
      checkboxFamily.checked = false;
      showFamily = false;
      // Reset locators to initial positions
      locators = [
        { x: -4, y:  1 },
        { x: -2, y:  1.8 },
        { x:  1, y: -1 },
        { x:  4, y: -1 }
      ];
    }

    // p5.js helpers for coordinate transforms
    function worldToScreen(x, y) {
      const sx = CANVAS_W / 2 + x * SCALE;
      const sy = CANVAS_H / 2 - y * SCALE;
      return { x: sx, y: sy };
    }
    function screenToWorld(sx, sy) {
      const x = (sx - CANVAS_W / 2) / SCALE;
      const y = (CANVAS_H / 2 - sy) / SCALE;
      return { x, y };
    }

    // Math for cubic coefficients from 4 points
    function solveCubicCoefficients(points) {
      // Solve M * [a, b, c, d]^T = y
      // M rows: [x^3, x^2, x, 1]
      const M = points.map(pt => [Math.pow(pt.x, 3), Math.pow(pt.x, 2), pt.x, 1]);
      const y = points.map(pt => pt.y);
      // Augmented matrix 4x5
      const A = M.map((row, i) => row.concat([y[i]]));

      const n = 4;
      for (let col = 0; col < n; col++) {
        // Partial pivoting
        let pivotRow = col;
        let maxAbs = Math.abs(A[col][col]);
        for (let r = col + 1; r < n; r++) {
          if (Math.abs(A[r][col]) > maxAbs) {
            maxAbs = Math.abs(A[r][col]);
            pivotRow = r;
          }
        }
        if (maxAbs < 1e-12) {
          return [0, 0, 0, 0]; // singular fallback
        }
        if (pivotRow !== col) {
          const tmp = A[col]; A[col] = A[pivotRow]; A[pivotRow] = tmp;
        }
        // Normalize pivot row
        const pivot = A[col][col];
        for (let c = col; c <= n; c++) A[col][c] /= pivot;
        // Eliminate all other rows
        for (let r = 0; r < n; r++) {
          if (r === col) continue;
          const factor = A[r][col];
          for (let c = col; c <= n; c++) {
            A[r][c] -= factor * A[col][c];
          }
        }
      }
      return [A[0][4], A[1][4], A[2][4], A[3][4]];
    }

    function cubicAt(x, coeffs) {
      const [a, b, c, d] = coeffs;
      return ((a * x + b) * x + c) * x + d;
    }

    function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
      const [a, b, c, d] = cubicCoeffs;
      const p = 3 * a * t + b;
      const q = -3 * a * t * t + c;
      const r = a * t * t * t + d;
      return [p, q, r];
    }

    function quadAt(x, coeffs) {
      const [p, q, r] = coeffs;
      return (p * x + q) * x + r;
    }

    // p5.js sketch
    function setup() {
      const cnv = createCanvas(CANVAS_W, CANVAS_H);
      cnv.parent('canvas-container');
      pixelDensity(1);
      textFont('Courier New, monospace');
      textSize(11);
      resetAll();
    }

    function drawAxes() {
      stroke('#cccccc');
      strokeWeight(1);

      // X-axis
      const p1 = worldToScreen(X_MIN, 0);
      const p2 = worldToScreen(X_MAX, 0);
      line(p1.x, p1.y, p2.x, p2.y);

      // Y-axis
      const p3 = worldToScreen(0, Y_MIN);
      const p4 = worldToScreen(0, Y_MAX);
      line(p3.x, p3.y, p4.x, p4.y);

      // Ticks and labels
      fill('#777');
      noStroke();
      textAlign(CENTER, TOP);
      for (let xi = Math.ceil(X_MIN); xi <= Math.floor(X_MAX); xi++) {
        const ps = worldToScreen(xi, 0);
        stroke('#dddddd');
        line(ps.x, ps.y - 4, ps.x, ps.y + 4);
        noStroke();
        if (xi !== 0) text(xi.toString(), ps.x, ps.y + 6);
      }

      textAlign(LEFT, CENTER);
      for (let yi = Math.ceil(Y_MIN); yi <= Math.floor(Y_MAX); yi++) {
        const ps = worldToScreen(0, yi);
        stroke('#dddddd');
        line(ps.x - 4, ps.y, ps.x + 4, ps.y);
        noStroke();
        if (yi !== 0) text(yi.toString(), ps.x + 6, ps.y - 8);
      }
    }

    function drawCubic(coeffs) {
      noFill();
      stroke(colorCubic);
      strokeWeight(3);
      beginShape();
      const dx = 1 / SCALE; // roughly 1 pixel step
      for (let x = X_MIN; x <= X_MAX + 1e-9; x += dx) {
        const y = cubicAt(x, coeffs);
        const p = worldToScreen(x, y);
        vertex(p.x, p.y);
      }
      endShape();
    }

    function drawQuadraticLine(qcoeffs, col = colorQuad, weight = 3) {
      noFill();
      stroke(col);
      strokeWeight(weight);
      beginShape();
      const dx = 1 / SCALE;
      for (let x = X_MIN; x <= X_MAX + 1e-9; x += dx) {
        const y = quadAt(x, qcoeffs);
        const p = worldToScreen(x, y);
        vertex(p.x, p.y);
      }
      endShape();
    }

    function drawFamily(cubicCoeffs) {
      // Draw multiple filled quadratics across t in steps
      const step = 0.5;
      noStroke();
      const dx = 1 / SCALE;

      for (let k = 0, T = X_MIN; T <= X_MAX + 1e-9; T += step, k++) {
        const q = getOsculatingQuadraticCoeffs(T, cubicCoeffs);
        const pOpen = q[0]; // quadratic coefficient determines opening
        const opensUp = pOpen >= 0;

        const colHex = familyPalette[k % familyPalette.length];
        let c = color(colHex);
        c.setAlpha(90);
        fill(c);

        beginShape();
        const yBoundary = opensUp ? Y_MAX : Y_MIN;
        let ws = worldToScreen(X_MIN, yBoundary);
        vertex(ws.x, ws.y);
        for (let x = X_MIN; x <= X_MAX + 1e-9; x += dx) {
          const y = quadAt(x, q);
          const p = worldToScreen(x, y);
          vertex(p.x, p.y);
        }
        ws = worldToScreen(X_MAX, yBoundary);
        vertex(ws.x, ws.y);
        endShape(CLOSE);
      }
    }

    function drawLocators() {
      for (let i = 0; i < locators.length; i++) {
        const L = locators[i];
        const p = worldToScreen(L.x, L.y);
        // hit area
        stroke('#000');
        strokeWeight(2);
        fill('#ffffff');
        circle(p.x, p.y, 14);
        // crosshair
        strokeWeight(1.5);
        line(p.x - 5, p.y, p.x + 5, p.y);
        line(p.x, p.y - 5, p.x, p.y + 5);
      }
    }

    function draw() {
      background('#ffffff');
      drawAxes();

      // Compute cubic from locators
      const cubicCoeffs = solveCubicCoefficients(locators);
      // Current osculating quadratic at t
      const quadCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);

      if (showFamily) {
        drawFamily(cubicCoeffs);
        drawQuadraticLine(quadCoeffs, colorQuad, 3.5);
      } else {
        drawCubic(cubicCoeffs);
        drawQuadraticLine(quadCoeffs, colorQuad, 3.5);
      }

      // Tangency point marker on the cubic (for guidance when cubic visible)
      if (!showFamily) {
        const yT = cubicAt(t, cubicCoeffs);
        const pT = worldToScreen(t, yT);
        noStroke();
        fill(colorQuad);
        circle(pT.x, pT.y, 6);
      }

      // Draw locators on top
      drawLocators();

      // Small legend text
      noStroke();
      fill('#555');
      textAlign(LEFT, TOP);
      text('Blue: cubic through 4 points\nOrange: osculating quadratic at x = ' + t.toFixed(2), 10, 10);
    }

    // Dragging logic
    function mousePressed() {
      // Only if within canvas
      if (mouseX < 0 || mouseX > width || mouseY < 0 || mouseY > height) return;
      // Find locator under cursor
      const HIT_R = 10; // pixels
      let idx = -1;
      for (let i = 0; i < locators.length; i++) {
        const p = worldToScreen(locators[i].x, locators[i].y);
        const d = Math.hypot(mouseX - p.x, mouseY - p.y);
        if (d <= HIT_R) { idx = i; break; }
      }
      draggingIndex = idx;
    }

    function mouseDragged() {
      if (draggingIndex < 0) return;
      const w = screenToWorld(mouseX, mouseY);
      // Clamp to visible world range to keep points in view
      locators[draggingIndex].x = clamp(w.x, X_MIN, X_MAX);
      locators[draggingIndex].y = clamp(w.y, Y_MIN, Y_MAX);
    }

    function mouseReleased() {
      draggingIndex = -1;
    }
  </script>
</body>
