  #plot-container, #piston-container {
    flex: 1 1 0;
    min-height: 360px;
    background: #0f1620;
    border: 1px solid var(--border);
    border-radius: 10px;
    position: relative;
    overflow: hidden;
  }
  #plot-container {
    padding: 4px;
  }
  /* Ensure Plotly fills container nicely */
  #plot-container > .js-plotly-plot, #plot-container > div {
    width: 100% !important;
    height: 100% !important;
  }

  /* Small helper badge for axes units (visual only) */
