</style>
</head>
<body>
  <div id="demo-container">
    <div id="control-panel">
      <div class="control-group">
        <label for="slider-v0">initial volume v₀</label>
        <input type="range" id="slider-v0" min="100" max="500" step="1" value="211">
      </div>
      <div class="control-group">
        <label for="slider-T">temperature T</label>
        <input type="range" id="slider-T" min="-273" max="727" step="1" value="270">
      </div>
      <div class="control-group">
        <label>temperature units</label>
        <div class="toggle">
          <input type="radio" id="radio-kelvin" name="temp-units" value="K">
          <label for="radio-kelvin" class="toggle-btn">Kelvin</label>
          <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
          <label for="radio-celsius" class="toggle-btn">Celsius</label>
        </div>
      </div>
    </div>

    <div id="visualization-area">
      <div id="plot-container">
        <div class="unit-badge" id="unit-badge">temperature °C</div>
      </div>
      <div id="piston-container"></div>
    </div>
  </div>

<script>
  // ----- Global state and constants -----
  const T0 = 273.15; // reference temperature (K)
  const yMax = 2000; // y-axis and piston mapping maximum
  const xRangeC = { min: -273, max: 727 };
  const xRangeK = { min: 0, max: 1000 };
  const N_POINTS = 121; // for segmented gradient line

  const state = {
    units: 'C',
    v0: 211,
    k: 211 / T0,
    T_display: 270 // in current units
  };

  const el = {
    v0: document.getElementById('slider-v0'),
    T: document.getElementById('slider-T'),
    radioK: document.getElementById('radio-kelvin'),
    radioC: document.getElementById('radio-celsius'),
    plot: document.getElementById('plot-container'),
    unitBadge: document.getElementById('unit-badge')
  };

  function clamp(v, lo, hi) { return Math.max(lo, Math.min(hi, v)); }
  function lerp(a, b, t) { return a + (b - a) * t; }

  // Color mapping for temperature (Kelvin) -> RGB and Plotly color string
  function colorRGBfromKelvin(TK) {
    const t = clamp(TK, 0, 1000);
    let r, g, b;
    if (t <= 500) {
      const u = t / 500;
      r = lerp(128, 255, u);
      g = lerp(0, 255, u);
      b = lerp(128, 0, u);
    } else {
      const u = (t - 500) / 500;
      r = lerp(255, 255, u);
      g = lerp(255, 0, u);
      b = lerp(0, 0, u);
    }
    return [Math.round(r), Math.round(g), Math.round(b)];
  }
  function plotColorFromKelvin(TK) {
    const [r, g, b] = colorRGBfromKelvin(TK);
    return `rgb(${r},${g},${b})`;
  }

  // Current computed values
  function currentTK() {
    return state.units === 'C' ? (state.T_display + T0) : state.T_display;
  }
  function currentVolume() {
    return state.k * currentTK();
  }

  // Build gradient line segments for Plotly
  function buildLineSegments() {
    const traces = [];
    const range = state.units === 'C' ? xRangeC : xRangeK;
    const xMin = range.min;
    const xMax = range.max;
    const xs = [];
    const TsK = [];
    const Vs = [];
    for (let i = 0; i < N_POINTS; i++) {
      const t = i / (N_POINTS - 1);
      const x = lerp(xMin, xMax, t);
      const TK = state.units === 'C' ? x + T0 : x;
      const V = state.k * TK;
      xs.push(x);
      TsK.push(TK);
      Vs.push(V);
    }
    // Create line segment per consecutive pair with its own color
    for (let i = 0; i < xs.length - 1; i++) {
      const TKmid = (TsK[i] + TsK[i + 1]) / 2;
      const seg = {
        x: [xs[i], xs[i + 1]],
        y: [Vs[i], Vs[i + 1]],
        mode: 'lines',
        line: { color: plotColorFromKelvin(TKmid), width: 3 },
        hoverinfo: 'skip',
        showlegend: false
      };
      traces.push(seg);
    }
    return traces;
  }

  function plotLayout() {
    const range = state.units === 'C' ? xRangeC : xRangeK;
    const xlabel = state.units === 'C' ? 'temperature °C' : 'temperature K';
    return {
      margin: { l: 60, r: 20, t: 10, b: 50 },
      paper_bgcolor: '#0f1620',
      plot_bgcolor: '#0f1620',
      xaxis: {
        title: xlabel,
        range: [range.min, range.max],
        gridcolor: '#223040',
        zeroline: false,
        tickfont: { color: '#cfe0f2' },
        titlefont: { color: '#cfe0f2' }
      },
      yaxis: {
        title: 'volume',
        range: [0, yMax],
        gridcolor: '#223040',
        zeroline: false,
        tickfont: { color: '#cfe0f2' },
        titlefont: { color: '#cfe0f2' }
      },
      showlegend: false
    };
  }

  function renderPlot() {
    const lineTraces = buildLineSegments();

    const TKc = currentTK();
    const Vc = currentVolume();
    const xCurrent = state.units === 'C' ? (state.T_display) : TKc;
    const markerTrace = {
      x: [xCurrent],
      y: [Vc],
      mode: 'markers',
      marker: {
        size: 12,
        color: plotColorFromKelvin(TKc),
        line: { color: 'white', width: 1 }
      },
      hovertemplate:
        (state.units === 'C' ? 'T = %{x:.0f} °C' : 'T = %{x:.0f} K') +
        '<br>V = %{y:.1f}<extra></extra>'
    };

    const traces = [...lineTraces, markerTrace];
    const layout = plotLayout();
    const config = { responsive: true, displayModeBar: false };
    Plotly.react(el.plot, traces, layout, config);

    // Update unit badge visual
    el.unitBadge.textContent = state.units === 'C' ? 'temperature °C' : 'temperature K';
  }

  // ----- p5.js piston sketch -----
  let pistonP5 = null;
  const pistonSketch = (p) => {
    let cw, ch;
    let padding = 20;

    p.setup = function() {
      const parent = document.getElementById('piston-container');
      cw = parent.clientWidth - 2; // account for border
      ch = Math.max(320, Math.floor(cw * 0.6));
      p.createCanvas(cw, ch).parent('piston-container');
      p.noLoop();
    };

    p.windowResized = function() {
      const parent = document.getElementById('piston-container');
      cw = parent.clientWidth - 2;
      ch = Math.max(320, Math.floor(cw * 0.6));
      p.resizeCanvas(cw, ch);
      p.redraw();
    };

    function gasColorFromTK(TK) {
      // Use p5.lerpColor between 0K(purple)->500K(yellow)->1000K(red)
      const color_low = p.color(128, 0, 128);
      const color_mid = p.color(255, 255, 0);
      const color_high = p.color(255, 0, 0);
      const t = clamp(TK, 0, 1000);
      if (t <= 500) {
        const u = t / 500;
        return p.lerpColor(color_low, color_mid, u);
      } else {
        const u = (t - 500) / 500;
        return p.lerpColor(color_mid, color_high, u);
      }
    }

    p.draw = function() {
      p.clear();
      p.background('#0f1620');

      // Cylinder geometry
      const wallThickness = 12;
      const baseMargin = 30;
      const topMargin = 30;
      const innerLeft = padding + wallThickness + 10;
      const innerRight = cw - padding - wallThickness - 10;
      const innerBottom = ch - baseMargin - wallThickness;
      const innerTop = topMargin + 10;
      const innerWidth = innerRight - innerLeft;
      const innerHeight = innerBottom - innerTop;

      // Draw cylinder walls and base (static blue)
      p.noStroke();
      p.fill(40, 100, 180);
      // Left wall
      p.rect(padding, topMargin, wallThickness, ch - topMargin - baseMargin, 4);
      // Right wall
      p.rect(cw - padding - wallThickness, topMargin, wallThickness, ch - topMargin - baseMargin, 4);
      // Base
      p.rect(innerLeft, innerBottom, innerWidth, wallThickness, 2);

      // Compute dynamic gas height from current volume
      const Vc = currentVolume();
      const Vclamped = clamp(Vc, 0, yMax);
      const gasHeight = p.map(Vclamped, 0, yMax, 0, innerHeight);
      const pistonY = innerBottom - gasHeight; // piston sits on top of gas

      // Gas fill (temperature color)
      const TKc = currentTK();
      const gCol = gasColorFromTK(TKc);
      p.fill(gCol);
      p.noStroke();
      p.rect(innerLeft, pistonY, innerWidth, gasHeight);

      // Piston head (grey) and stem block
      p.stroke(200);
      p.strokeWeight(1);
      p.fill(180);
      const pistonThickness = 12;
      p.rect(innerLeft - 2, pistonY - pistonThickness, innerWidth + 4, pistonThickness, 2);
      // Vertical block on top
      const blockW = 30;
      const blockH = 26;
      const blockX = innerLeft + innerWidth / 2 - blockW / 2;
      p.rect(blockX, pistonY - pistonThickness - blockH, blockW, blockH, 3);

      // Pressure arrows (static, orange) pointing down
      p.noStroke();
      p.fill(255, 159, 28);
      const arrows = 5;
      for (let i = 0; i < arrows; i++) {
        const ax = innerLeft + (i + 0.5) * (innerWidth / arrows);
        const ay = innerTop + 6;
        // shaft
        p.rect(ax - 2, ay, 4, 22, 2);
        // head triangle
        p.triangle(ax - 8, ay + 18, ax + 8, ay + 18, ax, ay + 32);
      }

      // Labels
      p.noStroke();
      p.fill(200);
      p.textSize(12);
      p.textAlign(p.LEFT, p.TOP);
      p.text('cylinder + piston', padding + 4, 6);
      p.textAlign(p.RIGHT, p.TOP);
      p.text('gas', innerRight, pistonY + 4);
    };
  };

  // ----- Interaction handlers -----
  function setUnits(newUnits) {
    if (state.units === newUnits) return;
    // Convert current display temperature to preserve physical temperature
    const TK = currentTK(); // based on previous units
    let newDisplay = (newUnits === 'C') ? (TK - T0) : TK;
    newDisplay = Math.round(newDisplay);
    // Update slider attributes
    if (newUnits === 'C') {
      el.T.min = String(xRangeC.min);
      el.T.max = String(xRangeC.max);
      el.T.step = '1';
    } else {
      el.T.min = String(xRangeK.min);
      el.T.max = String(xRangeK.max);
      el.T.step = '1';
    }
    // Clamp and set value
    newDisplay = clamp(newDisplay, Number(el.T.min), Number(el.T.max));
    state.units = newUnits;
    state.T_display = newDisplay;
    el.T.value = String(newDisplay);

    renderAll();
  }

  function renderAll() {
    // Update physics constant
    state.k = state.v0 / T0;
    // Plot and piston
    renderPlot();
    if (pistonP5) pistonP5.redraw();
  }

  // ----- Initialize -----
  function init() {
    // Set initial slider attributes based on default Celsius
    el.T.min = String(xRangeC.min);
    el.T.max = String(xRangeC.max);
    el.T.step = '1';
    el.T.value = String(state.T_display);

    // Create Plot
    renderPlot();

    // Create p5 sketch
    pistonP5 = new p5(pistonSketch);

    // Bind events
    el.v0.addEventListener('input', () => {
      state.v0 = Number(el.v0.value);
      renderAll();
    });
    el.T.addEventListener('input', () => {
      state.T_display = Number(el.T.value);
      renderAll();
    });
    el.radioK.addEventListener('change', (e) => {
      if (e.target.checked) setUnits('K');
    });
    el.radioC.addEventListener('change', (e) => {
      if (e.target.checked) setUnits('C');
    });
  }

  document.addEventListener('DOMContentLoaded', init);
</script>
</body>
</html>
