      cx = width / 2;
      cy = height / 2 - 10;
      R = Math.min(width, height) * 0.33;
    }

    function draw() {
      background('#ffffff');

      // Compute points
      const aR = degToRad(angleA);
      const bR = degToRad(angleB);
      const cR = degToRad(angleC);

      const Ax = cx + R * Math.cos(aR);
      const Ay = cy + R * Math.sin(aR);
      const Bx = cx + R * Math.cos(bR);
      const By = cy + R * Math.sin(bR);
      const Cx = cx + R * Math.cos(cR);
      const Cy = cy + R * Math.sin(cR);

      // Arcs in degrees
      const arcBC = mod360(angleC - angleB);
      const arcCA = mod360(angleA - angleC);
      const arcAB = mod360(angleB - angleA);

      // Background sectors (AB green, BC yellow, CA blue)
      drawSector(cx, cy, R, aR, degToRad(arcAB), getColor('--sector-ab'));
      drawSector(cx, cy, R, bR, degToRad(arcBC), getColor('--sector-bc'));
      drawSector(cx, cy, R, cR, degToRad(arcCA), getColor('--sector-ca'));

      // Circle outline (light fill with alpha so sectors remain visible)
      push();
      stroke('#666666');
      strokeWeight(1.5);
      fill('rgba(170,204,222,0.2)'); // #aaccde with alpha
      circle(cx, cy, R * 2);
      // center point
      noStroke();
      fill('#888888');
      circle(cx, cy, 6);
      pop();

      // Triangle
      push();
      stroke('#666666');
      strokeWeight(2);
      fill('rgba(173,216,230,0.45)');
      beginShape();
      vertex(Ax, Ay);
      vertex(Bx, By);
      vertex(Cx, Cy);
      endShape(CLOSE);
      pop();

      // Vertices
      drawVertex(Ax, Ay);
      drawVertex(Bx, By);
      drawVertex(Cx, Cy);

      // Labels A, B, C just outside
      drawVertexLabel('A', angleA, '#00008B');
      drawVertexLabel('B', angleB, '#00008B');
      drawVertexLabel('C', angleC, '#00008B');

      // Highlight if a vertex is selected
      if (selectedVertex !== 'None') {
        let startAngleDeg, sweepDeg, vLabel, arcLabel;
        if (selectedVertex === 'A') {
          // Arc BC ↔ Vertex A
          startAngleDeg = angleB; sweepDeg = arcBC; vLabel = 'A'; arcLabel = 'BC';
        } else if (selectedVertex === 'B') {
          startAngleDeg = angleC; sweepDeg = arcCA; vLabel = 'B'; arcLabel = 'CA';
        } else { // 'C'
          startAngleDeg = angleA; sweepDeg = arcAB; vLabel = 'C'; arcLabel = 'AB';
        }
        const startR = degToRad(startAngleDeg);
        const sweepR = degToRad(sweepDeg);
        const endR = startR + sweepR;

        // Central angle arc (green) with arrow
        const centralRadius = R - 18;
        drawArcWithArrow(cx, cy, centralRadius, startR, endR, '#008000', 5);

        // Small center arc to emphasize central angle
        push();
        stroke('#008000');
        strokeWeight(4);
        noFill();
        const smallR = 42;
        drawArcSegments(cx, cy, smallR, startR, endR);
        pop();

        // Central angle label
        const midR = startR + sweepR / 2;
        const labelR = R * 0.60;
        const lx = cx + labelR * Math.cos(midR);
        const ly = cy + labelR * Math.sin(midR);
        push();
        noStroke();
        fill('#008000');
        textAlign(CENTER, CENTER);
        textStyle(BOLD);
        textSize(14);
        const thetaText = `θ = ${fmtDeg(sweepDeg)}`;
        text(thetaText, lx, ly);
        pop();

        // Inscribed (vertex) angle arc (red) at selected vertex
        let VX, VY, vTo1, vTo2; // angles from vertex to endpoints
        let P1x, P1y, P2x, P2y; // endpoints for the vertex's two rays
        if (selectedVertex === 'A') {
          VX = Ax; VY = Ay;
          P1x = Bx; P1y = By; P2x = Cx; P2y = Cy;
        } else if (selectedVertex === 'B') {
          VX = Bx; VY = By;
          P1x = Cx; P1y = Cy; P2x = Ax; P2y = Ay;
        } else {
          VX = Cx; VY = Cy;
          P1x = Ax; P1y = Ay; P2x = Bx; P2y = By;
        }
        vTo1 = Math.atan2(P1y - VY, P1x - VX);
        vTo2 = Math.atan2(P2y - VY, P2x - VX);
        let d = smallAngleDelta(vTo1, vTo2); // signed shortest angle
        let startVArc = vTo1;
        let sweepVArc = Math.abs(d);
        if (d < 0) {
          // draw CCW from vTo2 to vTo1
          startVArc = vTo2;
        }
        const inscribedR = 46;
        drawArcWithArrow(VX, VY, inscribedR, startVArc, startVArc + sweepVArc, '#FF0000', 5);

        // Inscribed label near vertex
        const midV = startVArc + sweepVArc / 2;
        const lvr = inscribedR + 18;
        const lvx = VX + lvr * Math.cos(midV);
        const lvy = VY + lvr * Math.sin(midV);
        const halfText = `θ/2 = ${fmtDeg(sweepDeg / 2)}`;
        push();
        noStroke();
        fill('#FF0000');
        textAlign(CENTER, CENTER);
        textStyle(BOLD);
        textSize(14);
        text(halfText, lvx, lvy);
        pop();
      }
    }

    function drawVertex(x, y) {
      push();
      stroke('#444');
      strokeWeight(2);
      fill('#ffffff');
      circle(x, y, 10);
      pop();
    }

    function drawVertexLabel(ch, angDeg, color) {
      const r = R + 18;
      const t = degToRad(angDeg);
      const x = cx + r * Math.cos(t);
      const y = cy + r * Math.sin(t);
      push();
      noStroke();
      fill(color || '#00008B');
      textAlign(CENTER, CENTER);
      textStyle(BOLD);
      textSize(18);
      text(ch, x, y);
      pop();
    }

    function getColor(varName) {
      return getComputedStyle(document.documentElement).getPropertyValue(varName).trim() || '#ddd';
    }

    // Draw filled sector (start angle + sweep), handling wrap
    function drawSector(x, y, radius, startAngle, sweepAngle, fillColor) {
      const endAngle = startAngle + sweepAngle;
      push();
      noStroke();
      fill(fillColor);
      if (endAngle <= Math.PI * 2) {
        arc(x, y, radius * 2, radius * 2, startAngle, endAngle, PIE);
      } else {
        // split into two to handle wrap
        arc(x, y, radius * 2, radius * 2, startAngle, Math.PI * 2, PIE);
        arc(x, y, radius * 2, radius * 2, 0, endAngle % (Math.PI * 2), PIE);
      }
      pop();
    }

    // Draw arc with CCW direction and add arrowhead at the end
    function drawArcWithArrow(x, y, radius, startAng, endAng, color, weight=4) {
      push();
      noFill();
      stroke(color);
      strokeWeight(weight);
      drawArcSegments(x, y, radius, startAng, endAng);

      // Arrowhead at end
      const ex = x + radius * Math.cos(endAng);
      const ey = y + radius * Math.sin(endAng);
      const tangent = endAng + Math.PI / 2; // CCW direction tangent
      const L = 14;
      const wing = 0.42;
      stroke(color);
      // two small wings
      line(ex, ey, ex - L * Math.cos(tangent - wing), ey - L * Math.sin(tangent - wing));
      line(ex, ey, ex - L * Math.cos(tangent + wing), ey - L * Math.sin(tangent + wing));
      pop();
    }

    // Draw arc that may wrap beyond 2π
    function drawArcSegments(x, y, radius, startAng, endAng) {
      if (endAng <= Math.PI * 2) {
        arc(x, y, radius * 2, radius * 2, startAng, endAng);
      } else {
        arc(x, y, radius * 2, radius * 2, startAng, Math.PI * 2);
        arc(x, y, radius * 2, radius * 2, 0, endAng % (Math.PI * 2));
      }
    }

    // ------------- Interactions -------------
    function attachEvents() {
      sliderA.addEventListener('input', () => {
        angleA = normDeg(parseFloat(sliderA.value));
        syncReadouts();
        updateInfoPanel();
        redraw();
      });
      sliderB.addEventListener('input', () => {
        angleB = normDeg(parseFloat(sliderB.value));
        syncReadouts();
        updateInfoPanel();
        redraw();
      });
      sliderC.addEventListener('input', () => {
        angleC = normDeg(parseFloat(sliderC.value));
        syncReadouts();
        updateInfoPanel();
        redraw();
      });

      radios.forEach(r => {
        r.addEventListener('change', e => {
          if (e.target.checked) {
            selectedVertex = e.target.value;
            updateInfoPanel();
            redraw();
          }
        });
      });
    }

    function syncReadouts() {
      readoutA.textContent = fmtDeg(angleA);
      readoutB.textContent = fmtDeg(angleB);
      readoutC.textContent = fmtDeg(angleC);
      sliderA.value = angleA;
      sliderB.value = angleB;
      sliderC.value = angleC;
    }

    function updateInfoPanel() {
      // Compute arcs
      const arcBC = mod360(angleC - angleB);
      const arcCA = mod360(angleA - angleC);
      const arcAB = mod360(angleB - angleA);

      const vA = arcBC / 2;
      const vB = arcCA / 2;
      const vC = arcAB / 2;

      // Helper to clear rows
      function clearRow(idx) {
        if (idx === 1) { arcLabel1.textContent = ''; arcValue1.textContent = ''; vertexLabel1.textContent = ''; vertexValue1.textContent = ''; }
        if (idx === 2) { arcLabel2.textContent = ''; arcValue2.textContent = ''; vertexLabel2.textContent = ''; vertexValue2.textContent = ''; }
        if (idx === 3) { arcLabel3.textContent = ''; arcValue3.textContent = ''; vertexLabel3.textContent = ''; vertexValue3.textContent = ''; }
      }

      // Reset
      clearRow(1); clearRow(2); clearRow(3);
      arcTotal.textContent = '?';
      vertexTotal.textContent = '?';

      if (selectedVertex === 'A') {
        arcLabel1.textContent = 'Arc BC';
        arcValue1.textContent = fmtDeg(arcBC);
        vertexLabel1.textContent = 'Vertex A';
        vertexValue1.textContent = fmtDeg(vA);
      } else if (selectedVertex === 'B') {
        arcLabel1.textContent = 'Arc CA';
        arcValue1.textContent = fmtDeg(arcCA);
        vertexLabel1.textContent = 'Vertex B';
        vertexValue1.textContent = fmtDeg(vB);
      } else if (selectedVertex === 'C') {
        arcLabel1.textContent = 'Arc AB';
        arcValue1.textContent = fmtDeg(arcAB);
        vertexLabel1.textContent = 'Vertex C';
        vertexValue1.textContent = fmtDeg(vC);
      } else {
        // None: show all
        arcLabel1.textContent = 'Arc AB';
        arcValue1.textContent = fmtDeg(arcAB);
        vertexLabel1.textContent = 'Vertex C';
        vertexValue1.textContent = fmtDeg(vC);

        arcLabel2.textContent = 'Arc BC';
        arcValue2.textContent = fmtDeg(arcBC);
        vertexLabel2.textContent = 'Vertex A';
        vertexValue2.textContent = fmtDeg(vA);

        arcLabel3.textContent = 'Arc CA';
        arcValue3.textContent = fmtDeg(arcCA);
        vertexLabel3.textContent = 'Vertex B';
        vertexValue3.textContent = fmtDeg(vB);

        arcTotal.textContent = '360°';
        vertexTotal.textContent = '180°';
      }
    }

    // ------------- Mouse dragging on canvas -------------
    function mousePressed() {
      if (!isMouseOnCanvas()) return;
      const pts = getVertexPositions();
      const m = createVector(mouseX, mouseY);
      const thresh = 16;

      if (p5.Vector.dist(m, pts.A) <= thresh) dragging = 'A';
      else if (p5.Vector.dist(m, pts.B) <= thresh) dragging = 'B';
      else if (p5.Vector.dist(m, pts.C) <= thresh) dragging = 'C';
    }

    function mouseDragged() {
      if (!dragging || !isMouseOnCanvas()) return;
      const ang = normDeg(radToDeg(Math.atan2(mouseY - cy, mouseX - cx)));
      if (dragging === 'A') angleA = ang;
      if (dragging === 'B') angleB = ang;
      if (dragging === 'C') angleC = ang;
      // Sync sliders & redraw
      syncReadouts();
      updateInfoPanel();
      redraw();
    }

    function mouseReleased() {
      dragging = null;
    }

    function isMouseOnCanvas() {
      return mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height;
    }

    function getVertexPositions() {
      return {
        A: createVector(cx + R * Math.cos(degToRad(angleA)), cy + R * Math.sin(degToRad(angleA))),
        B: createVector(cx + R * Math.cos(degToRad(angleB)), cy + R * Math.sin(degToRad(angleB))),
        C: createVector(cx + R * Math.cos(degToRad(angleC)), cy + R * Math.sin(degToRad(angleC))),
      };
    }
  </script>
</body>
