    </div>
  </div>

<script>
(function() {
  // ----- Global configuration and state -----
  const N = 100;                         // number of spatial grid points
  const xMin = 0.0, xMax = 1.0;
  const dx = (xMax - xMin) / (N - 1);
  const dx2 = dx * dx;
  const xiGrid = new Float64Array(N);
  for (let i = 0; i < N; i++) xiGrid[i] = xMin + i * dx;

  // State variables (arrays)
  let c = new Float64Array(N);
  let th = new Float64Array(N);

  // Scratch arrays for RK4
  let k1c = new Float64Array(N), k2c = new Float64Array(N), k3c = new Float64Array(N), k4c = new Float64Array(N);
  let k1t = new Float64Array(N), k2t = new Float64Array(N), k3t = new Float64Array(N), k4t = new Float64Array(N);
  let tmpC = new Float64Array(N), tmpT = new Float64Array(N);

  // Parameters
  let tau = 1.0, phi = 2.0, beta = 0.2, gamma = 20.0, xi = 0.4;

  // ----- DOM elements -----
  const elTau = document.getElementById('slider-tau');
  const elPhi = document.getElementById('slider-phi');
  const elBeta = document.getElementById('slider-beta');
  const elGamma = document.getElementById('slider-gamma');
  const elXi = document.getElementById('slider-xi');

  const spTau = document.getElementById('value-tau');
  const spPhi = document.getElementById('value-phi');
  const spBeta = document.getElementById('value-beta');
  const spGamma = document.getElementById('value-gamma');
  const spXi = document.getElementById('value-xi');

  const btnDecTau = document.getElementById('btn-dec-tau');
  const btnIncTau = document.getElementById('btn-inc-tau');
  const btnDecPhi = document.getElementById('btn-dec-phi');
  const btnIncPhi = document.getElementById('btn-inc-phi');
  const btnDecBeta = document.getElementById('btn-dec-beta');
  const btnIncBeta = document.getElementById('btn-inc-beta');
  const btnDecGamma = document.getElementById('btn-dec-gamma');
  const btnIncGamma = document.getElementById('btn-inc-gamma');
  const btnDecXi = document.getElementById('btn-dec-xi');
  const btnIncXi = document.getElementById('btn-inc-xi');

  const plotC = document.getElementById('plot-concentration');
  const plotT = document.getElementById('plot-temperature');

  // ----- Utilities -----
  function clamp(val, min, max) { return Math.max(min, Math.min(max, val)); }
  function fmt(val, digits) { return Number(val).toFixed(digits); }

  function updateValueSpans() {
    spTau.textContent = fmt(tau, 2);
    // For φ display minimal decimal where step is 0.1
    spPhi.textContent = (Math.round(phi * 10) / 10).toFixed(1);
    spBeta.textContent = fmt(beta, 2);
    spGamma.textContent = fmt(gamma, 0);
    spXi.textContent = fmt(xi, 3);
  }

  function initStateArrays() {
    // Initial conditions at τ=0:
    // c(ξ, 0) = 0 in interior; θ(ξ, 0) = 1
    for (let i = 0; i < N - 1; i++) {
      c[i] = 0.0;
      th[i] = 1.0;
    }
    // Enforce boundary at surface ξ=1: c=1, θ=1
    c[N - 1] = 1.0;
    th[N - 1] = 1.0;
  }

  // Compute RHS for the coupled system at given c, th
  function computeRHS(cArr, tArr, dc, dt) {
    const phisq = phi * phi;
    const b = beta;
    const g = gamma;

    // i = 0 (center, Neumann zero-gradient -> mirrored)
    {
      const d2c = 2.0 * (cArr[1] - cArr[0]) / dx2;
      const d2t = 2.0 * (tArr[1] - tArr[0]) / dx2;
      const thetaClamped = Math.max(tArr[0], 0.05);
      const expo = Math.exp(g * (1.0 - 1.0 / thetaClamped));
      const r = phisq * cArr[0] * expo;
      dc[0] = d2c - r;
      dt[0] = d2t + b * r;
    }

    // Interior points
    for (let i = 1; i < N - 1; i++) {
      const d2c = (cArr[i + 1] - 2.0 * cArr[i] + cArr[i - 1]) / dx2;
      const d2t = (tArr[i + 1] - 2.0 * tArr[i] + tArr[i - 1]) / dx2;
      const thetaClamped = Math.max(tArr[i], 0.05);
      const expo = Math.exp(g * (1.0 - 1.0 / thetaClamped));
      const r = phisq * cArr[i] * expo;
      dc[i] = d2c - r;
      dt[i] = d2t + b * r;
    }

    // i = N - 1 (surface, Dirichlet)
    dc[N - 1] = 0.0;
    dt[N - 1] = 0.0;
  }

  function rk4Integrate(tauEnd) {
    // Explicit RK4 with fixed time step determined by stability consideration for diffusion
    // Stability limit for RK4 on diffusion: dt_max ≈ 0.7 * dx^2
    const dtMax = 0.7 * dx2;
    const steps = Math.max(1, Math.ceil(tauEnd / dtMax));
    const h = tauEnd / steps;

    let t = 0.0;
    for (let s = 0; s < steps; s++) {
      const hh = h;

      // Enforce boundary before RHS
      c[N - 1] = 1.0; th[N - 1] = 1.0;
      computeRHS(c, th, k1c, k1t);

      // k2
      for (let i = 0; i < N; i++) {
        tmpC[i] = c[i] + 0.5 * hh * k1c[i];
        tmpT[i] = th[i] + 0.5 * hh * k1t[i];
      }
      tmpC[N - 1] = 1.0; tmpT[N - 1] = 1.0;
      computeRHS(tmpC, tmpT, k2c, k2t);

      // k3
      for (let i = 0; i < N; i++) {
        tmpC[i] = c[i] + 0.5 * hh * k2c[i];
        tmpT[i] = th[i] + 0.5 * hh * k2t[i];
      }
      tmpC[N - 1] = 1.0; tmpT[N - 1] = 1.0;
      computeRHS(tmpC, tmpT, k3c, k3t);

      // k4
      for (let i = 0; i < N; i++) {
        tmpC[i] = c[i] + hh * k3c[i];
        tmpT[i] = th[i] + hh * k3t[i];
      }
      tmpC[N - 1] = 1.0; tmpT[N - 1] = 1.0;
      computeRHS(tmpC, tmpT, k4c, k4t);

      // Update solution
      for (let i = 0; i < N; i++) {
        c[i] += (hh / 6.0) * (k1c[i] + 2.0 * k2c[i] + 2.0 * k3c[i] + k4c[i]);
        th[i] += (hh / 6.0) * (k1t[i] + 2.0 * k2t[i] + 2.0 * k3t[i] + k4t[i]);
      }

      // Enforce physical bounds and Dirichlet constraints
      c[N - 1] = 1.0; th[N - 1] = 1.0;
      for (let i = 0; i < N - 1; i++) {
        // Keep concentration within [0, 1.1] to avoid numeric blowups
        if (c[i] < 0) c[i] = 0;
        if (c[i] > 1.1) c[i] = 1.1;
        // Keep temperature positive to avoid division by zero in subsequent steps
        if (th[i] < 0.05) th[i] = 0.05;
      }

      t += hh;
    }
  }

  function interpolateAtXi(arr, xiVal) {
    // Linear interpolation on uniform grid
    if (xiVal <= xMin) return arr[0];
    if (xiVal >= xMax) return arr[N - 1];
    const idx = Math.floor((xiVal - xMin) / dx);
    const i = Math.min(idx, N - 2);
    const x0 = xiGrid[i], x1 = xiGrid[i + 1];
    const y0 = arr[i], y1 = arr[i + 1];
    const w = (xiVal - x0) / (x1 - x0);
    return y0 * (1 - w) + y1 * w;
  }

  function makeConcentrationFigure() {
    const cxi = interpolateAtXi(c, xi);
    const markerX = [xi, xi, 0.0];
    const markerY = [0.0, cxi, cxi];

    const traces = [
      {
        type: 'scatter',
        mode: 'lines',
        x: Array.from(xiGrid),
        y: Array.from(c),
        line: { color: '#1f77b4', width: 2 },
        name: 'c(ξ)'
      },
      {
        type: 'scatter',
        mode: 'lines',
        x: markerX,
        y: markerY,
        line: { color: '#333333', width: 1.5, dash: 'dot' },
        showlegend: false,
        hoverinfo: 'skip'
      }
    ];

    const annotationText = `τ = ${fmt(tau, 2)}\nξ = ${fmt(xi, 3)}\nc = ${fmt(cxi, 5)}`;

    const layout = {
      margin: { l: 50, r: 20, t: 10, b: 40 },
      xaxis: { title: 'ξ', range: [0, 1.0] },
      yaxis: { title: 'c', range: [0, 1.1] },
      annotations: [
        {
          x: 0.8, y: 0.85, xref: 'x', yref: 'paper',
          text: annotationText, showarrow: false, align: 'left',
          bordercolor: '#999', borderwidth: 1, bgcolor: 'rgba(255,255,255,0.8)',
          font: { size: 12 }
        }
      ]
    };

    return { traces, layout };
  }

  function makeTemperatureFigure() {
    const thetaxi = interpolateAtXi(th, xi);
    // Initial guess for y_min before Plotly computes autorange
    const yMinGuess = Math.min.apply(null, th);

    const markerX = [xi, xi, 0.0];
    const markerY = [yMinGuess, thetaxi, thetaxi];

    const traces = [
      {
        type: 'scatter',
        mode: 'lines',
        x: Array.from(xiGrid),
        y: Array.from(th),
        line: { color: '#d62728', width: 2 },
        name: 'θ(ξ)'
      },
      {
        type: 'scatter',
        mode: 'lines',
        x: markerX,
        y: markerY,
        line: { color: '#333333', width: 1.5, dash: 'dot' },
        showlegend: false,
        hoverinfo: 'skip'
      }
    ];

    const yMin = yMinGuess;
    const yMax = Math.max.apply(null, th);
    const yPos = yMin + 0.75 * (yMax - yMin);

    const annotationText = `τ = ${fmt(tau, 2)}\nξ = ${fmt(xi, 3)}\nθ = ${fmt(thetaxi, 5)}`;

    const layout = {
      margin: { l: 50, r: 20, t: 10, b: 40 },
      xaxis: { title: 'ξ', range: [0, 1.0] },
      yaxis: { title: 'θ', autorange: true },
      annotations: [
        {
          x: 0.8, y: yPos, xref: 'x', yref: 'y',
          text: annotationText, showarrow: false, align: 'left',
          bordercolor: '#999', borderwidth: 1, bgcolor: 'rgba(255,255,255,0.8)',
          font: { size: 12 }
        }
      ]
    };

    return { traces, layout };
  }

  function simulateAndRender() {
    updateValueSpans();
    initStateArrays();
    rk4Integrate(tau);

    // Prepare figures
    const concFig = makeConcentrationFigure();
    const tempFig = makeTemperatureFigure();

    // Render/Update plots
    Plotly.react(plotC, concFig.traces, concFig.layout, {displayModeBar: false, responsive: true});
    Plotly.react(plotT, tempFig.traces, tempFig.layout, {displayModeBar: false, responsive: true});

    // Adjust temperature marker to actual autoranged y-axis
    requestAnimationFrame(() => {
      updateXiMarker(); // ensures marker lines and annotations are consistent with current axes
    });
  }

  function updateXiMarker() {
    const cxi = interpolateAtXi(c, xi);
    const thetaxi = interpolateAtXi(th, xi);

    // Update concentration marker (trace index 1)
    const xMarkerC = [[xi, xi, 0.0]];
    const yMarkerC = [[0.0, cxi, cxi]];
    Plotly.restyle(plotC, { x: xMarkerC, y: yMarkerC }, [1]);

    const cAnnoText = `τ = ${fmt(tau, 2)}\nξ = ${fmt(xi, 3)}\nc = ${fmt(cxi, 5)}`;
    Plotly.relayout(plotC, { 'annotations[0].text': cAnnoText });

    // Determine current y-axis range for temperature plot
    let yMinTemp = Math.min.apply(null, th);
    if (plotT && plotT._fullLayout && plotT._fullLayout.yaxis && Array.isArray(plotT._fullLayout.yaxis.range)) {
      const yr = plotT._fullLayout.yaxis.range;
      yMinTemp = Math.min(yr[0], yr[1]);
    }

    const xMarkerT = [[xi, xi, 0.0]];
    const yMarkerT = [[yMinTemp, thetaxi, thetaxi]];
    Plotly.restyle(plotT, { x: xMarkerT, y: yMarkerT }, [1]);

    const yMaxTemp = (plotT && plotT._fullLayout && plotT._fullLayout.yaxis && Array.isArray(plotT._fullLayout.yaxis.range))
      ? Math.max(plotT._fullLayout.yaxis.range[0], plotT._fullLayout.yaxis.range[1])
      : Math.max.apply(null, th);
    const yPos = yMinTemp + 0.75 * (yMaxTemp - yMinTemp);
    const tAnnoText = `τ = ${fmt(tau, 2)}\nξ = ${fmt(xi, 3)}\nθ = ${fmt(thetaxi, 5)}`;
    Plotly.relayout(plotT, {
      'annotations[0].text': tAnnoText,
      'annotations[0].y': yPos
    });

    // Update displayed ξ value
    spXi.textContent = fmt(xi, 3);
  }

  // ----- Event binding -----
  function bindSpinButtons(btnDec, btnInc, slider) {
    btnDec.addEventListener('click', () => {
      const step = parseFloat(slider.step);
      const min = parseFloat(slider.min);
      const current = parseFloat(slider.value);
      const next = clamp(current - step, min, parseFloat(slider.max));
      slider.value = next;
      slider.dispatchEvent(new Event('input', { bubbles: true }));
    });
    btnInc.addEventListener('click', () => {
      const step = parseFloat(slider.step);
      const max = parseFloat(slider.max);
      const current = parseFloat(slider.value);
      const next = clamp(current + step, parseFloat(slider.min), max);
      slider.value = next;
      slider.dispatchEvent(new Event('input', { bubbles: true }));
    });
  }

  // τ, φ, β, γ -> full simulation re-run
  elTau.addEventListener('input', () => {
    tau = parseFloat(elTau.value);
    simulateAndRender();
  });

  elPhi.addEventListener('input', () => {
    phi = parseFloat(elPhi.value);
    simulateAndRender();
  });

  elBeta.addEventListener('input', () => {
    beta = parseFloat(elBeta.value);
    simulateAndRender();
  });

  elGamma.addEventListener('input', () => {
    gamma = parseFloat(elGamma.value);
    simulateAndRender();
  });

  // ξ -> only marker update
  elXi.addEventListener('input', () => {
    xi = parseFloat(elXi.value);
    updateXiMarker();
  });

  bindSpinButtons(btnDecTau, btnIncTau, elTau);
  bindSpinButtons(btnDecPhi, btnIncPhi, elPhi);
  bindSpinButtons(btnDecBeta, btnIncBeta, elBeta);
  bindSpinButtons(btnDecGamma, btnIncGamma, elGamma);
  bindSpinButtons(btnDecXi, btnIncXi, elXi);

  // Resize handling
  window.addEventListener('resize', () => {
    Plotly.Plots.resize(plotC);
    Plotly.Plots.resize(plotT);
    // keep marker aligned after resize
    requestAnimationFrame(updateXiMarker);
  });

  // ----- Initialize -----
  function initializeFromDefaults() {
    tau = parseFloat(elTau.value);
    phi = parseFloat(elPhi.value);
    beta = parseFloat(elBeta.value);
    gamma = parseFloat(elGamma.value);
    xi = parseFloat(elXi.value);
    updateValueSpans();
    simulateAndRender();
  }

  // Kick off
  initializeFromDefaults();

})();
</script>
</body>
</html>
