        <input id="slider-b0" type="range" min="1" max="100" step="0.1" value="30"/>
        <span class="plus-sign">+</span>
        <span class="value" id="value-b0">30</span>
      </div>
      <div class="slider-row">
        <label for="slider-r">intrinsic growth rate \\(r\\)</label>
        <input id="slider-r" type="range" min="0.01" max="0.2" step="0.001" value="0.113"/>
        <span class="plus-sign">+</span>
        <span class="value" id="value-r">0.113</span>
      </div>
      <div class="slider-row">
        <label for="slider-beta">shape exponent \\(\\beta\\)</label>
        <input id="slider-beta" type="range" min="0.1" max="1.5" step="0.001" value="0.477"/>
        <span class="plus-sign">+</span>
        <span class="value" id="value-beta">0.477</span>
      </div>
    </div>

    <div id="visualization-area">
      <div id="viz-flex">
        <div id="plot-container"></div>
        <div id="table-container">
          <table>
            <thead>
              <tr>
                <th>Age (years)</th>
                <th>Volume (m³ ha⁻¹)</th>
              </tr>
            </thead>
            <tbody id="data-tbody">
              <!-- Filled via JS -->
            </tbody>
          </table>
        </div>
      </div>
      <div id="results-display">
        <p id="result-u">rejuvenation bias u = —</p>
        <p id="result-p">site productivity p = —</p>
      </div>
    </div>
  </div>

<script>
(function(){
  // Static empirical data
  const ages = [30, 35, 40, 45, 50, 55];
  const volumes = [289, 445, 565, 704, 833, 924];

  // Populate table with static data
  function populateTable() {
    const tbody = document.getElementById('data-tbody');
    tbody.innerHTML = '';
    for (let i = 0; i < ages.length; i++) {
      const tr = document.createElement('tr');
      const tdAge = document.createElement('td');
      const tdVol = document.createElement('td');
      tdAge.textContent = ages[i].toString();
      tdVol.textContent = volumes[i].toString();
      tr.appendChild(tdAge);
      tr.appendChild(tdVol);
      tbody.appendChild(tr);
    }
  }

  // Utility to format slider readouts
  function setValueDisplays(bmax, b0, r, beta) {
    document.getElementById('value-bmax').textContent = Math.round(bmax).toString();
    document.getElementById('value-b0').textContent = b0.toFixed(1);
    document.getElementById('value-r').textContent = r.toFixed(3);
    document.getElementById('value-beta').textContent = beta.toFixed(3);
  }

  // Prepare x-domain for plotting
  function linspace(a, b, n) {
    const arr = new Array(n);
    const step = (b - a) / (n - 1);
    for (let i = 0; i < n; i++) arr[i] = a + i * step;
    return arr;
  }

  // Main update logic
  function update() {
    const bmax = parseFloat(document.getElementById('slider-bmax').value);
    const b0   = parseFloat(document.getElementById('slider-b0').value);
    const r    = parseFloat(document.getElementById('slider-r').value);
    const beta = parseFloat(document.getElementById('slider-beta').value);

    setValueDisplays(bmax, b0, r, beta);

    // Time axis for curves
    const tMin = 15, tMax = 80, N = 400;
    const t = linspace(tMin, tMax, N);

    // Richards curve
    // V(t) = bmax / (1 + M * exp(-r*t))^(1/beta) where M = (bmax/b0)^beta - 1
    const ratio = (bmax / b0);
    const M = Math.pow(ratio, beta) - 1;

    const V_rich = t.map(tt => bmax / Math.pow(1 + M * Math.exp(-r * tt), 1 / beta));

    // Inflection point properties
    // ti = ln( ((bmax/b0)^beta - 1) * beta ) / r
    // Vi = bmax * ( (beta+1) / (2*beta) )^(1/beta)
    // V'i = (r / beta) * Vi * (1 - (Vi/bmax)^beta)
    let ti = NaN, Vi = NaN, dVi = NaN, u = NaN, p = NaN;
    let fourFifthY = new Array(t.length).fill(null);

    const argLog = ((Math.pow(bmax / b0, beta) - 1) * beta);
    if (argLog > 0 && r > 0 && beta > 0) {
      ti = Math.log(argLog) / r;
      const frac = (beta + 1) / (2 * beta);
      Vi = bmax * Math.pow(frac, 1 / beta);
      const Vi_over_b = Vi / bmax;
      dVi = (r / beta) * Vi * (1 - Math.pow(Vi_over_b, beta));

      // 4/5 law parameters
      // u = ti - (4/5) * Vi / V'i
      // p = Vi / (ti - u)^(4/5)
      const denom = dVi;
      if (isFinite(denom) && Math.abs(denom) > 1e-12) {
        const delta = (4/5) * (Vi / denom);
        u = ti - delta;
        const base = (ti - u);
        if (base > 0) {
          p = Vi / Math.pow(base, 4/5);
          // Build 4/5 law curve only where t > u
          for (let i = 0; i < t.length; i++) {
            const tt = t[i];
            const diff = tt - u;
            fourFifthY[i] = (diff > 0) ? p * Math.pow(diff, 4/5) : null;
          }
        }
      }
    }

    // Update results display
    const uText = (isFinite(u)) ? u.toFixed(4) : '—';
    const pText = (isFinite(p)) ? p.toFixed(4) : '—';
    document.getElementById('result-u').textContent = `rejuvenation bias u = ${uText}`;
    document.getElementById('result-p').textContent = `site productivity p = ${pText}`;

    // Determine y-axis upper bound: at least 1600 or slightly above bmax
    const ymax = Math.max(1600, Math.ceil(bmax * 1.05 / 50) * 50);

    // Plotly react
    const traces = [
      {
        x: ages,
        y: volumes,
        type: 'scatter',
        mode: 'markers',
        marker: { color: '#1f77b4', size: 8, opacity: 0.9 },
        name: 'data points',
        hovertemplate: 'Age: %{x} yr<br>Vol: %{y} m³/ha<extra></extra>'
      },
      {
        x: t,
        y: V_rich,
        type: 'scatter',
        mode: 'lines',
        line: { color: '#ff7f0e', width: 3 },
        name: 'Richards curve',
        hovertemplate: 't=%{x:.1f}<br>V=%{y:.1f}<extra></extra>'
      },
      {
        x: t,
        y: fourFifthY,
        type: 'scatter',
        mode: 'lines',
        line: { color: '#1f77b4', width: 3 },
        name: '4/5 law',
        hovertemplate: 't=%{x:.1f}<br>V₄⁄₅=%{y:.1f}<extra></extra>'
      }
    ];

    const layout = {
      title: { text: 'forest growth curve', x: 0.02, xanchor: 'left' },
      margin: { t: 40, r: 10, b: 50, l: 70 },
      plot_bgcolor: '#FFFFF0',
      paper_bgcolor: '#ffffff',
      xaxis: {
        title: 'stand age (years)',
        range: [15, 80],
        showgrid: true,
        gridcolor: 'lightgrey',
        gridwidth: 1,
        zeroline: false,
        griddash: 'dot'
      },
      yaxis: {
        title: { text: 'wood volume, m³ ha⁻¹\nRichards curve vs 4/5 law' },
        range: [0, ymax],
        showgrid: true,
        gridcolor: 'lightgrey',
        gridwidth: 1,
        zeroline: false,
        griddash: 'dot'
      },
      showlegend: false
    };

    Plotly.react('plot-container', traces, layout, {responsive: true});
  }

  // Attach events
  function bindEvents() {
    const ids = ['slider-bmax','slider-b0','slider-r','slider-beta'];
    ids.forEach(id => {
      document.getElementById(id).addEventListener('input', update);
    });
  }

  // Initialize
  function init() {
    populateTable();
    bindEvents();
    // Typeset MathJax once labels are present
    if (window.MathJax && window.MathJax.typesetPromise) {
      MathJax.typesetPromise().then(update);
    } else {
      update();
    }
  }

  // Run when DOM is ready
  if (document.readyState !== 'loading') {
    init();
  } else {
    document.addEventListener('DOMContentLoaded', init);
  }
})();
</script>
</body>
</html>
