  <script src="https://unpkg.com/three@0.158.0/build/three.min.js"></script>

  <script>
    // DOM references
    const canvas = document.getElementById('main-canvas');
    const canvasContainer = document.getElementById('canvas-container');

    // Sliders and controls
    const sliderUnits    = document.getElementById('slider-units');
    const sliderAngle    = document.getElementById('slider-angle');
    const sliderConical  = document.getElementById('slider-conical');
    const sliderRotX     = document.getElementById('slider-rotate-x');
    const sliderRotZ     = document.getElementById('slider-rotate-z');

    const btnUnitsPlus   = document.getElementById('btn-units-plus');
    const btnAnglePlus   = document.getElementById('btn-angle-plus');
    const btnConicalPlus = document.getElementById('btn-conical-plus');
    const btnRotXPlus    = document.getElementById('btn-rotate-x-plus');
    const btnRotZPlus    = document.getElementById('btn-rotate-z-plus');

    const radios = Array.from(document.querySelectorAll('input[name="base-polygon"]'));

    // Three.js setup
    const renderer = new THREE.WebGLRenderer({ canvas, antialias: true, alpha: false });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
    renderer.setClearColor(0xffffff, 1);
    renderer.outputColorSpace = THREE.SRGBColorSpace;

    const scene = new THREE.Scene();

    // Camera
    const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 2000);
    camera.position.set(0, -250, 220);
    camera.up.set(0, 0, 1); // make Z up for a more natural "cone" sense
    camera.lookAt(0, 0, 60);

    // Lighting
    const ambient = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambient);

    const dirLight = new THREE.DirectionalLight(0xffffff, 0.85);
    dirLight.position.set(150, -120, 200);
    scene.add(dirLight);

    // Group for all segments
    const pineGroup = new THREE.Group();
    scene.add(pineGroup);

    // Base geometry holder (for cleanup)
    let currentBaseGeometry = null;

    // Utility: degrees to radians
    const deg2rad = (deg) => deg * Math.PI / 180;

    // Create a regular n-gon as a THREE.Shape centered at origin
    function createRegularPolygonShape(n, radius) {
      const shape = new THREE.Shape();
      for (let k = 0; k < n; k++) {
        const a = (k / n) * Math.PI * 2;
        const x = radius * Math.cos(a);
        const y = radius * Math.sin(a);
        if (k === 0) shape.moveTo(x, y);
        else shape.lineTo(x, y);
      }
      shape.closePath();
      return shape;
    }

    // Clear group children and dispose old materials; dispose base geometry once
    function clearPineGroup() {
      for (let i = pineGroup.children.length - 1; i >= 0; i--) {
        const child = pineGroup.children[i];
        pineGroup.remove(child);
        if (child.material) {
          if (Array.isArray(child.material)) {
            child.material.forEach(m => m && m.dispose && m.dispose());
          } else {
            child.material.dispose && child.material.dispose();
          }
        }
        // child.geometry is shared, do not dispose per-mesh
      }
      if (currentBaseGeometry) {
        currentBaseGeometry.dispose();
        currentBaseGeometry = null;
      }
    }

    // Read selected polygon sides
    function getSelectedSides() {
      const checked = document.querySelector('input[name="base-polygon"]:checked');
      return checked ? parseInt(checked.value, 10) : 7;
    }

    // Main redraw function
    function redraw() {
      const nSides = getSelectedSides();
      const units = Math.max(10, Math.min(500, Math.round(parseFloat(sliderUnits.value))));
      const angleDeg = parseFloat(sliderAngle.value);
      const conical = parseFloat(sliderConical.value);
      const rotXdeg = parseFloat(sliderRotX.value);
      const rotZdeg = parseFloat(sliderRotZ.value);

      const angularAdvanceRad = deg2rad(angleDeg);

      // Clear existing meshes
      clearPineGroup();

      // Base 2D polygon geometry (small initial radius)
      const baseRadius = 10;
      const baseShape = createRegularPolygonShape(nSides, baseRadius);
      currentBaseGeometry = new THREE.ShapeGeometry(baseShape);
      currentBaseGeometry.computeVertexNormals();

      // Build segments
      for (let i = 0; i < units; i++) {
        const angle = i * angularAdvanceRad;
        const radius = 4 * Math.sqrt(i);           // spread factor
        const scale = 0.5 * Math.log(i + 2);       // size factor (starts > 0)

        const x = radius * Math.cos(angle);
        const y = radius * Math.sin(angle);
        const z = conical * radius;

        const material = new THREE.MeshStandardMaterial({
          color: 0xffffff,
          roughness: 0.55,
          metalness: 0.0,
          side: THREE.DoubleSide
        });
        // Rainbow by angle
        const hue = ((angle / (2 * Math.PI)) % 1 + 1) % 1; // ensure [0,1)
        material.color.setHSL(hue, 0.8, 0.6);

        const mesh = new THREE.Mesh(currentBaseGeometry, material);
        mesh.position.set(x, y, z);
        mesh.scale.setScalar(scale);
        mesh.rotation.z = angle; // align tangent

        pineGroup.add(mesh);
      }

      // Master rotations
      pineGroup.rotation.set(0, 0, 0);
      pineGroup.rotation.x = deg2rad(rotXdeg);
      pineGroup.rotation.z = deg2rad(rotZdeg);

      renderOnce();
    }

    // Resize handling
    function resizeRenderer() {
      const width = canvasContainer.clientWidth;
      const height = canvasContainer.clientHeight;
      if (width === 0 || height === 0) return;
      renderer.setSize(width, height, false);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    }

    function renderOnce() {
      resizeRenderer();
      renderer.render(scene, camera);
    }

    // Event wiring
    function addEvents() {
      // Radio buttons
      radios.forEach(r => r.addEventListener('change', redraw));

      // Sliders
      sliderUnits.addEventListener('input', redraw);
      sliderAngle.addEventListener('input', redraw);
      sliderConical.addEventListener('input', redraw);
      sliderRotX.addEventListener('input', redraw);
      sliderRotZ.addEventListener('input', redraw);

      // Plus buttons
      function attachPlus(btn, slider) {
        btn.addEventListener('click', () => {
          const step = parseFloat(slider.step || '1');
          const min = parseFloat(slider.min || Number.NEGATIVE_INFINITY);
          const max = parseFloat(slider.max || Number.POSITIVE_INFINITY);
          let val = parseFloat(slider.value);
          // increment by one step
          val = Math.min(max, Math.max(min, val + step));
          // Fix floating point artifacts
          const decimals = (slider.step.includes('.') ? slider.step.split('.')[1].length : 0);
          slider.value = val.toFixed(decimals);
          redraw();
        });
      }
      attachPlus(btnUnitsPlus, sliderUnits);
      attachPlus(btnAnglePlus, sliderAngle);
      attachPlus(btnConicalPlus, sliderConical);
      attachPlus(btnRotXPlus, sliderRotX);
      attachPlus(btnRotZPlus, sliderRotZ);

      window.addEventListener('resize', renderOnce);
      window.addEventListener('orientationchange', () => {
        setTimeout(renderOnce, 150);
      });
    }

    // Initialize
    addEvents();
    redraw(); // initial render
  </script>
</body>
</html>
