  <script src="https://unpkg.com/three@0.150.1/build/three.min.js"></script>
  <script src="https://unpkg.com/three@0.150.1/examples/js/controls/OrbitControls.js"></script>

  <script>
    // DOM references
    const btnSC  = document.getElementById('btn-sc');
    const btnBCC = document.getElementById('btn-bcc');
    const btnFCC = document.getElementById('btn-fcc');
    const inputH = document.getElementById('input-h');
    const inputK = document.getElementById('input-k');
    const inputL = document.getElementById('input-l');
    const sliderOpacity = document.getElementById('slider-opacity');
    const canvasContainer = document.getElementById('canvas-container');

    // Three.js basics
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xEAEFF5);

    const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 100);
    camera.position.set(2.2, 1.7, 2.2);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
    renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
    canvasContainer.appendChild(renderer.domElement);

    const controls = new THREE.OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.07;
    controls.target.set(0,0,0);
    controls.update();

    // Lighting
    const ambient = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambient);
    const dir1 = new THREE.DirectionalLight(0xffffff, 0.55);
    dir1.position.set(2, 3, 1);
    scene.add(dir1);
    const dir2 = new THREE.DirectionalLight(0xffffff, 0.35);
    dir2.position.set(-2, -1, -1);
    scene.add(dir2);

    // Unit cell wireframe
    const boxGeo = new THREE.BoxGeometry(1,1,1);
    const edges = new THREE.EdgesGeometry(boxGeo);
    const lineMat = new THREE.LineBasicMaterial({ color: 0x222222, linewidth: 1 });
    const wireframe = new THREE.LineSegments(edges, lineMat);
    scene.add(wireframe);

    // Axes (ArrowHelpers)
    const origin = new THREE.Vector3(0,0,0);
    const axisLen = 0.8, headLen = 0.08, headWidth = 0.04;
    const xArrow = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), origin, axisLen, 0xff0000, headLen, headWidth);
    const yArrow = new THREE.ArrowHelper(new THREE.Vector3(0,1,0), origin, axisLen, 0x00aa00, headLen, headWidth);
    const zArrow = new THREE.ArrowHelper(new THREE.Vector3(0,0,1), origin, axisLen, 0x0066ff, headLen, headWidth);
    scene.add(xArrow, yArrow, zArrow);

    // Groups for easy clearing/redrawing
    const atomsGroup  = new THREE.Group();
    const planesGroup = new THREE.Group();
    scene.add(atomsGroup);
    scene.add(planesGroup);

    // State
    let currentLattice = 'SC';

    // Common geometries/materials
    const atomGeo = new THREE.SphereGeometry(0.05, 24, 16);
    const atomMat = new THREE.MeshPhongMaterial({
      color: 0x253045,
      shininess: 60,
      specular: 0x66c2c2
    });

    // Cube corners and edges for intersection calculations
    const corners = [
      new THREE.Vector3(-0.5,-0.5,-0.5),
      new THREE.Vector3( 0.5,-0.5,-0.5),
      new THREE.Vector3( 0.5, 0.5,-0.5),
      new THREE.Vector3(-0.5, 0.5,-0.5),
      new THREE.Vector3(-0.5,-0.5, 0.5),
      new THREE.Vector3( 0.5,-0.5, 0.5),
      new THREE.Vector3( 0.5, 0.5, 0.5),
      new THREE.Vector3(-0.5, 0.5, 0.5)
    ];
    const edgesIdx = [
      [0,1],[1,2],[2,3],[3,0], // bottom
      [4,5],[5,6],[6,7],[7,4], // top
      [0,4],[1,5],[2,6],[3,7]  // vertical
    ];

    // Utilities
    function setActiveButton(btn) {
      [btnSC, btnBCC, btnFCC].forEach(b => b.classList.remove('active'));
      btn.classList.add('active');
    }

    function gcd2(a, b) {
      a = Math.abs(a); b = Math.abs(b);
      while (b) { const t = b; b = a % b; a = t; }
      return a;
    }
    function gcd3(a, b, c) {
      return gcd2(gcd2(a, b), c);
    }

    function clearGroup(group) {
      for (let i = group.children.length - 1; i >= 0; i--) {
        const obj = group.children[i];
        group.remove(obj);
        if (obj.geometry) obj.geometry.dispose?.();
        if (obj.material) {
          if (Array.isArray(obj.material)) obj.material.forEach(m => m.dispose?.());
          else obj.material.dispose?.();
        }
      }
    }

    function addAtomAt(v) {
      const mesh = new THREE.Mesh(atomGeo, atomMat);
      mesh.position.copy(v);
      atomsGroup.add(mesh);
    }

    function drawAtomsForLattice(type) {
      const r = 0.5;
      const cornerPositions = [
        new THREE.Vector3(-r,-r,-r),
        new THREE.Vector3( r,-r,-r),
        new THREE.Vector3( r, r,-r),
        new THREE.Vector3(-r, r,-r),
        new THREE.Vector3(-r,-r, r),
        new THREE.Vector3( r,-r, r),
        new THREE.Vector3( r, r, r),
        new THREE.Vector3(-r, r, r)
      ];
      // Corners always present
      cornerPositions.forEach(addAtomAt);

      if (type === 'BCC') {
        addAtomAt(new THREE.Vector3(0,0,0));
      }
      if (type === 'FCC') {
        const f = 0.5;
        const faces = [
          new THREE.Vector3( f, 0, 0),
          new THREE.Vector3(-f, 0, 0),
          new THREE.Vector3( 0, f, 0),
          new THREE.Vector3( 0,-f, 0),
          new THREE.Vector3( 0, 0, f),
          new THREE.Vector3( 0, 0,-f),
        ];
        faces.forEach(addAtomAt);
      }
    }

    function computeIntersectionsForPlane(h, k, l, nConst) {
      // Plane: h*x + k*y + l*z = nConst
      const EPS = 1e-8;
      const pts = [];
      const keySet = new Set();

      for (const [i0, i1] of edgesIdx) {
        const p0 = corners[i0];
        const p1 = corners[i1];

        const s0 = h * p0.x + k * p0.y + l * p0.z;
        const s1 = h * p1.x + k * p1.y + l * p1.z;
        const denom = s1 - s0;

        if (Math.abs(denom) < EPS) {
          // Edge is parallel to plane; if it's coplanar, add both endpoints
          if (Math.abs(nConst - s0) < 1e-6) {
            const key0 = `${p0.x.toFixed(6)},${p0.y.toFixed(6)},${p0.z.toFixed(6)}`;
            if (!keySet.has(key0)) { pts.push(p0.clone()); keySet.add(key0); }
            const key1 = `${p1.x.toFixed(6)},${p1.y.toFixed(6)},${p1.z.toFixed(6)}`;
            if (!keySet.has(key1)) { pts.push(p1.clone()); keySet.add(key1); }
          }
          continue;
        }

        const t = (nConst - s0) / denom;
        if (t >= -1e-6 && t <= 1+1e-6) {
          const inter = new THREE.Vector3().copy(p0).addScaledVector(new THREE.Vector3().subVectors(p1, p0), t);
          // Clamp tiny numerical drift
          inter.x = Math.max(-0.5, Math.min(0.5, inter.x));
          inter.y = Math.max(-0.5, Math.min(0.5, inter.y));
          inter.z = Math.max(-0.5, Math.min(0.5, inter.z));
          const key = `${inter.x.toFixed(6)},${inter.y.toFixed(6)},${inter.z.toFixed(6)}`;
          if (!keySet.has(key)) {
            pts.push(inter);
            keySet.add(key);
          }
        }
      }
      return pts;
    }

    function createPlanePolygonMesh(points, h, k, l, opacity) {
      // points: array of Vector3 on the plane
      if (points.length < 3) return null;

      // Compute plane normal and centroid
      const n = new THREE.Vector3(h, k, l).normalize();
      const centroid = new THREE.Vector3(0,0,0);
      points.forEach(p => centroid.add(p));
      centroid.multiplyScalar(1 / points.length);

      // Local basis (u, v) on the plane
      const ref = (Math.abs(n.x) < 0.9) ? new THREE.Vector3(1,0,0) : new THREE.Vector3(0,1,0);
      const u = new THREE.Vector3().crossVectors(n, ref).normalize();
      const v = new THREE.Vector3().crossVectors(n, u).normalize();

      // Sort points angularly around centroid in (u,v) coordinates
      const pts2D = points.map(p => {
        const d = new THREE.Vector3().subVectors(p, centroid);
        return { x: d.dot(u), y: d.dot(v) };
      });
      const withAngles = pts2D.map((p, i) => ({ i, angle: Math.atan2(p.y, p.x) }));
      withAngles.sort((a, b) => a.angle - b.angle);
      const sorted2D = withAngles.map(a => pts2D[a.i]);

      // Build 2D shape at z=0 centered at origin; then rotate/translate
      const shape = new THREE.Shape();
      shape.moveTo(sorted2D[0].x, sorted2D[0].y);
      for (let i = 1; i < sorted2D.length; i++) shape.lineTo(sorted2D[i].x, sorted2D[i].y);
      shape.closePath();

      const geom = new THREE.ShapeGeometry(shape);
      const mat = new THREE.MeshBasicMaterial({
        color: 0x2c6df3,
        transparent: true,
        opacity: opacity,
        side: THREE.DoubleSide
      });
      const mesh = new THREE.Mesh(geom, mat);

      // Orient XY plane (local) to (u,v) in world and translate to centroid
      const basis = new THREE.Matrix4().makeBasis(u, v, n);
      mesh.setRotationFromMatrix(basis);
      mesh.position.copy(centroid);

      return mesh;
    }

    function drawPlanes(h, k, l, opacity) {
      // If all zero, nothing to draw
      if (h === 0 && k === 0 && l === 0) return;

      // Reduce (h,k,l) to simplest integers
      const g = gcd3(h, k, l) || 1;
      const rh = h / g, rk = k / g, rl = l / g;

      // Determine n range using min/max over cube corners
      let minS = Infinity, maxS = -Infinity;
      for (const c of corners) {
        const s = rh * c.x + rk * c.y + rl * c.z;
        if (s < minS) minS = s;
        if (s > maxS) maxS = s;
      }
      const nStart = Math.floor(minS);
      const nEnd   = Math.ceil(maxS);

      for (let n = nStart; n <= nEnd; n++) {
        const pts = computeIntersectionsForPlane(rh, rk, rl, n);
        if (pts.length >= 3) {
          const mesh = createPlanePolygonMesh(pts, rh, rk, rl, opacity);
          if (mesh) planesGroup.add(mesh);
        }
      }
    }

    function updateScene() {
      clearGroup(atomsGroup);
      clearGroup(planesGroup);

      // Read state
      const h = parseInt(inputH.value || '0', 10);
      const k = parseInt(inputK.value || '0', 10);
      const l = parseInt(inputL.value || '0', 10);
      const opacity = parseFloat(sliderOpacity.value);

      // Draw atoms
      drawAtomsForLattice(currentLattice);

      // Draw planes
      drawPlanes(h, k, l, opacity);
    }

    // Event wiring
    btnSC.addEventListener('click', () => { currentLattice = 'SC'; setActiveButton(btnSC); updateScene(); });
    btnBCC.addEventListener('click', () => { currentLattice = 'BCC'; setActiveButton(btnBCC); updateScene(); });
    btnFCC.addEventListener('click', () => { currentLattice = 'FCC'; setActiveButton(btnFCC); updateScene(); });

    [inputH, inputK, inputL].forEach(inp => {
      inp.addEventListener('change', () => {
        // Enforce integer within [-10,10]
        let v = Math.round(parseFloat(inp.value));
        v = Math.max(-10, Math.min(10, isNaN(v) ? 0 : v));
        inp.value = v.toString();
        updateScene();
      });
    });

    sliderOpacity.addEventListener('input', () => {
      const opacity = parseFloat(sliderOpacity.value);
      // Update opacity on existing plane meshes only
      planesGroup.traverse(obj => {
        if (obj.isMesh && obj.material) {
          if (Array.isArray(obj.material)) obj.material.forEach(m => { m.opacity = opacity; m.transparent = true; m.needsUpdate = true; });
          else { obj.material.opacity = opacity; obj.material.transparent = true; obj.material.needsUpdate = true; }
        }
      });
    });

    // Resize handling
    function onResize() {
      const w = canvasContainer.clientWidth;
      const h = canvasContainer.clientHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }
    window.addEventListener('resize', onResize);

    // Initial draw
    updateScene();
    onResize();

    // Animation loop
    function animate() {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    }
    animate();
  </script>
</body>
</html>
