      };

      const visualsDiv = document.getElementById('visuals');
      const orbitPlaceholder = document.getElementById('orbit-canvas');
      const plotPlaceholder = document.getElementById('plot-canvas');

      const state = {
        epsilonDeg: parseFloat(sliders.angle.value),
        omegaDeg: parseFloat(sliders.longitude.value),
        e: parseFloat(sliders.eccentricity.value)
      };

      function deg2rad(d) { return d * Math.PI / 180; }
      function rad2deg(r) { return r * 180 / Math.PI; }

      function formatStateLabels() {
        values.angle.textContent = state.epsilonDeg.toFixed(2);
        values.longitude.textContent = state.omegaDeg.toFixed(2);
        values.eccentricity.textContent = state.e.toFixed(6);
      }
      formatStateLabels();

      let orbitP5 = null;
      let plotP5 = null;

      function requestRedraw() {
        if (orbitP5) orbitP5.redraw();
        if (plotP5) plotP5.redraw();
      }

      // Bind sliders
      sliders.angle.addEventListener('input', () => {
        state.epsilonDeg = parseFloat(sliders.angle.value);
        values.angle.textContent = state.epsilonDeg.toFixed(2);
        requestRedraw();
      });
      sliders.longitude.addEventListener('input', () => {
        state.omegaDeg = parseFloat(sliders.longitude.value);
        values.longitude.textContent = state.omegaDeg.toFixed(2);
        requestRedraw();
      });
      sliders.eccentricity.addEventListener('input', () => {
        state.e = parseFloat(sliders.eccentricity.value);
        values.eccentricity.textContent = state.e.toFixed(6);
        requestRedraw();
      });

      // Orbit Sketch p5 instance
      const orbitSketch = (p) => {
        let canvas;
        const desiredHeight = () => {
          const w = visualsDiv.clientWidth || window.innerWidth;
          // Keep a pleasant aspect (wide but not too short)
          return Math.max(220, Math.min(380, Math.round(w * 0.35)));
        };

        p.setup = function() {
          const w = visualsDiv.clientWidth || window.innerWidth;
          canvas = p.createCanvas(w, desiredHeight());
          // Replace placeholder with this canvas and assign the required ID
          if (orbitPlaceholder && orbitPlaceholder.parentNode) {
            orbitPlaceholder.remove();
          }
          canvas.id('orbit-canvas');
          canvas.parent(visualsDiv);
          p.noLoop();
        };

        p.windowResized = function() {
          const w = visualsDiv.clientWidth || window.innerWidth;
          p.resizeCanvas(w, desiredHeight());
          p.redraw();
        };

        p.draw = function() {
          p.push();
          // Background per spec
          p.background('#f0f0f0');

          // Drawing center & ellipse
          const w = p.width;
          const h = p.height;
          p.translate(w / 2, h / 2);

          // Semi-major and semi-minor axes
          const a = Math.min(w, h) * 0.4;            // 40% of the smaller canvas dimension
          const e = Math.min(Math.max(state.e, 0), 0.99);
          const b = a * Math.sqrt(1 - e * e);

          // Rotate by longitude of periapsis (omega)
          const omega = deg2rad(state.omegaDeg);
          p.rotate(omega);

          // Draw ellipse
          p.noFill();
          p.stroke('#a0522d');
          p.strokeWeight(3);
          p.ellipse(0, 0, 2 * a, 2 * b);

          // Optional: draw center point and major/minor axes as subtle guides
          p.stroke('#c6c6c6');
          p.strokeWeight(1);
          p.line(-a * 1.1, 0, a * 1.1, 0);
          p.line(0, -b * 1.1, 0, b * 1.1);

          p.pop();
        };
      };

      // Equation of Time Plot p5 instance
      const plotSketch = (p) => {
        let canvas;
        const desiredHeight = () => {
          const w = visualsDiv.clientWidth || window.innerWidth;
          return Math.max(260, Math.min(480, Math.round(w * 0.45)));
        };

        // Utility: nice number for tick steps
        function niceNumber(range, round) {
          // Based on "Graphics Gems" style tick calculation
          const exponent = Math.floor(Math.log10(range));
          const fraction = range / Math.pow(10, exponent);
          let niceFraction;
          if (round) {
            if (fraction < 1.5) niceFraction = 1;
            else if (fraction < 3) niceFraction = 2;
            else if (fraction < 7) niceFraction = 5;
            else niceFraction = 10;
          } else {
            if (fraction <= 1) niceFraction = 1;
            else if (fraction <= 2) niceFraction = 2;
            else if (fraction <= 5) niceFraction = 5;
            else niceFraction = 10;
          }
          return niceFraction * Math.pow(10, exponent);
        }

        function computeEOTPoints(N) {
          const eps = deg2rad(state.epsilonDeg);
          const e = Math.min(Math.max(state.e, 0), 0.99);
          const omega = deg2rad(state.omegaDeg);
          const twoPi = Math.PI * 2;
          const minutesPerRad = 720 / Math.PI;

          const xs = [];
          const ys = [];

          let prevUnwrapped = null;
          for (let i = 0; i <= N; i++) {
            const M = (i / N) * twoPi;

            // Solve Kepler: M = E - e*sin(E)
            let E = M; // initial guess
            for (let k = 0; k < 8; k++) {
              const f = E - e * Math.sin(E) - M;
              const fprime = 1 - e * Math.cos(E);
              const delta = f / (Math.abs(fprime) < 1e-8 ? 1e-8 : fprime);
              E -= delta;
              if (Math.abs(delta) < 1e-12) break;
            }

            // True anomaly nu
            const sinE2 = Math.sin(E / 2);
            const cosE2 = Math.cos(E / 2);
            const nu = 2 * Math.atan2(Math.sqrt(1 + e) * sinE2, Math.sqrt(1 - e) * cosE2);

            // True longitude lambda
            let lambda = nu + omega;

            // Right ascension alpha = atan2(cos(eps)*sin(lambda), cos(lambda))
            const y = Math.cos(eps) * Math.sin(lambda);
            const x = Math.cos(lambda);
            let alpha = Math.atan2(y, x);

            // Equation of Time in radians (raw)
            let eotRad = M - alpha;

            // Phase unwrapping to keep continuity
            if (prevUnwrapped !== null) {
              let diff = eotRad - prevUnwrapped;
              if (diff > Math.PI) eotRad -= twoPi;
              else if (diff < -Math.PI) eotRad += twoPi;
            }
            prevUnwrapped = eotRad;

            const eotMin = eotRad * minutesPerRad;

            xs.push(M);
            ys.push(eotMin);
          }

          return { xs, ys };
        }

        p.setup = function() {
          const w = visualsDiv.clientWidth || window.innerWidth;
          canvas = p.createCanvas(w, desiredHeight());
          // Replace placeholder and assign ID
          if (plotPlaceholder && plotPlaceholder.parentNode) {
            plotPlaceholder.remove();
          }
          canvas.id('plot-canvas');
          canvas.parent(visualsDiv);
          p.noLoop();
        };

        p.windowResized = function() {
          const w = visualsDiv.clientWidth || window.innerWidth;
          p.resizeCanvas(w, desiredHeight());
          p.redraw();
        };

        p.draw = function() {
          const w = p.width;
          const h = p.height;
          p.background('#ffffff');

          // Compute data
          const N = 720;
          const { xs, ys } = computeEOTPoints(N);

          // Determine Y range with padding
          let yMin = Math.min(...ys);
          let yMax = Math.max(...ys);
          if (!isFinite(yMin) || !isFinite(yMax)) {
            yMin = -5; yMax = 5;
          }
          if (Math.abs(yMax - yMin) < 1e-9) {
            yMin -= 1;
            yMax += 1;
          }
          const range = yMax - yMin;
          const pad = 0.1 * range || 1.0;
          yMin -= pad;
          yMax += pad;

          // Plot area margins
          const margin = { left: 70, right: 20, top: 18, bottom: 44 };
          const plotL = margin.left;
          const plotR = w - margin.right;
          const plotT = margin.top;
          const plotB = h - margin.bottom;

          // Map functions
          const twoPi = Math.PI * 2;
          const xToPx = (x) => plotL + (x / twoPi) * (plotR - plotL);
          const yToPx = (y) => plotB - ((y - yMin) / (yMax - yMin)) * (plotB - plotT);

          // Draw grid (vertical at x=1..6)
          p.strokeWeight(1);
          p.textSize(12);
          p.noFill();

          // Major vertical grid lines and ticks/labels
          p.stroke('#e6e6e6');
          for (let i = 1; i <= 6; i++) {
            const x = xToPx(i);
            p.line(x, plotT, x, plotB);
          }

          // Y ticks using "nice" numbers
          const targetTicks = 6;
          const rawStep = (yMax - yMin) / targetTicks;
          const step = niceNumber(rawStep, true);
          const yStart = Math.ceil(yMin / step) * step;
          p.stroke('#e6e6e6');
          for (let yv = yStart; yv <= yMax + 1e-9; yv += step) {
            const py = yToPx(yv);
            p.line(plotL, py, plotR, py);
          }

          // Axes
          p.stroke('#000000');
          p.strokeWeight(1.2);
          // X axis (baseline)
          p.line(plotL, plotB, plotR, plotB);
          // Y axis (left)
          p.line(plotL, plotT, plotL, plotB);

          // X tick marks and labels (1..6)
          p.fill('#000');
          p.noStroke();
          p.textAlign(p.CENTER, p.TOP);
          for (let i = 1; i <= 6; i++) {
            const px = xToPx(i);
            // Tick mark
            p.stroke('#000');
            p.line(px, plotB, px, plotB + 6);
            p.noStroke();
            p.text(i.toString(), px, plotB + 8);
          }

          // Y tick marks and labels
          p.textAlign(p.RIGHT, p.CENTER);
          p.fill('#000');
          p.noStroke();
          for (let yv = yStart; yv <= yMax + 1e-9; yv += step) {
            const py = yToPx(yv);
            // Tick
            p.stroke('#000');
            p.line(plotL - 6, py, plotL, py);
            p.noStroke();
            const label = Math.abs(yv) < 1e-9 ? '0' : (Math.abs(yv) < 1 ? yv.toFixed(2) : yv.toFixed(1));
            p.text(label, plotL - 8, py);
          }

          // Y-axis label "minutes"
          p.push();
          p.translate(16, (plotT + plotB) / 2);
          p.rotate(-Math.PI / 2);
          p.textAlign(p.CENTER, p.CENTER);
          p.fill('#000');
          p.text('minutes', 0, 0);
          p.pop();

          // Plot curve
          p.stroke(varAccent());
          p.strokeWeight(2);
          p.noFill();

          // Draw with segment breaks if vertical jump is too large
          const jumpThresh = (yMax - yMin) * 0.8;
          let prevX = null, prevY = null, prevVal = null;
          for (let i = 0; i < xs.length; i++) {
            const px = xToPx(xs[i]);
            const py = yToPx(ys[i]);
            if (prevX !== null && prevY !== null) {
              const dy = Math.abs(ys[i] - prevVal);
              if (dy < jumpThresh && isFinite(py) && isFinite(prevY)) {
                p.line(prevX, prevY, px, py);
              }
            }
            prevX = px;
            prevY = py;
            prevVal = ys[i];
          }

          // Helper: x-axis label
          p.noStroke();
          p.fill('#000');
          p.textAlign(p.RIGHT, p.TOP);
          p.text('M (radians)', plotR, plotB + 24);
        };

        function varAccent() {
          return '#ff8c00';
        }
      };

      // Initialize p5 instances
      orbitP5 = new p5(orbitSketch);
      plotP5 = new p5(plotSketch);
    })();
  </script>
</body>
</html>
