  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    (function() {
      // Utility for formatting slider values according to step granularity
      function formatByStep(value, step) {
        const s = step.toString();
        const decimals = s.indexOf('.') >= 0 ? (s.length - s.indexOf('.') - 1) : 0;
        return (+value).toFixed(decimals);
      }

      // Elements
      const sliderC = document.getElementById('slider-c');
      const spanC = document.getElementById('c-value');

      const sliderHC = document.getElementById('slider-h_C');
      const spanHC = document.getElementById('h_C-value');

      const sliderDelta = document.getElementById('slider-delta');
      const spanDelta = document.getElementById('delta-value');

      const sliderPlotRange = document.getElementById('slider-plot-range');
      const spanPlotRange = document.getElementById('plot-range-value');

      const checkboxVerification = document.getElementById('check-verification');

      function getSelectedStep() {
        const checked = document.querySelector('input[name="steps"]:checked');
        return checked ? parseInt(checked.value, 10) : 5;
      }

      // Update displayed values initially and on input
      function bindSlider(slider, span) {
        const update = () => { span.textContent = formatByStep(slider.value, slider.step); redrawScene(); };
        slider.addEventListener('input', update);
        update();
      }

      bindSlider(sliderC, spanC);
      bindSlider(sliderHC, spanHC);
      bindSlider(sliderDelta, spanDelta);
      bindSlider(sliderPlotRange, spanPlotRange);

      document.getElementById('steps-group').addEventListener('change', () => redrawScene());
      checkboxVerification.addEventListener('change', () => redrawScene());

      // p5 sketch (instance mode)
      let p5Instance = null;

      const sketch = (p) => {
        const baseScale = 50; // base pixels per unit
        let cnv;

        p.setup = function() {
          const parent = document.getElementById('viz-area');
          const rect = parent.getBoundingClientRect();
          cnv = p.createCanvas(rect.width, rect.height);
          // Replace the placeholder canvas to keep exactly one canvas element
          const placeholder = document.getElementById('plemelj-canvas');
          placeholder.replaceWith(cnv.elt);
          cnv.id('plemelj-canvas'); // preserve id

          p.noLoop();
          p.pixelDensity(1);
          redrawScene();
        };

        p.windowResized = function() {
          const parent = document.getElementById('viz-area');
          const rect = parent.getBoundingClientRect();
          p.resizeCanvas(rect.width, rect.height);
          redrawScene();
        };

        function getControls() {
          const c = parseFloat(sliderC.value);
          const hC = parseFloat(sliderHC.value);
          const delta = parseFloat(sliderDelta.value);
          const zoom = parseFloat(sliderPlotRange.value);
          const step = getSelectedStep();
          const verification = checkboxVerification.checked;
          return { c, hC, delta, zoom, step, verification };
        }

        function computeGeometry(c, h, delta) {
          // Points defined directly
          const F = { x: 0, y: 0 };
          const C = { x: 0, y: h };
          const D = { x: c/2, y: 0 };
          const K = { x: c, y: 0 };

          // Solve for M
          // Perpendicular bisector: c*x - 2*h*y + h^2 - c^2/4 = 0
          // => x = (2*h*y - h^2 + c^2/4)/c = p*y + q
          const pcoef = (2*h)/c;
          const qcoef = (c/4) - (h*h)/c;

          // S(y) = (1+p^2) y^2 + (2 p q - (3c/2) p) y + (q^2 - (3c/2) q + c^2/2)
          const a2 = 1 + pcoef*pcoef;
          const a1 = 2 * pcoef * qcoef - (3*c/2) * pcoef;
          const a0 = qcoef*qcoef - (3*c/2) * qcoef + (c*c)/2;

          const t = Math.tan(delta);

          // Quadratic for y: A y^2 + B y + Cc = 0
          const A = t * a2;
          const B = t * a1 - (c/2);
          const Cc = t * a0;

          let yM;
          const EPS = 1e-12;

          if (Math.abs(A) < 1e-12) {
            // Linear case: B*y + Cc = 0
            if (Math.abs(B) < EPS) {
              yM = 0; // fallback
            } else {
              yM = -Cc / B;
            }
          } else {
            let disc = B*B - 4*A*Cc;
            if (disc < 0) disc = 0;
            const sqrtDisc = Math.sqrt(disc);
            const y1 = (-B + sqrtDisc) / (2*A);
            const y2 = (-B - sqrtDisc) / (2*A);
            // Select the negative root (or the smaller if none negative)
            const candidates = [y1, y2].sort((a,b)=>a-b);
            const negatives = candidates.filter(v => v < 0);
            yM = negatives.length ? negatives[0] : candidates[0];
          }
          const xM = pcoef * yM + qcoef;
          const M = { x: xM, y: yM };

          const R = Math.hypot(M.x - C.x, M.y - C.y);

          // Intersect circle κ with λ (x-axis): solutions x = c/2 (D) and x = 2*xM - c/2
          const xB = 2*xM - c/2;
          const Bpt = { x: xB, y: 0 };

          // Point A such that |AB| = c along λ
          const Apt = { x: xB - c, y: 0 };

          // Verification E: D is midpoint of E and B => E = 2D - B
          const Ept = { x: (c - xB), y: 0 };

          return { F, C, D, K, M, R, B: Bpt, A: Apt, E: Ept };
        }

        function worldScale() {
          const { zoom } = getControls();
          return baseScale * zoom;
        }

        // Drawing helpers (work in "world coordinates" after transform)
        function setDash(dashLengthPx) {
          const s = worldScale();
          const dl = dashLengthPx / s;
          p.drawingContext.setLineDash([dl, dl]);
        }
        function clearDash() {
          p.drawingContext.setLineDash([]);
        }
        function strokePx(px) {
          const s = worldScale();
          p.strokeWeight(px / s);
        }
        function drawInfiniteBaseLine() {
          const s = worldScale();
          const xMaxWorld = p.width / (2*s);
          p.stroke(0);
          strokePx(1.2);
          clearDash();
          p.line(-xMaxWorld, 0, xMaxWorld, 0);
        }
        function drawSegment(a, b, dashed=false) {
          p.stroke(0);
          if (dashed) { setDash(6); } else { clearDash(); }
          strokePx(dashed ? 1.0 : 1.2);
          p.line(a.x, a.y, b.x, b.y);
          clearDash();
        }
        function drawCircle(center, radius, dashed=false) {
          p.noFill();
          p.stroke(0);
          if (dashed) { setDash(6); } else { clearDash(); }
          strokePx(1.0);
          p.ellipse(center.x, center.y, 2*radius, 2*radius);
          clearDash();
        }
        function drawPoint(pt, label, dx=6, dy=-10) {
          const s = worldScale();
          p.noStroke();
          p.fill(0);
          const r = 3 / s;
          p.ellipse(pt.x, pt.y, 2*r, 2*r);

          // Label (screen-space, unflipped)
          const sx = p.screenX(pt.x, pt.y);
          const sy = p.screenY(pt.x, pt.y);
          p.push();
          p.resetMatrix();
          p.fill(0);
          p.noStroke();
          p.textSize(12);
          p.text(label, sx + dx, sy + dy);
          p.pop();
        }
        function drawTriangle(a,b,c) {
          p.stroke(0);
          clearDash();
          strokePx(2.4);
          p.noFill();
          p.beginShape();
          p.vertex(a.x, a.y);
          p.vertex(b.x, b.y);
          p.vertex(c.x, c.y);
          p.endShape(p.CLOSE);
        }

        p.draw = function() {
          const { c, hC, delta, zoom, step, verification } = getControls();

          // Background
          p.background('#E0E0E0');

          // Compute geometry first
          const G = computeGeometry(c, hC, delta);
          const { F, C, D, K, M, R, B, A, E } = G;

          // Set up world coordinate system
          p.push();
          p.translate(p.width / 2, p.height * 0.6);
          p.scale(worldScale(), -worldScale()); // scale and flip Y up

          // Step 1: base line and basic points
          if (step >= 1) {
            drawInfiniteBaseLine();
            drawSegment(F, C, false); // FC
            drawPoint(F, 'F', 6, -8);
            drawPoint(C, 'C', 6, -8);
            drawPoint(D, 'D', 6, -8);
          }

          // Step 2: K, M, dashed helper lines, circle κ
          if (step >= 2) {
            drawPoint(K, 'K', 6, -8);

            // Dashed lines: CD, DK
            drawSegment(C, D, true);
            drawSegment(D, K, true);

            // M and dashed MC, MD, MK
            drawPoint(M, 'M', 6, -8);
            drawSegment(M, C, true);
            drawSegment(M, D, true);
            drawSegment(M, K, true);

            // Circle κ centered at M radius |MC|
            drawCircle(M, R, false);
          }

          // Step 3: point B
          if (step >= 3) {
            drawPoint(B, 'B', 6, -8);
          }

          // Step 4: point A
          if (step >= 4) {
            drawPoint(A, 'A', 6, -8);
          }

          // Step 5: final triangle ABC
          if (step >= 5) {
            drawTriangle(A, B, C);
          }

          // Verification geometry
          if (verification) {
            drawPoint(E, 'E', 6, -8);
            drawSegment(C, E, true);
            drawSegment(K, B, true);
          }

          p.pop();
        };
      };

      p5Instance = new p5(sketch);

      // Public redrawScene (called by UI events)
      window.redrawScene = function redrawScene() {
        if (p5Instance) p5Instance.redraw();
      };
    })();
  </script>
</body>
</html>
