<body>
  <div id="main-container">
    <div id="control-panel">
      <label for="slider-z">z (m)</label>
      <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0" />
      <span id="label-z">0.</span>
    </div>

    <div id="plots-container">
      <div id="plot-intensity"><div class="plot"></div></div>
      <div id="plot-population"><div class="plot"></div></div>
      <div id="plot-coherence"><div class="plot"></div></div>
    </div>
  </div>

  <script>
    // --- Constants and utilities ---
    const t_values = Array.from({length: 301}, (_, i) => i - 50); // -50 .. 250 ns
    const c_m_per_ns = 0.3; // speed of light approx in m/ns

    function formatZLabel(z) {
      // Slider label: show with a few decimals, but if integer, show with trailing decimal point like "32."
      const zi = Math.trunc(z);
      if (Math.abs(z - zi) < 1e-9) {
        return zi.toString() + '.';
      }
      return Number(z).toFixed(5);
    }

    function formatZLegend(z) {
      // Legend requirement: For integer, "32." else show e.g., "7.02"
      const zi = Math.trunc(z);
      if (Math.abs(z - zi) < 1e-9) {
        return `${zi}.`;
      }
      return Number(z).toFixed(2);
    }

    // --- Red (z=0) data generation (static) ---
    function intensityRed(t) {
      if (t < -15 || t > 170) return 0;
      if (t > 15) return 1;
      return 0.5 * (1 + Math.tanh(t / 4));
    }
    function populationRed(t) {
      if (t < -15) return 0;
      return 4.5 * (1 - Math.exp(-(t + 15) / 50));
    }
    function coherenceRed(t) {
      if (t < -15) return 0;
      return 2.2 * (1 - Math.exp(-(t + 15) / 50));
    }
    const I_red = t_values.map(intensityRed);
    const P_red = t_values.map(populationRed);
    const C_red = t_values.map(coherenceRed);

    // --- Blue (dynamic, depends on z) ---
    function computeBlueSeries(z) {
      const t_delay = z / c_m_per_ns;
      const tCut = 170 + t_delay;

      const I_blue = [];
      const P_blue = [];
      const C_blue = [];

      for (let i = 0; i < t_values.length; i++) {
        const t = t_values[i];
        const t_prime = t - t_delay;

        // Clip all series for times beyond tCut by setting to null
        const clip = (t > tCut);

        // Intensity
        let I_base;
        if (t_prime < -15) I_base = 0;
        else if (t_prime > 15) I_base = 1;
        else I_base = 0.5 * (1 + Math.tanh(t_prime / 4));
        const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
        const I_val = clip ? null : I_base * envelope;

        // Population
        const scaling_p = 1 - 0.45 * (z / 32);
        const P_base = (t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime + 15) / 50));
        const P_val = clip ? null : scaling_p * P_base;

        // Coherence
        const scaling_c = 1 - 0.45 * (z / 32);
        const C_base = (t_prime < -15) ? 0 : 2.2 * (1 - Math.exp(-(t_prime + 15) / 50));
        const C_val = clip ? null : scaling_c * C_base;

        I_blue.push(I_val);
        P_blue.push(P_val);
        C_blue.push(C_val);
      }
      return { I_blue, P_blue, C_blue, t_delay };
    }

    // --- Plotly setup helpers ---
    function makeShapes(t_delay_initial = 0) {
      return [
        // Fixed vertical dashed line at t=0
        {
          type: 'line',
          x0: 0, y0: 0,
          x1: 0, y1: 1,
          xref: 'x',
          yref: 'paper',
          line: { color: 'black', width: 2, dash: 'dash' }
        },
        // Moving vertical dashed line at t = t_delay
        {
          type: 'line',
          x0: t_delay_initial, y0: 0,
          x1: t_delay_initial, y1: 1,
          xref: 'x',
          yref: 'paper',
          line: { color: 'black', width: 2, dash: 'dash' }
        }
      ];
    }

    function makeCommonLayout(titleY, annotationTag) {
      return {
        xaxis: {
          title: 'time (ns)',
          range: [-50, 250],
          zeroline: true
        },
        yaxis: {
          title: titleY,
          zeroline: true
        },
        margin: { l: 80, r: 20, t: 40, b: 40 },
        showlegend: true,
        legend: { x: 0.7, y: 0.9 },
        shapes: makeShapes(0),
        annotations: [
          { x: -45, y: 0.9, xref: 'x', yref: 'paper', text: annotationTag, showarrow: false, font: { size: 14 } },
          { x: 0, y: 0.75, xref: 'x', yref: 'paper', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
        ]
      };
    }

    // Create traces for a given y arrays
    function makeTraces(yRed, yBlueInit, zInitLegendStr) {
      return [
        {
          x: t_values,
          y: yRed,
          name: 'z = 0 meters',
          mode: 'lines',
          line: { color: 'red', width: 2 }
        },
        {
          x: t_values,
          y: yBlueInit,
          name: `z = ${zInitLegendStr} meters`,
          mode: 'lines',
          line: { color: 'blue', width: 2, dash: 'dash' }
        }
      ];
    }

    // --- Initialize plots ---
    const config = { responsive: true, displaylogo: false };

    // Initial blue data at z=0 (identical to red due to definitions and clipping)
    const { I_blue: I_blue0, P_blue: P_blue0, C_blue: C_blue0 } = computeBlueSeries(0);
    const blueLegend0 = formatZLegend(0);

    const layoutIntensity = makeCommonLayout('intensity (arb)', '(a)');
    layoutIntensity.yaxis.range = [-0.1, 1.1];

    const layoutPopulation = makeCommonLayout('excited-state population × 10<sup>4</sup>', '(b)');
    layoutPopulation.yaxis.range = [-0.5, 5.5];

    const layoutCoherence = makeCommonLayout('coherence × 10<sup>2</sup>', '(c)');
    layoutCoherence.yaxis.range = [-0.2, 2.4];

    const plotDivIntensity = document.querySelector('#plot-intensity .plot');
    const plotDivPopulation = document.querySelector('#plot-population .plot');
    const plotDivCoherence = document.querySelector('#plot-coherence .plot');

    Plotly.newPlot(
      plotDivIntensity,
      makeTraces(I_red, I_blue0, blueLegend0),
      layoutIntensity,
      config
    );
    Plotly.newPlot(
      plotDivPopulation,
      makeTraces(P_red, P_blue0, blueLegend0),
      layoutPopulation,
      config
    );
    Plotly.newPlot(
      plotDivCoherence,
      makeTraces(C_red, C_blue0, blueLegend0),
      layoutCoherence,
      config
    );

    // --- Slider interaction ---
    const sliderZ = document.getElementById('slider-z');
    const labelZ = document.getElementById('label-z');

    function updatePlotsForZ(zValue) {
      const z = parseFloat(zValue);
      // Update label
      labelZ.textContent = formatZLabel(z);

      // Compute new blue series
      const { I_blue, P_blue, C_blue, t_delay } = computeBlueSeries(z);

      const blueName = `z = ${formatZLegend(z)} meters`;

      // Update traces (trace index 1 is blue)
      Plotly.restyle(plotDivIntensity, { y: [I_blue], name: [blueName] }, [1]);
      Plotly.restyle(plotDivPopulation, { y: [P_blue], name: [blueName] }, [1]);
      Plotly.restyle(plotDivCoherence, { y: [C_blue], name: [blueName] }, [1]);

      // Update moving vertical line position in all plots (shapes[1])
      const relayoutObj = {};
      relayoutObj['shapes[1].x0'] = t_delay;
      relayoutObj['shapes[1].x1'] = t_delay;

      Plotly.relayout(plotDivIntensity, relayoutObj);
      Plotly.relayout(plotDivPopulation, relayoutObj);
      Plotly.relayout(plotDivCoherence, relayoutObj);
    }

    // Initialize with z=0 ensuring initial legend and lines are consistent
    updatePlotsForZ(0);

    // Attach event
    sliderZ.addEventListener('input', (e) => {
      updatePlotsForZ(e.target.value);
    });
  </script>
</body>
</html>
