  <!-- CDN for Plotly.js -->
  <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
  <script>
    // Constants
    const C_A0 = 10;                // mmol/dm^3
    const R = 1.987;                // cal/mol/K
    const E_f = 25000;              // cal/mol
    const deltaH_rxn = -200000;     // cal/mol
    const E_r = E_f - deltaH_rxn;   // = 225000 cal/mol
    const k_f0 = 1e15;              // 1/s
    const rhoCp = 20000;            // cal/(dm^3 K)
    const negDeltaH = -deltaH_rxn;  // = +200000

    // Elements
    const elUA = document.getElementById('slider-ua');
    const elKr0 = document.getElementById('slider-kr0');
    const elTf = document.getElementById('slider-tf');
    const elTau = document.getElementById('slider-tau');

    const valUA = document.getElementById('ua-value');
    const valKr0 = document.getElementById('kr0-value');
    const valTf = document.getElementById('tf-value');
    const valTau = document.getElementById('tau-value');

    const plotDiv = document.getElementById('plot-container');

    function formatKr0Display(v) {
      // v is slider value in [0,10], actual pre-exponential = v x 1e12
      return `${Number(v).toFixed(1)} × 10¹²`;
    }

    function updateValueDisplays(UA, kr0Slider, Tf, tau) {
      valUA.textContent = UA.toFixed(1);
      valKr0.textContent = formatKr0Display(kr0Slider);
      valTf.textContent = Math.round(Tf).toString();
      valTau.textContent = Math.round(tau).toString();
    }

    function computeAndPlot() {
      const UA = parseFloat(elUA.value);
      const kr0Slider = parseFloat(elKr0.value);
      const Tf = parseFloat(elTf.value);
      const tau = parseFloat(elTau.value);

      updateValueDisplays(UA, kr0Slider, Tf, tau);

      const Tmin = 250, Tmax = 400, dT = 1;
      const T = [];
      const CB_mass = [];
      const CB_energy = [];

      for (let t = Tmin; t <= Tmax; t += dT) {
        T.push(t);
        const kf = k_f0 * Math.exp(-E_f / (R * t));
        const kr = (kr0Slider * 1e12) * Math.exp(-E_r / (R * t));
        const CBm = (tau * kf * C_A0) / (1 + tau * (kf + kr)); // mmol/dm^3
        CB_mass.push(CBm);

        const CB_e = ((rhoCp + UA * tau) * (t - Tf)) / (negDeltaH);
        CB_energy.push(CB_e);
      }

      // Find y values for annotations near specified T locations
      function yAtT(target, xArr, yArr) {
        // nearest index
        let idx = 0;
        let best = Infinity;
        for (let i = 0; i < xArr.length; i++) {
          const diff = Math.abs(xArr[i] - target);
          if (diff < best) { best = diff; idx = i; }
        }
        return yArr[idx];
      }
      const xMassAnno = 360;
      const yMassAnno = yAtT(xMassAnno, T, CB_mass);
      const xEnergyAnno = 280;
      const yEnergyAnno = yAtT(xEnergyAnno, T, CB_energy);

      const data = [
        {
          x: T,
          y: CB_mass,
          mode: 'lines',
          line: { color: '#008000', width: 3 },
          name: 'mass balance',
          hovertemplate: 'T=%{x:.0f} K<br>C_B=%{y:.2f} mmol/dm³<extra>Mass balance</extra>'
        },
        {
          x: T,
          y: CB_energy,
          mode: 'lines',
          line: { color: '#0000FF', width: 3 },
          name: 'energy balance',
          hovertemplate: 'T=%{x:.0f} K<br>C_B=%{y:.2f} mmol/dm³<extra>Energy balance</extra>'
        }
      ];

      const layout = {
        margin: { l: 70, r: 20, t: 18, b: 60 },
        plot_bgcolor: '#ffffff',
        paper_bgcolor: '#ffffff',
        xaxis: {
          title: 'temperature (K)',
          range: [250, 400],
          gridcolor: '#e6e6e6',
          zeroline: false,
          tickmode: 'auto'
        },
        yaxis: {
          title: 'product concentration (mmol/dm³)',
          range: [0, 12],
          gridcolor: '#e6e6e6',
          zeroline: false
        },
        showlegend: false,
        annotations: [
          {
            x: xMassAnno,
            y: yMassAnno,
            text: 'mass balance',
            showarrow: false,
            font: { color: '#008000', size: 13 },
            xanchor: 'left',
            yanchor: 'bottom',
            bgcolor: 'rgba(255,255,255,0.7)',
            bordercolor: '#008000',
            borderwidth: 0,
            align: 'left'
          },
          {
            x: xEnergyAnno,
            y: yEnergyAnno,
            text: 'energy balance',
            showarrow: false,
            font: { color: '#0000FF', size: 13 },
            xanchor: 'left',
            yanchor: 'bottom',
            bgcolor: 'rgba(255,255,255,0.7)',
            bordercolor: '#0000FF',
            borderwidth: 0,
            align: 'left'
          }
        ]
      };

      Plotly.react(plotDiv, data, layout, { responsive: true, displayModeBar: false });
    }

    // Initialize and bind events
    ['input'].forEach(evt => {
      elUA.addEventListener(evt, computeAndPlot);
      elKr0.addEventListener(evt, computeAndPlot);
      elTf.addEventListener(evt, computeAndPlot);
      elTau.addEventListener(evt, computeAndPlot);
    });

    // Initial plot
    computeAndPlot();

    // Handle resize
    window.addEventListener('resize', () => {
      Plotly.Plots.resize(plotDiv);
    });
  </script>
</body>
