            <label for="radio-fixed-centers">fixed centers</label>
        </div>
        <div>
            <span>envelope to show</span>
            <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
            <label for="radio-inner">inner</label>
            <input type="radio" name="envelope-type" id="radio-outer" value="outer">
            <label for="radio-outer">outer</label>
            <input type="radio" name="envelope-type" id="radio-both" value="both">
            <label for="radio-both">both</label>
        </div>
        <div>
            <label for="slider-rotation">rotation angle</label>
            <input type="range" id="slider-rotation">
            <span id="label-rotation" class="value-pill"></span>
        </div>
        <div class="footnote">
          Tip: Drag the rotation angle to animate. Change the reference frame to see the motion from different viewpoints.
        </div>
    </div>

    <!-- Visualization Canvas -->
    <div id="canvas-container">
        <!-- p5.js canvas will be inserted here -->
        <div class="legend">
          <div><span class="blue">●</span> epitrochoid (housing)</div>
          <div><span class="orange">●</span> envelope (rotor)</div>
        </div>
    </div>

</div>

<!-- CDN Libraries -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
<script>
  // DOM references
  const sliderEcc = document.getElementById('slider-eccentricity');
  const labelEcc  = document.getElementById('label-eccentricity');
  const sliderRot = document.getElementById('slider-rotation');
  const labelRot  = document.getElementById('label-rotation');

  const radioEpi  = document.getElementById('radio-epitrochoid');
  const radioEnv  = document.getElementById('radio-envelope');
  const radioFix  = document.getElementById('radio-fixed-centers');

  const radioInner = document.getElementById('radio-inner');
  const radioOuter = document.getElementById('radio-outer');
  const radioBoth  = document.getElementById('radio-both');

  // Initialize slider attributes as per spec
  sliderEcc.min   = "0.0";
  sliderEcc.max   = "0.3";
  sliderEcc.step  = "0.0001";
  sliderEcc.value = "0.14";

  sliderRot.min   = "0";
  sliderRot.max   = "6.28318";
  sliderRot.step  = "0.0001";
  sliderRot.value = "0";

  // State
  let state = {
    k: parseFloat(sliderEcc.value),
    phi: parseFloat(sliderRot.value),
    frame: 'epitrochoid',       // 'epitrochoid' | 'envelope' | 'fixed centers'
    envelopeType: 'inner',      // 'inner' | 'outer' | 'both'
    baseScale: 150              // pixels per unit, starting point
  };

  // Update labels
  function updateLabels() {
    labelEcc.textContent = Number(state.k).toFixed(4);
    labelRot.textContent = Number(state.phi).toFixed(3) + " rad";
  }
  updateLabels();

  // Event listeners
  sliderEcc.addEventListener('input', () => {
    state.k = parseFloat(sliderEcc.value);
    updateLabels();
  });
  sliderRot.addEventListener('input', () => {
    state.phi = parseFloat(sliderRot.value);
    updateLabels();
  });

  [radioEpi, radioEnv, radioFix].forEach(r => {
    r.addEventListener('change', () => {
      if (radioEpi.checked) state.frame = 'epitrochoid';
      else if (radioEnv.checked) state.frame = 'envelope';
      else state.frame = 'fixed centers';
    });
  });

  [radioInner, radioOuter, radioBoth].forEach(r => {
    r.addEventListener('change', () => {
      if (radioInner.checked) state.envelopeType = 'inner';
      else if (radioOuter.checked) state.envelopeType = 'outer';
      else state.envelopeType = 'both';
    });
  });

  // Geometry helpers
  function rotatePoint(p, ang) {
    const c = Math.cos(ang), s = Math.sin(ang);
    return [c*p[0] - s*p[1], s*p[0] + c*p[1]];
  }
  function add(p, q) { return [p[0]+q[0], p[1]+q[1]]; }
  function sub(p, q) { return [p[0]-q[0], p[1]-q[1]]; }

  // Parametric curves (canonical, centered at origin)
  // Blue epitrochoid (2-lobed): x = cos t - k cos 3t, y = sin t - k sin 3t
  function epitrochoidPoints(k, n=600, phase=0) {
    const pts = [];
    const twoPi = Math.PI * 2;
    for (let i = 0; i <= n; i++) {
      const t = (i / n) * twoPi;
      const tt = t + phase;
      const x = Math.cos(tt) - k * Math.cos(3 * tt);
      const y = Math.sin(tt) - k * Math.sin(3 * tt);
      pts.push([x, y]);
    }
    return pts;
  }

  // Rotor envelope shapes (inner/outer) canonical at origin.
  // Using provided formulation:
  // inner: x=(2/3)R cos t + e cos 2t, y=(2/3)R sin t - e sin 2t
  // outer: x=(2/3)R cos t - e cos 2t, y=(2/3)R sin t + e sin 2t
  function rotorPointsInner(e, R=1, n=600) {
    const pts = [];
    const twoPi = Math.PI * 2;
    for (let i = 0; i <= n; i++) {
      const t = (i / n) * twoPi;
      const x = (2/3)*R*Math.cos(t) + e*Math.cos(2*t);
      const y = (2/3)*R*Math.sin(t) - e*Math.sin(2*t);
      pts.push([x, y]);
    }
    return pts;
  }
  function rotorPointsOuter(e, R=1, n=600) {
    const pts = [];
    const twoPi = Math.PI * 2;
    for (let i = 0; i <= n; i++) {
      const t = (i / n) * twoPi;
      const x = (2/3)*R*Math.cos(t) - e*Math.cos(2*t);
      const y = (2/3)*R*Math.sin(t) + e*Math.sin(2*t);
      pts.push([x, y]);
    }
    return pts;
  }

  // Transform a set of points by rotation and translation
  function transformPoints(pts, rotAngle=0, translation=[0,0]) {
    const out = new Array(pts.length);
    const c = Math.cos(rotAngle), s = Math.sin(rotAngle);
    for (let i = 0; i < pts.length; i++) {
      const px = pts[i][0], py = pts[i][1];
      const rx = c*px - s*py;
      const ry = s*px + c*py;
      out[i] = [rx + translation[0], ry + translation[1]];
    }
    return out;
  }
  // Transform by inverse (x' = R(-a) (x - T))
  function transformInverse(pts, rotAngle=0, translation=[0,0]) {
    const out = new Array(pts.length);
    const c = Math.cos(-rotAngle), s = Math.sin(-rotAngle);
    for (let i = 0; i < pts.length; i++) {
      const px = pts[i][0] - translation[0];
      const py = pts[i][1] - translation[1];
      const rx = c*px - s*py;
      const ry = s*px + c*py;
      out[i] = [rx, ry];
    }
    return out;
  }

  // Compute scene points according to selected frame
  function buildScene(k, phi, frame, envelopeType) {
    // Motion relations
    const phi_shaft = 2 * phi;
    const theta = -phi_shaft / 3;                 // rotor rotation
    const C = [k * Math.cos(phi_shaft), k * Math.sin(phi_shaft)]; // rotor center translation
    const alpha = (frame === 'fixed centers') ? phi_shaft : 0;    // optional housing rotation

    // Canonical curves
    const blueCanonical = epitrochoidPoints(k, 600, 0);
    const envInnerCanonical = rotorPointsInner(k, 1, 600);
    const envOuterCanonical = rotorPointsOuter(k, 1, 600);

    // Apply frame logic
    let bluePts = [];
    let envInnerPts = null;
    let envOuterPts = null;

    if (frame === 'epitrochoid') {
      bluePts = blueCanonical; // stationary at origin
      if (envelopeType === 'inner' || envelopeType === 'both') {
        envInnerPts = transformPoints(envInnerCanonical, theta, C);
      }
      if (envelopeType === 'outer' || envelopeType === 'both') {
        envOuterPts = transformPoints(envOuterCanonical, theta, C);
      }
    } else if (frame === 'envelope') {
      // orange stationary at origin, blue seen in inverse motion
      if (envelopeType === 'inner' || envelopeType === 'both') {
        envInnerPts = envInnerCanonical;
      }
      if (envelopeType === 'outer' || envelopeType === 'both') {
        envOuterPts = envOuterCanonical;
      }
      bluePts = transformInverse(blueCanonical, theta, C);
    } else { // fixed centers
      // blue rotates about origin, orange does its forward motion
      bluePts = transformPoints(blueCanonical, alpha, [0,0]);
      if (envelopeType === 'inner' || envelopeType === 'both') {
        envInnerPts = transformPoints(envInnerCanonical, theta, C);
      }
      if (envelopeType === 'outer' || envelopeType === 'both') {
        envOuterPts = transformPoints(envOuterCanonical, theta, C);
      }
    }

    return { bluePts, envInnerPts, envOuterPts };
  }

  // Compute bounds
  function boundsOf(pointsArray) {
    let minX = Infinity, maxX = -Infinity, minY = Infinity, maxY = -Infinity;
    pointsArray.forEach(pts => {
      if (!pts || pts.length === 0) return;
      for (let i = 0; i < pts.length; i++) {
        const x = pts[i][0], y = pts[i][1];
        if (x < minX) minX = x;
        if (x > maxX) maxX = x;
        if (y < minY) minY = y;
        if (y > maxY) maxY = y;
      }
    });
    if (!isFinite(minX)) { minX = -1; maxX = 1; minY = -1; maxY = 1; }
    return { minX, maxX, minY, maxY };
  }

  // Nice tick computation
  function niceStep(range, targetTicks=8) {
    const step0 = range / Math.max(targetTicks,1);
    const pow10 = Math.pow(10, Math.floor(Math.log10(step0)));
    const candidates = [1, 2, 2.5, 5, 10];
    let best = candidates[0] * pow10;
    for (let c of candidates) {
      const s = c * pow10;
      if (Math.abs(step0 - s) < Math.abs(step0 - best)) best = s;
    }
    return best;
  }

  // p5 sketch
  let cnv, canvW = 100, canvH = 100;

  function setup() {
    const container = document.getElementById('canvas-container');
    resizeCanvasToContainer(container);
  }

  function windowResized() {
    const container = document.getElementById('canvas-container');
    resizeCanvasToContainer(container);
  }

  function resizeCanvasToContainer(container) {
    const w = container.clientWidth || 800;
    const h = Math.max(container.clientHeight, Math.floor(window.innerHeight*0.55));
    if (!cnv) {
      cnv = createCanvas(w, h);
      cnv.parent('canvas-container');
    } else {
      resizeCanvas(w, h);
    }
    canvW = w; canvH = h;
  }

  function drawAxes(scalePxPerUnit, bounds) {
    // Determine world range we show based on canvas size and scale
    const halfW = canvW / (2*scalePxPerUnit);
    const halfH = canvH / (2*scalePxPerUnit);
    const worldRange = Math.max(halfW, halfH);

    const minW = -worldRange, maxW = worldRange;

    // Helper to transform world->screen
    const cx = canvW/2, cy = canvH/2;
    const w2s = (x, y) => [cx + x*scalePxPerUnit, cy - y*scalePxPerUnit];

    stroke(150);
    strokeWeight(1);
    noFill();

    // Axes
    const axX1 = w2s(minW, 0), axX2 = w2s(maxW, 0);
    const axY1 = w2s(0, minW), axY2 = w2s(0, maxW);
    line(axX1[0], axX1[1], axX2[0], axX2[1]);
    line(axY1[0], axY1[1], axY2[0], axY2[1]);

    // Ticks
    const tickStep = niceStep(worldRange*2, 8);
    const tickPix = 6;

    textAlign(CENTER, TOP);
    textSize(12);
    fill(170);
    noStroke();
    // X ticks
    for (let x = Math.ceil(minW / tickStep) * tickStep; x <= maxW; x += tickStep) {
      const p = w2s(x, 0);
      stroke(150);
      strokeWeight(1);
      line(p[0], p[1]-tickPix, p[0], p[1]+tickPix);
      noStroke();
      if (Math.abs(x) > 1e-9) {
        text(x.toFixed(1), p[0], p[1]+tickPix+2);
      }
    }
    // Y ticks
    for (let y = Math.ceil(minW / tickStep) * tickStep; y <= maxW; y += tickStep) {
      const p = w2s(0, y);
      stroke(150);
      strokeWeight(1);
      line(p[0]-tickPix, p[1], p[0]+tickPix, p[1]);
      noStroke();
      if (Math.abs(y) > 1e-9) {
        // Align left of axis
        textAlign(RIGHT, CENTER);
        text(y.toFixed(1), p[0]-tickPix-4, p[1]-6); // slight upward shift for clarity
        textAlign(CENTER, TOP);
      }
    }
  }

  function drawCurve(points, strokeRGB, scalePxPerUnit) {
    if (!points || points.length < 2) return;
    stroke(strokeRGB[0], strokeRGB[1], strokeRGB[2]);
    strokeWeight(2);
    noFill();

    // Use world transform (translate center, scale, flip y)
    push();
    translate(width/2, height/2);
    scale(scalePxPerUnit, -scalePxPerUnit);

    beginShape();
    for (let i = 0; i < points.length; i++) {
      vertex(points[i][0], points[i][1]);
    }
    endShape();

    pop();
  }

  function computeScale(bounds) {
    const margin = 1.1;
    const maxAbsX = Math.max(Math.abs(bounds.minX), Math.abs(bounds.maxX)) * margin;
    const maxAbsY = Math.max(Math.abs(bounds.minY), Math.abs(bounds.maxY)) * margin;
    const maxR = Math.max(maxAbsX, maxAbsY, 1e-6);
    const fitScale = 0.45 * Math.min(canvW, canvH) / maxR; // ensure fit with some padding
    // Use baseScale as a starting point; do not exceed baseScale to avoid overly large scaling.
    return Math.min(state.baseScale, fitScale);
  }

  function draw() {
    background(12, 14, 24);

    // Build scene geometry
    const scene = buildScene(state.k, state.phi, state.frame, state.envelopeType);
    const bounds = boundsOf([scene.bluePts, scene.envInnerPts, scene.envOuterPts]);

    // Compute dynamic scale
    const s = computeScale(bounds);

    // Draw axes and ticks
    drawAxes(s, bounds);

    // Draw curves
    // Blue curve: epitrochoid (housing)
    drawCurve(scene.bluePts, [50, 100, 200], s);

    // Orange curve(s): envelope(s) (rotor)
    if (scene.envInnerPts) drawCurve(scene.envInnerPts, [230, 150, 0], s);
    if (scene.envOuterPts) drawCurve(scene.envOuterPts, [230, 150, 0], s);
  }
</script>
</body>
</html>
