    dGamma: document.getElementById('gamma-sat-value-display'),
    dR: document.getElementById('r-value-display'),
    dRpe: document.getElementById('rpe-value-display'),
    dMw: document.getElementById('mw-value-display'),
    dPhiAxis: document.getElementById('phi-axis-value-display'),
    dCAxis: document.getElementById('c-axis-value-display'),

    formula: document.getElementById('formula-display'),
    phimax: document.getElementById('phimax-display'),
    range: document.getElementById('range-display'),
    plot: document.getElementById('plot-container')
  };

  // State
  let tracesOrder = {
    y1: 0,
    y2: 1,
    y3: 2,
    shadeLower: 3,
    shadeUpper: 4,
    markers: 5
  };

  function formatNum(x) {
    if (!isFinite(x)) return '∞';
    if (Math.abs(x) >= 1000) return x.toExponential(2);
    if (Math.abs(x) < 0.001) return x.toExponential(2);
    return x.toFixed(3);
  }

  function getParams() {
    return {
      phi: parseFloat(el.phi.value),
      gamma: parseFloat(el.gamma.value),
      r: parseFloat(el.r.value),
      rpe: parseFloat(el.rpe.value),
      mw: parseFloat(el.mw.value),
      phiAxisMax: parseFloat(el.phiAxis.value),
      cAxisMax: parseFloat(el.cAxis.value)
    };
  }

  function computeAB(params) {
    const A = (3 * params.gamma / params.r) * C_UNIT_CONVERSION;
    const B = K_DEPLETION * params.mw / Math.pow(params.rpe, 3);
    return {A, B};
  }

  function computePhiMax(A, B) {
    const val = Math.sqrt(B / A);
    return isFinite(val) ? val : NaN;
  }

  function genPhiArray(maxPhi, N) {
    const arr = [];
    const start = Math.min(EPS, maxPhi);
    for (let i = 0; i < N; i++) {
      const t = i / (N - 1);
      const p = start + t * (maxPhi - start);
      arr.push(p);
    }
    return arr;
  }

  function y1(A, phi) { return A * phi; }
  function y2(A, phi) { return A * phi / (1 - phi); }
  function y3(A, B, phi) { return A * phi + B / phi; }

  function buildData(params) {
    const N = 401;
    const {A,B} = computeAB(params);
    const phiMax = computePhiMax(A,B);

    // Main curves across full x-axis range
    const x = genPhiArray(params.phiAxisMax, N);
    const y1Arr = x.map(p => y1(A, p));
    const y2Arr = x.map(p => y2(A, p));
    const y3Arr = x.map(p => y3(A, B, p));

    // Shaded region up to min(phiMax, phiAxisMax)
    const shadeEnd = Math.min(phiMax, params.phiAxisMax);
    let xShade = [];
    if (isFinite(shadeEnd) && shadeEnd > EPS) {
      const Ns = Math.max(3, Math.floor(N * (shadeEnd / params.phiAxisMax)));
      const start = Math.min(EPS, shadeEnd);
      for (let i = 0; i < Ns; i++) {
        const t = i / (Ns - 1);
        xShade.push(start + t * (shadeEnd - start));
      }
    } else {
      // No shade region; create a trivial degenerate segment to avoid Plotly errors
      xShade = [EPS, EPS];
    }
    const y2Shade = xShade.map(p => y2(A,p));
    const y3Shade = xShade.map(p => y3(A,B,p));

    return {
      A, B, phiMax, x, y1Arr, y2Arr, y3Arr, xShade, y2Shade, y3Shade
    };
  }

  function makeTraces(data, params) {
    const lineWidth = 2.5;
    const t1 = {
      type: 'scatter', mode: 'lines', x: data.x, y: data.y1Arr,
      line: {color: 'blue', width: lineWidth}, name: 'y1'
    };
    const t2 = {
      type: 'scatter', mode: 'lines', x: data.x, y: data.y2Arr,
      line: {color: 'red', width: lineWidth}, name: 'y2'
    };
    const t3 = {
      type: 'scatter', mode: 'lines', x: data.x, y: data.y3Arr,
      line: {color: 'green', width: lineWidth}, name: 'y3'
    };
    const shadeLower = {
      type: 'scatter', mode: 'lines', x: data.xShade, y: data.y2Shade,
      line: {color: 'rgba(0,0,0,0)'}, hoverinfo: 'skip', showlegend: false
    };
    const shadeUpper = {
      type: 'scatter', mode: 'lines', x: data.xShade, y: data.y3Shade,
      fill: 'tonexty', fillcolor: 'rgba(173, 216, 230, 0.5)',
      line: {color: 'rgba(0,0,0,0)'}, hoverinfo: 'skip', showlegend: false,
      name: 'Stability Region'
    };
    const phiVal = params.phi;
    const markers = {
      type: 'scatter', mode: 'markers',
      x: [phiVal, phiVal, phiVal],
      y: [y1(data.A, phiVal), y2(data.A, phiVal), y3(data.A, data.B, phiVal)],
      marker: {color: ['blue','red','green'], size: 10, symbol: 'circle'},
      hovertemplate: 'ϕ=%{x:.4f}<br>C=%{y:.3f}<extra></extra>',
      showlegend: false
    };
    return [t1, t2, t3, shadeLower, shadeUpper, markers];
  }

  function makeLayout(params) {
    const phiVal = params.phi;
    return {
      margin: {l: 60, r: 20, t: 10, b: 50},
      xaxis: {title: 'ϕ (volume fraction)', range: [0, params.phiAxisMax]},
      yaxis: {title: 'C (kg/m³)', range: [0, params.cAxisMax]},
      shapes: [
        {
          type: 'line',
          x0: phiVal, x1: phiVal,
          y0: 0, y1: params.cAxisMax,
          line: {color: 'grey', dash: 'dash', width: 2}
        }
      ],
      showlegend: false
    };
  }

  function updateInfoPanel(data, params) {
    el.formula.innerHTML =
      'Curves: <span style="color:blue;">y_1(\\(\\phi\\))</span>, ' +
      '<span style="color:red;">y_2(\\(\\phi\\))</span>, and ' +
      '<span style="color:green;">y_3(\\(\\phi\\))</span>. ' +
      'The shaded area indicates the stability region for \\(0 < \\phi \\le \\phi_{max}\\).';

    const phimaxStr = isFinite(data.phiMax) ? data.phiMax : NaN;
    el.phimax.innerHTML = `\\(\\phi_{max} = ${formatNum(phimaxStr)}\\)`;

    const phi = params.phi;
    const cLower = y2(data.A, phi);
    const cUpper = y3(data.A, data.B, phi);
    if (isFinite(cLower) && isFinite(cUpper) && cUpper > cLower) {
      el.range.innerHTML = `Stability range at \\(\\phi=${formatNum(phi)}\\): ` +
        `\\(\\; ${formatNum(cLower)} \\le C \\le ${formatNum(cUpper)}\\;\\) (kg/m³)`;
    } else {
      el.range.innerHTML = `At \\(\\phi=${formatNum(phi)}\\), no stable concentration window (\\(y_2 > y_3\\)).`;
    }

    if (window.MathJax && MathJax.typesetPromise) {
      MathJax.typesetPromise([el.formula, el.phimax, el.range]).catch(()=>{});
    }
  }

  function updateValueDisplays() {
    el.dPhi.textContent = parseFloat(el.phi.value).toFixed(4);
    el.dGamma.textContent = parseFloat(el.gamma.value).toFixed(1);
    el.dR.textContent = parseFloat(el.r.value).toFixed(0);
    el.dRpe.textContent = parseFloat(el.rpe.value).toFixed(0);
    el.dMw.textContent = parseFloat(el.mw.value).toFixed(0);
    el.dPhiAxis.textContent = parseFloat(el.phiAxis.value).toFixed(2);
    el.dCAxis.textContent = parseFloat(el.cAxis.value).toFixed(1);
  }

  function clampPhiToAxis() {
    const axisMax = parseFloat(el.phiAxis.value);
    el.phi.max = axisMax;
    if (parseFloat(el.phi.value) > axisMax) {
      el.phi.value = axisMax;
    }
  }

  function renderAll() {
    updateValueDisplays();
    clampPhiToAxis();
    const params = getParams();
    const data = buildData(params);
    const traces = makeTraces(data, params);
    const layout = makeLayout(params);

    Plotly.react(el.plot, traces, layout, {responsive: true});
    updateInfoPanel(data, params);
  }

  function updateForPhiChange() {
    updateValueDisplays();
    const params = getParams();
    const {A, B} = computeAB(params);
    const phi = params.phi;

    // Update markers (trace index = markers)
    const newX = [[phi, phi, phi]];
    const newY = [[y1(A, phi), y2(A, phi), y3(A, B, phi)]];
    Plotly.restyle(el.plot, {x: newX, y: newY}, [tracesOrder.markers]);

    // Update vertical line
    Plotly.relayout(el.plot, {
      'shapes[0].x0': phi,
      'shapes[0].x1': phi,
      'shapes[0].y1': params.cAxisMax
    });

    // Update info panel (range only depends on phi for fixed A,B)
    const dataPartial = {A, B, phiMax: computePhiMax(A,B)};
    updateInfoPanel(dataPartial, params);
  }

  function updateForParamChange() {
    // Parameters affect curves, phi_max, shaded region, markers.
    renderAll();
  }

  function updateForAxisChange(which) {
    updateValueDisplays();
    // Keep phi slider linked to axis max
    if (which === 'phi') {
      clampPhiToAxis();
    }
    // Recompute to ensure line arrays cover the new axis range
    const params = getParams();
    const data = buildData(params);
    const traces = makeTraces(data, params);
    const layout = makeLayout(params);
    Plotly.react(el.plot, traces, layout, {responsive: true});
    updateInfoPanel(data, params);
  }

  // Initialize
  function init() {
    // Initialize formula header content
    el.formula.innerHTML =
      'Curves: <span style="color:blue;">y_1(\\(\\phi\\))</span>, ' +
      '<span style="color:red;">y_2(\\(\\phi\\))</span>, and ' +
      '<span style="color:green;">y_3(\\(\\phi\\))</span>.';
    if (window.MathJax && MathJax.typesetPromise) MathJax.typesetPromise().catch(()=>{});

    renderAll();

    // Listeners
    el.phi.addEventListener('input', updateForPhiChange);

    el.gamma.addEventListener('input', updateForParamChange);
    el.r.addEventListener('input', updateForParamChange);
    el.rpe.addEventListener('input', updateForParamChange);
    el.mw.addEventListener('input', updateForParamChange);

    el.phiAxis.addEventListener('input', () => updateForAxisChange('phi'));
    el.cAxis.addEventListener('input', () => updateForAxisChange('c'));
  }

  // Start
  window.addEventListener('DOMContentLoaded', init);
})();
</script>
</body>
</html>
