          <span id="label-v0x"></span>
          <span id="label-v0y"></span>
        </div>
      </div>
    </div>

    <div id="canvas-container"></div>
  </div>

  <script>
    // -----------------------
    // State and DOM references
    // -----------------------
    const el = {
      time: document.getElementById('slider-time'),
      m1: document.getElementById('slider-m1'),
      v1: document.getElementById('slider-v1'),
      m2: document.getElementById('slider-m2'),
      v2: document.getElementById('slider-v2'),
      lblTime: document.getElementById('label-time'),
      lblM1: document.getElementById('label-m1'),
      lblV1: document.getElementById('label-v1'),
      lblM2: document.getElementById('label-m2'),
      lblV2: document.getElementById('label-v2'),
      joyBox: document.getElementById('joystick-container'),
      joyKnob: document.getElementById('joystick-knob'),
      lblV0x: document.getElementById('label-v0x'),
      lblV0y: document.getElementById('label-v0y'),
      canvasContainer: document.getElementById('canvas-container')
    };

    const JOY_MIN = -5, JOY_MAX = 5;

    const state = {
      // sliders
      t: parseFloat(el.time.value),
      m1: parseFloat(el.m1.value),
      m2: parseFloat(el.m2.value),
      s1_cm: parseFloat(el.v1.value), // particle 1 speed in CM
      s2_cm: 0,                       // derived: m1/m2 * s1
      // observer velocity (defaults)
      v0x: 1.1,
      v0y: 1.1,
      // derived vectors
      u1_cm: {x:0,y:0},
      u2_cm: {x:0,y:0},
      v1_cm: {x:0,y:0},   // post-collision (rotated by -45°)
      v2_cm: {x:0,y:0},   // post-collision (rotated by -45°)
    };

    // -------------
    // Math helpers
    // -------------
    const V = {
      add: (a,b)=>({x:a.x+b.x, y:a.y+b.y}),
      sub: (a,b)=>({x:a.x-b.x, y:a.y-b.y}),
      mul: (a,s)=>({x:a.x*s, y:a.y*s}),
      mag: (a)=>Math.hypot(a.x, a.y),
      rotate: (a, ang)=>({x:a.x*Math.cos(ang) - a.y*Math.sin(ang),
                          y:a.x*Math.sin(ang) + a.y*Math.cos(ang)}),
      dot: (a,b)=>a.x*b.x + a.y*b.y
    };

    function fmt(n, d) { return Number(n).toFixed(d); }

    // -----------------------
    // UI label updates
    // -----------------------
    function updateLabelsOnly() {
      el.lblTime.textContent = fmt(state.t, 2) + " s";
      el.lblM1.textContent = fmt(state.m1, 2) + " kg";
      el.lblV1.textContent = fmt(state.s1_cm, 2) + " m/s";
      el.lblM2.textContent = fmt(state.m2, 2) + " kg";
      el.lblV2.textContent = fmt(parseFloat(el.v2.value), 2) + " m/s";
      el.lblV0x.textContent = "v0x: " + fmt(state.v0x, 3) + " m/s";
      el.lblV0y.textContent = "v0y: " + fmt(state.v0y, 3) + " m/s";
    }

    // -----------------------
    // Physics recomputation
    // -----------------------
    const RAD_45 = -Math.PI/4; // -45 degrees
    function recompute() {
      state.s2_cm = (state.m1 / state.m2) * state.s1_cm;
      state.u1_cm = {x: state.s1_cm, y: 0};
      state.u2_cm = {x: -state.s2_cm, y: 0};
      state.v1_cm = V.rotate(state.u1_cm, RAD_45);
      state.v2_cm = V.rotate(state.u2_cm, RAD_45);
    }

    // -----------------------
    // p5.js rendering
    // -----------------------
    let s = 10; // world meters to pixels (dynamic)
    let cx = 0, cy = 0;

    function containerSize() {
      const r = el.canvasContainer.getBoundingClientRect();
      const w = Math.max(320, r.width);
      const h = Math.max(320, r.height);
      return {w, h};
    }

    function setup() {
      const {w,h} = containerSize();
      const cnv = createCanvas(w, h);
      cnv.parent('canvas-container');
      textFont('system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial');
      noLoop();
      recompute();
      positionJoystickFromState();
      updateLabelsOnly();
      redraw();
    }

    function windowResized() {
      const {w,h} = containerSize();
      resizeCanvas(w, h);
      redraw();
    }

    function worldToScreen(p) {
      return { x: cx + s * p.x, y: cy - s * p.y };
    }

    function drawArrowPx(base, vec, clr="#000") {
      push();
      stroke(clr);
      fill(clr);
      strokeWeight(2);
      line(base.x, base.y, base.x + vec.x, base.y + vec.y);
      // arrow head
      const ang = Math.atan2(vec.y, vec.x);
      const head = 10;
      const x2 = base.x + vec.x, y2 = base.y + vec.y;
      push();
      translate(x2, y2);
      rotate(ang);
      beginShape();
      vertex(0, 0);
      vertex(-head, 4);
      vertex(-head, -4);
      endShape(CLOSE);
      pop();
      pop();
    }

    function draw() {
      background(255);
      const W = width, H = height;
      cx = W/2;
      cy = H/2;

      // Precompute observer-frame velocities
      const v0 = {x: state.v0x, y: state.v0y};
      const u1_obs = V.add(state.u1_cm, v0);
      const u2_obs = V.add(state.u2_cm, v0);
      const v1_obs = V.add(state.v1_cm, v0);
      const v2_obs = V.add(state.v2_cm, v0);

      // Determine dynamic scale s based on trajectory extents
      const Tneg = -20, Tpos = 20;
      const candidates = [
        V.mag(u1_obs)*Math.abs(Tneg),
        V.mag(u2_obs)*Math.abs(Tneg),
        V.mag(v1_obs)*Tpos,
        V.mag(v2_obs)*Tpos,
        2 // minimal span
      ];
      const maxDist = Math.max(...candidates);
      const maxView = 0.45 * Math.min(W, H);
      s = maxDist > 0 ? (maxView / maxDist) : 10;

      // Draw axes cross at origin (optional subtle guide)
      push();
      stroke(230);
      strokeWeight(1);
      line(0, cy, W, cy);
      line(cx, 0, cx, H);
      pop();

      // Trajectories with dashed lines
      function drawTraj(u_obs, v_obs, color) {
        const pA = V.mul(u_obs, Tneg);
        const p0 = {x:0, y:0};
        const pB = V.mul(v_obs, Tpos);

        const A = worldToScreen(pA);
        const O = worldToScreen(p0);
        const B = worldToScreen(pB);

        push();
        stroke(color);
        strokeWeight(2);
        // dashed
        drawingContext.setLineDash([6,6]);
        line(A.x, A.y, O.x, O.y); // pre-collision
        line(O.x, O.y, B.x, B.y); // post-collision
        drawingContext.setLineDash([]); // reset
        pop();
      }
      drawTraj(u1_obs, v1_obs, '#ff4d4d'); // slightly softer red for lines
      drawTraj(u2_obs, v2_obs, '#4d4dff'); // slightly softer blue for lines

      // Draw observer velocity vector (-v0) from fixed pixel base
      const arrowBase = {x: 60, y: 60};
      const arrowVecPx = {x: -state.v0x * s, y: state.v0y * s}; // screen-space delta
      drawArrowPx(arrowBase, arrowVecPx, '#000');

      // Draw particles
      const kRadius = 8;
      const r1 = kRadius * Math.sqrt(state.m1);
      const r2 = kRadius * Math.sqrt(state.m2);
      const t = state.t;
      const v1_now = t < 0 ? u1_obs : v1_obs;
      const v2_now = t < 0 ? u2_obs : v2_obs;
      const p1 = worldToScreen(V.mul(v1_now, t));
      const p2 = worldToScreen(V.mul(v2_now, t));

      noStroke();
      fill('#FF0000');
      circle(p1.x, p1.y, 2*r1);
      fill('#0000FF');
      circle(p2.x, p2.y, 2*r2);

      // Info text block (pre-collision metrics in observer frame)
      const v0mag = Math.hypot(state.v0x, state.v0y);
      const v1mag = V.mag(u1_obs);
      const v2mag = V.mag(u2_obs);
      const energy = 0.5*state.m1*v1mag*v1mag + 0.5*state.m2*v2mag*v2mag;

      fill(0);
      textSize(13);
      textAlign(LEFT, TOP);
      const lines = [
        "pre-collision metrics (observer frame):",
        "v₀ = " + fmt(v0mag, 5) + " m/s",
        "v₁ = " + fmt(v1mag, 5) + " m/s",
        "v₂ = " + fmt(v2mag, 5) + " m/s",
        "Eₖ(total) = " + fmt(energy, 2) + " J"
      ];
      const boxX = 16, boxY = 16;
      const pad = 8;
      const lineH = 18;
      const boxW = 260;
      const boxH = pad*2 + lines.length*lineH;

      // semi-transparent backdrop for text
      push();
      noStroke();
      fill(255, 255, 255, 220);
      rect(boxX-6, boxY-6, boxW, boxH, 6);
      pop();

      for (let i=0;i<lines.length;i++) {
        text(lines[i], boxX, boxY + i*lineH);
      }

      // Legend for observer vector
      textSize(12);
      text("observer vector (−v₀)", arrowBase.x + 10, arrowBase.y + 10);
    }

    // -----------------------
    // UI events
    // -----------------------
    function onAnySlider() {
      state.t = parseFloat(el.time.value);
      state.m1 = parseFloat(el.m1.value);
      state.m2 = parseFloat(el.m2.value);
      state.s1_cm = parseFloat(el.v1.value);
      // v2 slider is display only
      updateLabelsOnly();
      recompute();
      redraw();
    }
    el.time.addEventListener('input', ()=>{
      // time slider should not alter displayed metrics; recompute not necessary
      state.t = parseFloat(el.time.value);
      el.lblTime.textContent = fmt(state.t, 2) + " s";
      redraw();
    });
    el.m1.addEventListener('input', onAnySlider);
    el.v1.addEventListener('input', onAnySlider);
    el.m2.addEventListener('input', onAnySlider);
    el.v2.addEventListener('input', ()=> {
      // display only
      el.lblV2.textContent = fmt(parseFloat(el.v2.value), 2) + " m/s";
    });

    // -----------------------
    // Joystick implementation
    // -----------------------
    let dragging = false;

    function clamp(v,min,max){ return Math.max(min, Math.min(max, v)); }

    function positionJoystickFromState() {
      const box = el.joyBox.getBoundingClientRect();
      const size = {w: box.width, h: box.height};
      const nx = (state.v0x - JOY_MIN) / (JOY_MAX - JOY_MIN); // 0..1
      const ny = (state.v0y - JOY_MIN) / (JOY_MAX - JOY_MIN); // 0..1
      // y up: knob visual y increases downward; invert mapping
      const px = nx * size.w;
      const py = (1 - ny) * size.h;
      el.joyKnob.style.left = px + "px";
      el.joyKnob.style.top  = py + "px";
      el.lblV0x.textContent = "v0x: " + fmt(state.v0x, 3) + " m/s";
      el.lblV0y.textContent = "v0y: " + fmt(state.v0y, 3) + " m/s";
    }

    function updateV0FromPointer(clientX, clientY) {
      const rect = el.joyBox.getBoundingClientRect();
      let x = clamp(clientX - rect.left, 0, rect.width);
      let y = clamp(clientY - rect.top, 0, rect.height);
      const nx = x / rect.width;           // 0..1
      const ny = 1 - (y / rect.height);    // 0..1, y up
      state.v0x = JOY_MIN + nx * (JOY_MAX - JOY_MIN);
      state.v0y = JOY_MIN + ny * (JOY_MAX - JOY_MIN);
      // Position knob (center-aligned via CSS transform)
      el.joyKnob.style.left = x + "px";
      el.joyKnob.style.top  = y + "px";
      // Update labels
      el.lblV0x.textContent = "v0x: " + fmt(state.v0x, 3) + " m/s";
      el.lblV0y.textContent = "v0y: " + fmt(state.v0y, 3) + " m/s";
      // Recompute observer frame (metrics depend on v0 only; time-independent)
      recompute();
      redraw();
    }

    function startDrag(e) {
      dragging = true;
      if (e.type.startsWith('touch')) {
        const t = e.touches[0];
        updateV0FromPointer(t.clientX, t.clientY);
      } else {
        updateV0FromPointer(e.clientX, e.clientY);
      }
      e.preventDefault();
    }
    function moveDrag(e) {
      if (!dragging) return;
      if (e.type.startsWith('touch')) {
        const t = e.touches[0];
        updateV0FromPointer(t.clientX, t.clientY);
      } else {
        updateV0FromPointer(e.clientX, e.clientY);
      }
      e.preventDefault();
    }
    function endDrag() { dragging = false; }

    el.joyBox.addEventListener('mousedown', startDrag);
    el.joyKnob.addEventListener('mousedown', startDrag);
    window.addEventListener('mousemove', moveDrag);
    window.addEventListener('mouseup', endDrag);

    el.joyBox.addEventListener('touchstart', startDrag, {passive:false});
    el.joyBox.addEventListener('touchmove', moveDrag, {passive:false});
    el.joyBox.addEventListener('touchend', endDrag);

    // Initialize labels and state on load
    window.addEventListener('load', () => {
      updateLabelsOnly();
      recompute();
      positionJoystickFromState();
      // Initial redraw will happen in setup via p5
    });
  </script>
</body>
</html>
