<body>
<div id="main-container">
  <!-- Control Panel -->
  <div class="control-panel">
    <div class="control-group">
      <label for="shift-slider">shift</label>
      <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
    </div>
    <div class="control-group">
      <label for="text-input">message</label>
      <input type="text" id="text-input" list="phrase-list" value="abcd">
      <datalist id="phrase-list">
        <option value="abcd">
        <option value="test phrase">
        <option value="to be or not to be">
        <option value="the quick brown fox jumped over the lazy dog">
      </datalist>
    </div>
  </div>

  <!-- Display Area -->
  <div id="display-area">
    <!-- Alphabet Mapping -->
    <div id="mapping-container">
      <div id="plaintext-alphabet" class="alphabet-row"></div>
      <div id="ciphertext-alphabet" class="alphabet-row"></div>
    </div>
    <!-- Key Section -->
    <div class="key-display">
      <p id="encryption-key-display">encryption key: 12</p>
      <p id="decryption-key-display">decryption key: 14</p>
    </div>
    <!-- Text Output -->
    <div class="output-display">
      <p id="plaintext-output"><span class="plaintext-label">plaintext :</span> abcd</p>
      <p id="ciphertext-output"><span class="ciphertext-label">ciphertext:</span> MNOP</p>
    </div>
  </div>
</div>

<script>
  // Encryption logic
  function encrypt(text, shift) {
    let result = '';
    for (let i = 0; i < text.length; i++) {
      const ch = text[i];
      if (ch >= 'a' && ch <= 'z') {
        const idx = ch.charCodeAt(0) - 'a'.charCodeAt(0);
        const shifted = (idx + shift) % 26;
        result += String.fromCharCode('A'.charCodeAt(0) + shifted);
      } else {
        result += ch;
      }
    }
    return result;
  }

  // Update everything based on current slider and text input
  function updateCipher() {
    const shiftValue = parseInt(document.getElementById('shift-slider').value, 10);
    const text = document.getElementById('text-input').value;

    // Plaintext alphabet (unchanged)
    const plainContainer = document.getElementById('plaintext-alphabet');
    plainContainer.innerHTML = '';
    for (let i = 0; i < 26; i++) {
      const span = document.createElement('span');
      span.textContent = String.fromCharCode('a'.charCodeAt(0) + i);
      plainContainer.appendChild(span);
    }

    // Ciphertext alphabet (shifted)
    const cipherContainer = document.getElementById('ciphertext-alphabet');
    cipherContainer.innerHTML = '';
    for (let i = 0; i < 26; i++) {
      const shiftedIdx = (i + shiftValue) % 26;
      const span = document.createElement('span');
      span.textContent = String.fromCharCode('A'.charCodeAt(0) + shiftedIdx);
      cipherContainer.appendChild(span);
    }

    // Keys
    document.getElementById('encryption-key-display').textContent =
      'encryption key: ' + shiftValue;
    const decryptionKey = (26 - shiftValue) % 26;
    document.getElementById('decryption-key-display').textContent =
      'decryption key: ' + decryptionKey;

    // Text outputs
    document.getElementById('plaintext-output').innerHTML =
      '<span class="plaintext-label">plaintext :</span> ' + text;
    const ciphertext = encrypt(text, shiftValue);
    document.getElementById('ciphertext-output').innerHTML =
      '<span class="ciphertext-label">ciphertext:</span> ' + ciphertext;
  }

  // Initial setup
  document.addEventListener('DOMContentLoaded', () => {
    updateCipher(); // Set default state
    document.getElementById('shift-slider').addEventListener('input', updateCipher);
    document.getElementById('text-input').addEventListener('input', updateCipher);
  });
</script>
</body>
</html>
