<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Ionic Coordination Visualizer</title>
<style>
  body{
    margin:0;font-family:Arial,sans-serif;background:#f0f0f0;
  }
  #app{
    max-width:1200px;margin:auto;padding:10px;
  }
  #control-panel{
    padding:10px 0;
    display:flex;
    flex-direction:column;
    gap:10px;
  }
  .button-group{
    display:flex;
    gap:5px;
  }
  .button-group button{
    padding:5px 10px;
    border:none;
    border-radius:4px;
    background:#e0e0e0;
    cursor:pointer;
  }
  .button-group button.active{
    background:#4a90e2;color:#fff;
  }
  .slider-group{
    display:flex;
    align-items:center;
    gap:10px;
  }
  .slider-group label{
    width:150px;
  }
  .slider-group input[type="range"]{
    flex:1;
  }
  #display-container{
    border:1px solid #ccc;
    padding:10px;
    background:#fff;
  }
  #canvas-container{
    width:100%;
    height:600px;
    position:relative;
  }
  #ratio-text{
    margin-top:10px;font-weight:bold;
  }
</style>
</head>
<body>
<div id="app">
  <!-- Control Panel -->
  <div id="control-panel">
    <!-- Coordination Buttons -->
    <div class="button-group" id="coordination-buttons">
      <label>coordination</label>
      <button id="btn-threefold" class="active">threefold</button>
      <button id="btn-fourfold">fourfold</button>
      <button id="btn-sixfold">sixfold</button>
      <button id="btn-eightfold">eightfold</button>
    </div>

    <!-- Anion radius slider -->
    <div class="slider-group">
      <label>relative anion radius</label>
      <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
      <span id="value-anion-radius">0.100</span>
    </div>

    <!-- Cation radius slider -->
    <div class="slider-group">
      <label>relative cation radius</label>
      <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
      <span id="value-cation-radius">0.100</span>
    </div>

    <!-- Opacity slider -->
    <div class="slider-group">
      <label>opacity of anions</label>
      <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
      <!-- No value display needed -->
    </div>
  </div>

  <!-- Main Display Container -->
  <div id="display-container">
    <div id="canvas-container"></div>
    <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
  </div>
</div>

<!-- Three.js and OrbitControls CDN -->
<script src="https://cdn.jsdelivr.net/npm/three@0.156.0/build/three.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/three@0.156.0/examples/js/controls/OrbitControls.js"></script>

<script>
/*------------------------------ Global Variables ------------------------------*/
let scene, camera, renderer, controls, anionMaterial, cationMaterial, cationMesh, anionMeshes = [], boundingGroup;

/*------------------------------ Utility Functions ------------------------------*/
function formatFloat(val, decimals=3){
  return parseFloat(val).toFixed(decimals);
}

function updateRatioText(){
  const cationRadius = parseFloat(sliderCation.value);
  const anionRadius = parseFloat(sliderAnion.value);
  const ratio = (cationRadius / anionRadius).toFixed(2);
  ratioText.innerText = `The ratio of the cation radius to the anion radius is ${ratio}.`;
}

function createMatcapTexture(){
  const size = 128;
  const canvas = document.createElement('canvas');
  canvas.width = size;
  canvas.height = size;
  const ctx = canvas.getContext('2d');
  const gradient = ctx.createRadialGradient(size/2,size/2,0,size/2,size/2,size/2);
  gradient.addColorStop(0, '#0000ff'); // blue
  gradient.addColorStop(0.5, '#ff8000'); // orange
  gradient.addColorStop(1, '#ff0000'); // red
  ctx.fillStyle = gradient;
  ctx.fillRect(0,0,size,size);
  const texture = new THREE.CanvasTexture(canvas);
  texture.minFilter = THREE.LinearFilter;
  texture.magFilter = THREE.LinearFilter;
  return texture;
}

function getPositions(coordination){
  const distance = 1.5; // fixed radial distance
  const positions = [];
  const norm = 1/ Math.sqrt(3); // for tetrahedron and cube
  switch(coordination){
    case 'threefold':
      for(let i=0;i<3;i++){
        const angle = Math.PI/3 + i * (2*Math.PI/3);
        positions.push(new THREE.Vector3(distance*Math.cos(angle), distance*Math.sin(angle),0));
      }
      break;
    case 'fourfold':
      const tetra = [
        new THREE.Vector3(1,1,1),
        new THREE.Vector3(1,-1,-1),
        new THREE.Vector3(-1,1,-1),
        new THREE.Vector3(-1,-1,1)
      ];
      for(const v of tetra){
        positions.push(v.clone().multiplyScalar(distance*norm));
      }
      break;
    case 'sixfold':
      const oct = [
        new THREE.Vector3(1,0,0),
        new THREE.Vector3(-1,0,0),
        new THREE.Vector3(0,1,0),
        new THREE.Vector3(0,-1,0),
        new THREE.Vector3(0,0,1),
        new THREE.Vector3(0,0,-1)
      ];
      for(const v of oct){
        positions.push(v.clone().multiplyScalar(distance));
      }
      break;
    case 'eightfold':
      const cube = [
        new THREE.Vector3(1,1,1),
        new THREE.Vector3(1,1,-1),
        new THREE.Vector3(1,-1,1),
        new THREE.Vector3(1,-1,-1),
        new THREE.Vector3(-1,1,1),
        new THREE.Vector3(-1,1,-1),
        new THREE.Vector3(-1,-1,1),
        new THREE.Vector3(-1,-1,-1)
      ];
      for(const v of cube){
        positions.push(v.clone().multiplyScalar(distance*norm));
      }
      break;
  }
  return positions;
}

/*------------------------------ Scene Building ------------------------------*/
function buildScene(coordination){
  // remove previous anions and bounding
  anionMeshes.forEach(m=>scene.remove(m));
  anionMeshes = [];
  if(boundingGroup){
    scene.remove(boundingGroup);
    boundingGroup = null;
  }

  const positions = getPositions(coordination);
  for(const pos of positions){
    const mesh = new THREE.Mesh(
      new THREE.SphereGeometry(1,32,32),
      anionMaterial
    );
    mesh.position.copy(pos);
    mesh.scale.set(parseFloat(sliderAnion.value),parseFloat(sliderAnion.value),parseFloat(sliderAnion.value));
    scene.add(mesh);
    anionMeshes.push(mesh);
  }

  // bounding box
  if(coordination !== 'threefold'){
    const bboxGeom = new THREE.EdgesGeometry(new THREE.BoxGeometry(3,3,3));
    const bboxMat = new THREE.LineBasicMaterial({color:0x888888});
    boundingGroup = new THREE.LineSegments(bboxGeom,bboxMat);
    boundingGroup.position.set(0,0,0);
    scene.add(boundingGroup);
  }

  // adjust camera
  camera.position.set(0,0,5);
  controls.update();
}

/*------------------------------ Initialization ------------------------------*/
function init(){
  // elements
  sliderAnion = document.getElementById('slider-anion-radius');
  sliderCation = document.getElementById('slider-cation-radius');
  sliderOpacity = document.getElementById('slider-opacity');
  valueAnion = document.getElementById('value-anion-radius');
  valueCation = document.getElementById('value-cation-radius');
  ratioText = document.getElementById('ratio-text');

  // Scene
  scene = new THREE.Scene();

  // Camera
  camera = new THREE.PerspectiveCamera(45,window.innerWidth/innerHeight,0.1,1000);
  camera.position.set(0,0,5);

  // Renderer
  renderer = new THREE.WebGLRenderer({antialias:true});
  renderer.setSize(document.getElementById('canvas-container').clientWidth,
                   document.getElementById('canvas-container').clientHeight);
  renderer.setPixelRatio(window.devicePixelRatio);
  document.getElementById('canvas-container').appendChild(renderer.domElement);

  // Controls
  controls = new THREE.OrbitControls(camera,renderer.domElement);
  controls.enableDamping = true;

  // Lights
  scene.add(new THREE.AmbientLight(0xffffff,0.6));
  const dirLight = new THREE.PointLight(0xffffff,0.8);
  dirLight.position.set(5,5,5);
  scene.add(dirLight);

  // Materials
  cationMaterial = new THREE.MeshPhongMaterial({
    matcap: createMatcapTexture(),
    shininess: 100
  });

  anionMaterial = new THREE.MeshPhongMaterial({
    color: 0xe0d8e0,
    shininess: 50,
    transparent:true,
    opacity: parseFloat(sliderOpacity.value)
  });

  // Cation Mesh
  cationMesh = new THREE.Mesh(
    new THREE.SphereGeometry(1,32,32),
    cationMaterial
  );
  cationMesh.scale.set(parseFloat(sliderCation.value),parseFloat(sliderCation.value),parseFloat(sliderCation.value));
  scene.add(cationMesh);

  // Build initial scene
  buildScene('threefold');

  // Update UI
  valueAnion.innerText = formatFloat(sliderAnion.value,3);
  valueCation.innerText = formatFloat(sliderCation.value,3);
  updateRatioText();

  // Event listeners
  sliderAnion.addEventListener('input',()=>{
    const val = parseFloat(sliderAnion.value);
    valueAnion.innerText = formatFloat(val,3);
    anionMeshes.forEach(m=>m.scale.set(val,val,val));
    updateRatioText();
  });

  sliderCation.addEventListener('input',()=>{
    const val = parseFloat(sliderCation.value);
    valueCation.innerText = formatFloat(val,3);
    cationMesh.scale.set(val,val,val);
    updateRatioText();
  });

  sliderOpacity.addEventListener('input',()=>{
    const val = parseFloat(sliderOpacity.value);
    anionMaterial.opacity = val;
  });

  // Buttons
  const btnThreefold = document.getElementById('btn-threefold');
  const btnFourfold = document.getElementById('btn-fourfold');
  const btnSixfold = document.getElementById('btn-sixfold');
  const btnEightfold = document.getElementById('btn-eightfold');
  const buttons = [btnThreefold,btnFourfold,btnSixfold,btnEightfold];

  function setActive(button){
    buttons.forEach(b=>b.classList.remove('active'));
    button.classList.add('active');
  }

  btnThreefold.addEventListener('click',()=>{setActive(btnThreefold);buildScene('threefold');});
  btnFourfold.addEventListener('click',()=>{setActive(btnFourfold);buildScene('fourfold');});
  btnSixfold.addEventListener('click',()=>{setActive(btnSixfold);buildScene('sixfold');});
  btnEightfold.addEventListener('click',()=>{setActive(btnEightfold);buildScene('eightfold');});

  // Resize
  window.addEventListener('resize',()=> {
    const width = document.getElementById('canvas-container').clientWidth;
    const height = document.getElementById('canvas-container').clientHeight;
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
    renderer.setSize(width, height);
  });

  animate();
}

function animate(){
  requestAnimationFrame(animate);
  controls.update();
  renderer.render(scene,camera);
}

init();
</script>
</body>
</html>
