<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Inclined Plane Block Animation</title>
<script src="https://cdn.jsdelivr.net/npm/p5@1.8.2/lib/p5.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
<style>
  body, html {margin:0; height:100%; font-family:sans-serif;}
  #main {display:flex; height:100%;}
  #control-panel {width:120px; background:#f0f0f0; padding:10px; box-sizing:border-box; text-align:center;}
  #control-panel input[type=range] {writing-mode: bt-lr; width:80%; transform: rotate(-90deg); margin:20px auto;}
  #control-panel button {width:40px; margin:5px;}
  #visualization-area {flex:1; padding:10px; box-sizing:border-box;}
  #info-display {font-size:1.2em; margin-bottom:10px;}
</style>
</head>
<body>
<div id="main">
  <div id="control-panel">
    <label for="slider-angle">slope θ</label>
    <input type="range" id="slider-angle" min="10" max="80" step="1" value="30">
    <div id="animation-controls">
      <p>release block</p>
      <button id="btn-play">▶</button>
      <button id="btn-pause">||</button>
      <button id="btn-reset">|<</button>
    </div>
  </div>
  <div id="visualization-area">
    <p id="info-display"></p>
    <canvas id="p5-canvas"></canvas>
  </div>
</div>

<script>
/* ==================== GLOBAL STATE ==================== */
let theta = 30;           // angle in degrees, initial value
let thetaRad = degreesToRadians(theta);
const g = 9.81;           // m/s^2
const planeLength = 300;  // length of incline in pixels
let s = 0;                // distance travelled along the plane (0 at top)
let startTime = 0;        // time at which animation was started/resumed
let elapsedTime = 0;      // elapsed time when paused
let isPlaying = false;    // animation state
let blockWidth = 40;
let blockHeight = 20;
let canvas;
let ctx;

/* ==================== HELPERS ==================== */
function degToRad(d){return d*Math.PI/180;}
function radToDeg(r){return r*180/Math.PI;}
function degreesToRadians(d){return d*Math.PI/180;}
function updateInfoDisplay(){
  const a = g*Math.sin(thetaRad);
  const aVal = a.toFixed(3);
  const text = `slope = \\(\\theta\\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${aVal} m/s²`;
  const el = document.getElementById('info-display');
  el.innerHTML = text;
  MathJax.typesetPromise([el]);
}
function drawArrow(x1, y1, x2, y2, color, label){
  const ctx = canvas.getContext('2d');
  const headLen = 10;
  const headAngle = Math.PI/6;
  const dx = x2-x1;
  const dy = y2-y1;
  const len = Math.hypot(dx,dy);
  const angle = Math.atan2(dy,dx);
  // line
  ctx.strokeStyle = color;
  ctx.lineWidth = 2;
  ctx.setLineDash([]);
  ctx.beginPath();
  ctx.moveTo(x1, y1);
  ctx.lineTo(x2, y2);
  ctx.stroke();
  // arrowhead
  ctx.beginPath();
  ctx.moveTo(x2, y2);
  ctx.lineTo(x2-headLen*Math.cos(angle-headAngle), y2-headLen*Math.sin(angle-headAngle));
  ctx.lineTo(x2-headLen*Math.cos(angle+headAngle), y2-headLen*Math.sin(angle+headAngle));
  ctx.closePath();
  ctx.fillStyle = color;
  ctx.fill();
  // label
  if(label){
    ctx.font = '14px sans-serif';
    ctx.fillStyle = 'black';
    ctx.fillText(label, (x1+x2)/2, (y1+y2)/2);
  }
}
function clamp(val, min, max){
  return Math.max(min, Math.min(max,val));
}

/* ==================== EVENT LISTENERS ==================== */
document.getElementById('slider-angle').addEventListener('input', function(){
  theta = parseInt(this.value);
  thetaRad = degreesToRadians(theta);
  s = 0;
  elapsedTime = 0;
  isPlaying = false;
  startTime = 0;
  updateInfoDisplay();
});
document.getElementById('btn-play').addEventListener('click', function(){
  if(isPlaying) return;
  isPlaying = true;
  startTime = millis() - elapsedTime*1000;
});
document.getElementById('btn-pause').addEventListener('click', function(){
  if(!isPlaying) return;
  isPlaying = false;
  elapsedTime = (millis() - startTime)/1000;
});
document.getElementById('btn-reset').addEventListener('click', function(){
  isPlaying = false;
  s = 0;
  elapsedTime = 0;
  startTime = 0;
});

/* ==================== P5.js SKETCH ==================== */
function setup(){
  canvas = createCanvas(800, 600);
  canvas.parent('visualization-area');
  ctx = canvas.canvas.getContext('2d');
  noStroke();
  updateInfoDisplay();
}
function draw(){
  background(255);
  // compute acceleration
  const a = g * Math.sin(thetaRad);
  // animate if playing
  if(isPlaying){
    const currentTime = (millis() - startTime)/1000;
    s = 0.5 * a * currentTime * currentTime;
    if(s >= planeLength){
      s = planeLength;
      isPlaying = false;
    }
  }
  // compute corner positions
  const h = height - 100; // bottom y for plane
  const originX = 200;
  const originY = h;
  const cosT = Math.cos(thetaRad);
  const sinT = Math.sin(thetaRad);
  // direction vector along plane from top to bottom (since top is at originX + L*cos, originY - L*sin)
  const topX = originX + planeLength * cosT;
  const topY = originY - planeLength * sinT;
  const corrX = originX + (planeLength - s) * cosT;
  const corrY = originY - (planeLength - s) * sinT;
  // Draw plane
  fill('#add8e6');
  stroke('#ffd700');
  strokeWeight(2);
  beginShape();
  vertex(topX, topY);
  vertex(originX, originY);
  vertex(originX, originY + 20); // small vertical depth
  vertex(topX, topY + 20);
  endShape(CLOSE);
  // Draw block
  push();
  translate(corrX, corrY);
  rotate(-thetaRad);
  fill('#555555');
  stroke('#000');
  rectMode(CENTER);
  rect(0, 0, blockWidth, blockHeight);
  pop();
  // Draw axes
  let axisLength = 80;
  // x-axis along plane direction
  let axX1 = corrX + cosT * (blockWidth/2 + 5);
  let ayX1 = corrY - sinT * (blockWidth/2 + 5);
  let axX2 = axX1 + cosT * axisLength;
  let ayX2 = ayX1 - sinT * axisLength;
  drawArrow(axX1, ayX1, axX2, ayX2, 'black', 'x');
  // y-axis perpendicular to plane
  let nxX1 = corrX + sinT * (blockWidth/2 + 5);
  let nyX1 = corrY + cosT * (blockWidth/2 + 5);
  let nxX2 = nxX1 - sinT * axisLength;
  let nyX2 = nyX1 + cosT * axisLength;
  drawArrow(nxX1, nyX1, nxX2, nyX2, 'black', 'y');
  // Forces
  // Gravity vector: downwards (positive y)
  let gX1 = corrX;
  let gY1 = corrY;
  let gX2 = corrX;
  let gY2 = corrY + 80;
  drawArrow(gX1, gY1, gX2, gY2, 'red', 'F_grav=mg');
  // Normal force (perpendicular to plane)
  let normalMag = 80 * Math.cos(thetaRad);
  let nX2 = corrX + sinT * normalMag;
  let nY2 = corrY + cosT * normalMag;
  drawArrow(gX1, gY1, nX2, nY2, 'deepskyblue', 'normal');
  // Net force down the plane
  let netMag = 80 * Math.sin(thetaRad);
  let nfX2 = corrX + cosT * netMag;
  let nfY2 = corrY - sinT * netMag;
  drawArrow(gX1, gY1, nfX2, nfY2, 'purple', 'F_net=mg sinθ');
  // Decompose gravity into parallel & perpendicular (dashed)
  ctx.setLineDash([5,5]);
  let gParX = corrX + cosT * 80;
  let gParY = corrY - sinT * 80;
  let gPerpX = corrX + sinT * 80;
  let gPerpY = corrY + cosT * 80;
  drawArrow(gX1, gY1, gParX, gParY, 'black', '');
  drawArrow(gX1, gY1, gPerpX, gPerpY, 'black', '');
  ctx.setLineDash([]);
  // label components
  drawArrow(gParX, gParY, gParX + 30, gParY + 30, 'black', 'mg sinθ');
  drawArrow(gPerpX, gPerpY, gPerpX - 30, gPerpY - 30, 'black', 'mg cosθ');
}
function millis(){
  return (new Date()).getTime();
}
</script>
