const SHIFTS = [1,1,2,2,2,2,2,2,1,2,2,2,2,2,2,1];

/* Utility Functions */
function permute(bits, table) {
    return table.map(pos => bits[pos - 1]).join('');
}
function leftShift(bits, n) {
    return bits.slice(n) + bits.slice(0, n);
}
function xorBits(a, b) {
    return a.split('').map((bit, i) => bit ^ b[i]).join('');
}
function toHex(bits) {
    return parseInt(bits, 2).toString(16).padStart(16, '0').toUpperCase();
}

/* Key Schedule */
function generateSubkeys(key64) {
    const key56 = permute(key64, PC1);
    let left = key56.slice(0, 28);
    let right = key56.slice(28, 56);
    const subkeys = [];
    for (let i = 0; i < 16; i++) {
        left = leftShift(left, SHIFTS[i]);
        right = leftShift(right, SHIFTS[i]);
        const combined = left + right;
        subkeys.push(permute(combined, PC2));
    }
    return subkeys;
}

/* S-box substitution */
function sBoxReplace(bits4, sboxIndex) {
    const row = (parseInt(bits4[0] + bits4[5], 2));
    const col = parseInt(bits4.slice(1,5), 2);
    const val = SBOXES[sboxIndex][row][col];
    return val.toString(2).padStart(4, '0');
}

/* Function f */
function f(right48, subkey48) {
    const expanded = permute(right48, E);
    const xorResult = xorBits(expanded, subkey48);
    let out = '';
    for (let i = 0; i < 8; i++) {
        out += sBoxReplace(xorResult.slice(i * 6, i * 6 + 6), i);
    }
    return permute(out, P);
}

/* DES encryption of one block with variable rounds */
function encryptBlock(block64, subkeys, rounds) {
    let bits = permute(block64, IP);
    let L = bits.slice(0, 32);
    let R = bits.slice(32, 64);
    for (let i = 0; i < rounds && i < 16; i++) {
        const temp = R;
        R = xorBits(L, f(R, subkeys[i]));
        L = temp;
    }
    const preoutput = R + L; // R and L swapped as per DES spec
    return permute(preoutput, FP);
}

/* Convert string to binary, padded to length multiples of 64 */
function stringToBinary(str, targetLength) {
    let bin = '';
    for (const ch of str) {
        bin += ch.charCodeAt(0).toString(2).padStart(8, '0');
    }
    while (bin.length < targetLength) {
        bin += '0';
    }
    return bin;
}

/* Main Update Function */
function updateDisplay() {
    const msg = document.getElementById('input-message').value || '';
    const key = document.getElementById('input-key').value || '';
    const steps = parseInt(document.querySelector('input[name="steps"]:checked').value);

    /* Process message */
    const binMsg = stringToBinary(msg, Math.ceil(msg.length * 8 / 64) * 64);
    const blocks = [];
    for (let i = 0; i < binMsg.length; i += 64) {
        blocks.push(binMsg.slice(i, i + 64));
    }

    /* Process key */
    const binKey = stringToBinary(key, 64);
    const subkeys = generateSubkeys(binKey);

    /* Generate output lines */
    const outEl = document.getElementById('output-results');
    const lines = [];
    for (let step = 2; step <= steps; step += 2) {
        let ciphertext = '';
        for (const block of blocks) {
            const encBlock = encryptBlock(block, subkeys, step);
            ciphertext += toHex(encBlock);
        }
        const stepStr = step.toString().padStart(2, ' ');
        lines.push(`${stepStr}  ${ciphertext}`);
    }
    outEl.textContent = lines.join('\n');
}

/* Event Listeners */
document.getElementById('input-message').addEventListener('input', updateDisplay);
document.getElementById('input-key').addEventListener('input', updateDisplay);
document.querySelectorAll('input[name="steps"]').forEach(radio => {
    radio.addEventListener('change', updateDisplay);
});

/* Initial Run */
window.addEventListener('load', updateDisplay);
</script>
