<body>

<div id="control-panel">
  <div id="elements-control">
    <label for="slider-elements">elements (set, then click "randomize")</label>
    <input type="range" id="slider-elements" min="2" max="50" step="1" value="10">
    <span id="elements-value">10</span>
  </div>

  <div id="step-control">
    <label for="slider-step">step</label>
    <input type="range" id="slider-step" min="1" max="1" step="1" value="1">
    <button id="btn-play-pause">▶</button>
    <button id="btn-step-backward">-</button>
    <button id="btn-step-forward">+</button>
    <button id="btn-reset-step">«</button>
    <span id="step-value">1</span>
  </div>

  <div id="sort-control">
    <label>sort</label>
    <button id="btn-shellsort" class="sort-btn active">shellsort</button>
    <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
    <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
    <button id="btn-cocktail" class="sort-btn">cocktail</button>
    <button id="btn-bogosort" class="sort-btn">bogosort</button>
    <button id="btn-randomize">randomize</button>
  </div>
</div>

<div id="visualization-area">
  <canvas id="bar-chart-canvas"></canvas>
</div>

<script>
/*=== Application State ===*/
const app = {
  currentAlgorithm: 'shellsort',
  numElements: 10,
  sortHistory: [],
  currentStep: 1,
  isPlaying: false,
  playInterval: null,
};

/*=== Utility Functions ===*/
function shuffledArray(n) {
  const arr = [...Array(n).keys()].map(i => i + 1);
  for (let i = n - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

function isSorted(arr) {
  for (let i = 1; i < arr.length; i++) {
    if (arr[i] < arr[i - 1]) return false;
  }
  return true;
}

/*=== Sorting Algorithms with History Capture ===*/
function generateSortHistory(arr, algo) {
  const history = [];
  const copy = arr.slice();
  history.push(copy.slice()); // step 0
  let steps = 0;
  const N = copy.length;

  const record = () => history.push(copy.slice());

  if (algo === 'shellsort') {
    let gap = Math.floor(N / 2);
    while (gap > 0) {
      for (let i = gap; i < N; i++) {
        const temp = copy[i];
        let j = i;
        while (j >= gap && copy[j - gap] > temp) {
          copy[j] = copy[j - gap];
          j -= gap;
          record();
          steps++;
        }
        copy[j] = temp;
        record();
        steps++;
      }
      gap = Math.floor(gap / 2);
    }
  } else if (algo === 'bubblesort') {
    for (let i = 0; i < N - 1; i++) {
      for (let j = 0; j < N - i - 1; j++) {
        if (copy[j] > copy[j + 1]) {
          [copy[j], copy[j + 1]] = [copy[j + 1], copy[j]];
          record();
          steps++;
        }
      }
    }
  } else if (algo === 'insertionsort') {
    for (let i = 1; i < N; i++) {
      const key = copy[i];
      let j = i - 1;
      while (j >= 0 && copy[j] > key) {
        copy[j + 1] = copy[j];
        j--;
        record();
        steps++;
      }
      copy[j + 1] = key;
      record();
      steps++;
    }
  } else if (algo === 'cocktail') {
    let start = 0, end = N - 1, swapped = true;
    while (swapped) {
      swapped = false;
      for (let i = start; i < end; i++) {
        if (copy[i] > copy[i + 1]) {
          [copy[i], copy[i + 1]] = [copy[i + 1], copy[i]];
          record();
          swapped = true;
          steps++;
        }
      }
      if (!swapped) break;
      swapped = false;
      end--;
      for (let i = end; i > start; i--) {
        if (copy[i] < copy[i - 1]) {
          [copy[i], copy[i - 1]] = [copy[i - 1], copy[i]];
          record();
          swapped = true;
          steps++;
        }
      }
      start++;
    }
  } else if (algo === 'bogosort') {
    let maxSteps = 10000;
    while (!isSorted(copy) && steps < maxSteps) {
      shuffledArray(copy.length).forEach((v, idx) => copy[idx] = v);
      record();
      steps++;
    }
  }

  return history;
}

/*=== UI Components ===*/
const sliders = {
  elements: document.getElementById('slider-elements'),
  step: document.getElementById('slider-step')
};

const spans = {
  elements: document.getElementById('elements-value'),
  step: document.getElementById('step-value')
};

const buttons = {
  playPause: document.getElementById('btn-play-pause'),
  stepBackward: document.getElementById('btn-step-backward'),
  stepForward: document.getElementById('btn-step-forward'),
  resetStep: document.getElementById('btn-reset-step'),
  randomize: document.getElementById('btn-randomize'),
  shellsort: document.getElementById('btn-shellsort'),
  bubblesort: document.getElementById('btn-bubblesort'),
  insertionsort: document.getElementById('btn-insertionsort'),
  cocktail: document.getElementById('btn-cocktail'),
  bogosort: document.getElementById('btn-bogosort')
};

const sortButtons = [
  buttons.shellsort,
  buttons.bubblesort,
  buttons.insertionsort,
  buttons.cocktail,
  buttons.bogosort
];

/*=== Initial Setup ===*/
function init() {
  setAlgorithm('shellsort');
  randomizeData(); // generate initial data
}
init();

/*=== Event Handlers ===*/
sliders.elements.addEventListener('input', () => {
  spans.elements.textContent = sliders.elements.value;
  if (app.currentAlgorithm === 'bogosort') {
    sliders.elements.value = 7;
    spans.elements.textContent = 7;
  }
});

buttons.randomize.addEventListener('click', () => randomizeData());

sortButtons.forEach(btn => {
  btn.addEventListener('click', () => setAlgorithm(btn.id.split('-').pop()));
});

sliders.step.addEventListener('input', () => {
  app.currentStep = parseInt(sliders.step.value);
  spans.step.textContent = app.currentStep;
  redraw();
});

buttons.playPause.addEventListener('click', togglePlayPause);
buttons.stepBackward.addEventListener('click', () => step(-1));
buttons.stepForward.addEventListener('click', () => step(1));
buttons.resetStep.addEventListener('click', () => stepReset());

function togglePlayPause() {
  if (!app.isPlaying) {
    app.isPlaying = true;
    buttons.playPause.textContent = '❚❚';
    app.playInterval = setInterval(() => {
      if (app.currentStep < sliders.step.max) {
        step(1);
      } else {
        clearInterval(app.playInterval);
        app.isPlaying = false;
        buttons.playPause.textContent = '▶';
      }
    }, 200);
  } else {
    app.isPlaying = false;
    buttons.playPause.textContent = '▶';
    clearInterval(app.playInterval);
  }
}

function step(delta) {
  const newStep = Math.max(1, Math.min(sliders.step.max, app.currentStep + delta));
  sliders.step.value = newStep;
  app.currentStep = newStep;
  spans.step.textContent = newStep;
  redraw();
}

function stepReset() {
  sliders.step.value = 1;
  app.currentStep = 1;
  spans.step.textContent = 1;
  redraw();
}

function setAlgorithm(name) {
  app.currentAlgorithm = name;
  sortButtons.forEach(btn => btn.classList.remove('active'));
  buttons[`btn-${name}`].classList.add('active');
  if (name === 'bogosort') {
    sliders.elements.disabled = true;
    sliders.elements.value = 7;
    spans.elements.textContent = 7;
    app.numElements = 7;
  } else {
    sliders.elements.disabled = false;
  }
  randomizeData();
}

/*=== Data Preparation ===*/
function randomizeData() {
  clearInterval(app.playInterval);
  app.isPlaying = false;
  buttons.playPause.textContent = '▶';

  app.numElements = parseInt(sliders.elements.value);
  const arr = shuffledArray(app.numElements);
  app.sortHistory = generateSortHistory(arr, app.currentAlgorithm);

  // Update step slider
  sliders.step.max = app.sortHistory.length - 1;
  sliders.step.value = 1;
  app.currentStep = 1;
  spans.step.textContent = 1;

  redraw();
}

/*=== p5.js Sketch ===*/
let myCanvas;

function setup() {
  myCanvas = createCanvas(800, 400);
  myCanvas.parent('visualization-area');
  colorMode(HSB, 360, 100, 100, 1);
  noLoop();
}

function draw() {
  background(255);
  renderBars();
  renderYAxis();
}

function renderBars() {
  const arr = app.sortHistory[app.currentStep];
  const N = arr.length;
  const marginLeft = 40;
  const marginRight = 20;
  const marginTop = 20;
  const marginBottom = 30;
  const chartW = width - marginLeft - marginRight;
  const chartH = height - marginTop - marginBottom;
  const barW = chartW / N;
  const maxVal = N;
  stroke(0);
  for (let i = 0; i < N; i++) {
    const v = arr[i];
    const barH = map(v, 1, maxVal, 0, chartH);
    const x = marginLeft + i * barW;
    const y = height - marginBottom - barH;
    const hue = map(v, 1, maxVal, 240, 0);
    fill(hue, 80, 90);
    rect(x, y, barW * 0.9, barH);
  }
}

function renderYAxis() {
  const marginLeft = 40;
  const marginTop = 20;
  const marginBottom = 30;
