<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
<script>
  // Default Values
  const defaults = {
    x1: 500,
    v1: -10.0,
    x2: 100,
    v2: 5.0
  };

  // Element References
  const sliders = {
    x1: document.getElementById('slider-x1'),
    v1: document.getElementById('slider-v1'),
    x2: document.getElementById('slider-x2'),
    v2: document.getElementById('slider-v2')
  };
  const displays = {
    x1: document.getElementById('value-x1'),
    v1: document.getElementById('value-v1'),
    x2: document.getElementById('value-x2'),
    v2: document.getElementById('value-v2')
  };
  const resultText = document.getElementById('result-text');
  const resetBtn = document.getElementById('btn-reset');

  // Helper Functions
  function formatVal(val, isVelocity = false) {
    return isVelocity ? Number(val).toFixed(2) : Math.round(val).toString();
  }

  function updateDisplay(slider, span, isVel = false) {
    span.textContent = formatVal(slider.value, isVel);
  }

  function computeIntersection(x1, v1, x2, v2) {
    if (v1 === v2) return null; // Parallel
    const T = (x1 - x2) / (v2 - v1);
    if (T < 0 || T > 1000) return null; // Out of time range
    const X = x1 + v1 * T;
    return { T, X };
  }

  function renderPlot(x1, v1, x2, v2, inter) {
    const trace1 = {
      x: [0, 1000],
      y: [x1, x1 + v1 * 1000],
      mode: 'lines',
      line: { color: 'rgb(70, 130, 180)' },
      hoverinfo: 'none'
    };
    const trace2 = {
      x: [0, 1000],
      y: [x2, x2 + v2 * 1000],
      mode: 'lines',
      line: { color: 'rgb(255, 165, 0)' },
      hoverinfo: 'none'
    };
    const trace3 = inter ? {
      x: [inter.T],
      y: [inter.X],
      mode: 'markers',
      marker: { color: 'rgb(0,200,0)', size: 12, line: { color: 'rgb(0,100,0)', width: 2 } },
      hoverinfo: 'none'
    } : { x: [], y: [], mode: 'markers', hoverinfo: 'none' };

    const data = [trace1, trace2, trace3];

    const layout = {
      xaxis: { title: 't (s)', range: [0, 1000], gridcolor: '#d3d3d3', zerolinecolor: '#a9a9a9' },
      yaxis: { title: 'distance (m)', autorange: true, gridcolor: '#d3d3d3', zerolinecolor: '#a9a9a9' },
      showlegend: false,
      margin: { t: 20, r: 20, b: 40, l: 60 }
    };

    Plotly.react('plot-container', data, layout, { responsive: true });
  }

  function updateAll() {
    const x1 = Number(sliders.x1.value);
    const v1 = Number(sliders.v1.value);
    const x2 = Number(sliders.x2.value);
    const v2 = Number(sliders.v2.value);

    const inter = computeIntersection(x1, v1, x2, v2);

    if (!inter) {
      resultText.textContent = "The runners' paths never intersect";
    } else {
      resultText.textContent = `T = ${inter.T.toFixed(2)} s | X = ${inter.X.toFixed(2)} m`;
    }

    renderPlot(x1, v1, x2, v2, inter);
  }

  // Event Listeners
  Object.entries(sliders).forEach(([key, slider]) => {
    const span = displays[key];
    const isVel = key.startsWith('v');
    slider.addEventListener('input', () => {
      updateDisplay(slider, span, isVel);
      updateAll();
    });
  });

  resetBtn.addEventListener('click', () => {
    sliders.x1.value = defaults.x1;
    sliders.v1.value = defaults.v1;
    sliders.x2.value = defaults.x2;
    sliders.v2.value = defaults.v2;
    // Update displays
    updateAll();
  });

  // Initialize
  window.addEventListener('load', () => {
    updateAll();
  });
</script>
</body>
</html>
