<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Phosphorus Cycle Visualization</title>
<style>
  body{font-family:Arial,Helvetica,sans-serif;margin:0;padding:0;background:#f0f0f0;}
  #control-panel{margin:20px;display:flex;align-items:center;gap:10px;}
  #canvas-container{border:1px solid #ccc;background:#fff;}
</style>
</head>
<body>
<div>
  <div id="control-panel">
    <label for="slider-phase">phosphorus cycle</label>
    <input type="range" id="slider-phase" min="0" max="8" step="1" value="0">
    <span id="slider-value">0</span>
    <button id="btn-reset">↻ Reset</button>
  </div>
  <div id="canvas-container"></div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
<script>
let sliderPhase, sliderValueSpan, btnReset;
let labels = [];                 // array of label objects {x, y, w, h, id}
let tooltipText = '';
let tooltipVisible = false;

// -------------------------------------------------------
// Setup and Draw
// -------------------------------------------------------
function setup() {
  createCanvas(600, 500);
  canvas.parent('canvas-container');

  sliderPhase = select('#slider-phase');
  sliderValueSpan = select('#slider-value');
  btnReset = select('#btn-reset');

  sliderPhase.input(() => sliderValueSpan.html(sliderPhase.value()));
  btnReset.mousePressed(() => {
    sliderPhase.value(0);
    sliderValueSpan.html(0);
  });

  textFont('Arial');
  textSize(14);
}

function draw() {
  background('#CAE9FF');          // sky
  fill('#9B7653');               // ground
  rect(0, 250, 600, 250);

  drawSun();
  drawClouds();
  drawRocks();
  drawAnimal();
  drawPlants();
  drawWater();

  // ----- Dynamic Process Arrows and Labels -----
  const phase = parseInt(sliderPhase.value(), 10);
  if (phase >= 1) drawArrowErosion();
  if (phase >= 2) drawArrowRunoff();
  if (phase >= 3) drawArrowSoil();
  if (phase >= 4) drawArrowLeaching();
  if (phase >= 5) drawArrowPlantUptake();
  if (phase >= 6) drawArrowAnimalPlants();
  if (phase >= 7) drawArrowDecomposition();
  if (phase >= 8) drawArrowGeologicUplift();

  // ----- Build labels array for hit‑testing -----
  buildLabelsArray(phase);

  // ----- Hover tooltip ---
  let hover = false;
  tooltipText = '';
  for (let lbl of labels) {
    if (mouseX >= lbl.x && mouseX <= lbl.x + lbl.w &&
        mouseY >= lbl.y && mouseY <= lbl.y + lbl.h) {
      hover = true;
      tooltipText = tooltipContent(lbl.id);
      break;
    }
  }
  tooltipVisible = hover;
  if (tooltipVisible) {
    fill(255, 255, 200, 200);
    noStroke();
    const tx = mouseX + 15;
    const ty = mouseY + 15;
    const tw = textWidth(tooltipText) + 10;
    const th = 20;
    rect(tx, ty, tw, th, 5);
    fill(0);
    text(tooltipText, tx + 5, ty + 15);
  }
}

// -------------------------------------------------------
// Drawing primitives
// -------------------------------------------------------
function drawSun() {
  fill('#FFDD00');
  ellipse(550, 75, 80, 80);
  stroke('#FFDD00');
  for (let i = 0; i < 8; i++) {
    const a = PI / 4 + i * PI / 4;
    const x1 = 550 + cos(a) * 40;
    const y1 = 75 + sin(a) * 40;
    const x2 = 550 + cos(a) * 80;
    const y2 = 75 + sin(a) * 80;
    line(x1, y1, x2, y2);
  }
  noStroke();
}

function drawClouds() {
  fill('#E0E0E0');
  ellipse(140, 80, 80, 50);
  ellipse(170, 80, 90, 60);
  ellipse(200, 85, 70, 50);
  fill('#505050');
  textSize(16);
  textAlign(CENTER, CENTER);
  text('Clouds', 170, 80);
  stroke('#0000FF');
  for (let i = 0; i < 5; i++) {
    const x = 140 + i * 10;
    line(x, 110, x + 4, 140);
  }
  noStroke();
}

function drawRocks() {
  fill('#5A5A5A');
  ellipse(80, 350, 120, 70);
  ellipse(120, 360, 100, 60);
  fill('#000');
  textSize(16);
  textAlign(CENTER, CENTER);
  text('Rocks', 100, 370);
}

function drawAnimal() {
  fill('#FFFFFF');
  stroke('#000000');
  ellipse(300, 360, 80, 40);    // body
  ellipse(310, 330, 30, 30);    // head
  rect(270, 380, 10, 30);       // leg left
  rect(310, 380, 10, 30);       // leg right
  fill('#000000');
  ellipse(290, 350, 10, 10);    // spot
  ellipse(320, 345, 8, 8);      // spot
  fill('#0000FF');
  ellipse(305, 325, 5, 5);      // eye
  fill('#000');
  textSize(16);
  textAlign(CENTER, CENTER);
  text('Animals', 300, 410);
}

function drawPlants() {
  fill('#90EE90');
  triangle(350, 360, 340, 380, 360, 380);
  triangle(375, 360, 365, 380, 385, 380);
  triangle(400, 360, 390, 380, 410, 380);
  fill('#006400');
  textSize(16);
  textAlign(CENTER, CENTER);
  text('Plants', 460, 360);
}

function drawWater() {
  fill(0, 0, 255, 150);
  ellipse(200, 440, 120, 80);
  ellipse(240, 460, 120, 80);
  fill('#00008B');
  textSize(16);
  textAlign(CENTER, CENTER);
  text('Water', 220, 425);
}

// -------------------------------------------------------
// Arrows
// -------------------------------------------------------
function drawArrow(start, end, label) {
  stroke(0);
  line(start.x, start.y, end.x, end.y);
  const ang = atan2(end.y - start.y, end.x - start.x);
  drawArrowHead(end.x, end.y, ang);
  fill(0);
  textSize(14);
  text(label, (start.x + end.x) / 2 - 20, (start.y + end.y) / 2);
}

function drawArrowHead(x, y, angle) {
  push();
  translate(x, y);
  rotate(angle);
  fill(0);
  triangle(0, 0, -8, -4, -8, 4);
  pop();
}

function drawArrowErosion()  { drawArrow(createVector(140,110), createVector(80,330),  "Erosion"); }
function drawArrowRunoff()   { drawArrow(createVector(80,350),  createVector(150,435), "Runoff");  }
function drawArrowSoil()     { drawArrow(createVector(150,430), createVector(260,400), "Soil");     }
function drawArrowLeaching() { drawArrow(createVector(260,400), createVector(150,430), "Leaching"); }
function drawArrowPlantUptake()   { drawArrow(createVector(350,380), createVector(350,350), "Plant uptake"); }
function drawArrowAnimalPlants() { drawArrow(createVector(360,380), createVector(320,360), "Animals eat plants"); }
function drawArrowDecomposition(){ drawArrow(createVector(320,360), createVector(260,400), "Decomposition"); }

function drawArrowGeologicUplift() {
  noFill();
  stroke(0);
  beginShape();
  vertex(140, 440);
  quadraticVertex(100, 380, 80, 330);
  endShape();
  const ang = atan2(330 - 380, 80 - 100);
  drawArrowHead(80, 330, ang);
}

// -------------------------------------------------------
// Build labels array for hit‑testing
// -------------------------------------------------------
function buildLabelsArray(phase) {
  labels = [];
  // Static labels
  labels.push({x: 140, y: 70, w: 100, h: 20, id: 'clouds'});
  labels.push({x: 80, y: 360, w: 100, h: 20, id: 'rocks'});
  labels.push({x: 290, y: 410, w: 120, h: 20, id: 'animals'});
  labels.push({x: 460, y: 360, w: 100, h: 20, id: 'plants'});
  labels.push({x: 210, y: 425, w: 120, h: 20, id: 'water'});

  // Dynamic labels
  if (phase >= 1) labels.push({x: 260, y: 160, w: 80, h: 20, id: 'erosion'});
  if (phase >= 2) labels.push({x: 190, y: 430, w: 80, h: 20, id: 'runoff'});
  if (phase >= 3) labels.push({x: 115, y: 410, w: 80, h: 20, id: 'soil'});
  if (phase >= 4) labels.push({x: 170, y: 405, w: 90, h: 20, id: 'leaching'});
  if (phase >= 5) labels.push({x: 340, y: 335, w: 120, h: 20, id: 'plantuptake'});
  if (phase >= 6) labels.push({x: 340, y: 360, w: 140, h: 20, id: 'animalsplants'});
  if (phase >= 7) labels.push({x: 270, y: 385, w: 120, h: 20, id: 'decomposition'});
  if (phase >= 8) labels.push({x: 70, y: 330, w: 160, h: 20, id: 'geologicuplift'});
}

// -------------------------------------------------------
// Tooltip text
// -------------------------------------------------------
function tooltipContent(id) {
  const map = {
    clouds:         "Water vapor condensed in the atmosphere.",
    rocks:          "A primary reservoir of phosphorus in the form of phosphate.",
    animals:        "Obtain phosphorus by eating plants or other animals.",
    plants:         "Absorb phosphate from the soil to build organic molecules.",
    water:          "Phosphorus is carried into water bodies by runoff and leaching.",
    erosion:        "Weathering of rocks by wind and water releases phosphate.",
    runoff:         "Phosphate from fertilizers and rocks is washed into rivers and oceans.",
    soil:           "Phosphorus in the soil is available for plants.",
    leaching:       "Phosphates in the soil are dissolved in water and carried away.",
    plantuptake:    "Plants absorb inorganic phosphate from the soil through their roots.",
    animalsplants:  "Transfers phosphorus from plants to animals along the food chain.",
    decomposition:  "Bacteria break down dead organic matter, returning phosphate to the soil and water.",
    geologicuplift: "Over millions of years, ocean sediments containing phosphate are lifted up to form new land."
  };
  return map[id] || '';
}
</script>
</body>
</html>
