    if (!step.matching.includes(idx)) {
      const v0 = data.vertices[edge.u];
      const v1 = data.vertices[edge.v];
      line(v0.x, v0.y, v1.x, v1.y);
    }
  });

  // Draw matched edges
  const color = step.isFinal ? '#4d4dff' : '#ff4d4d';
  strokeWeight(6);
  stroke(color);
  step.matching.forEach(idx=>{
    const e = data.edges[idx];
    const v0=data.vertices[e.u];
    const v1=data.vertices[e.v];
    line(v0.x, v0.y, v1.x, v1.y);
  });

  // Draw blossom if any
  if (step.blossom) {
    fill('rgba(0,0,255,0.3)');
    stroke('#000');
    strokeWeight(1);
    beginShape();
    step.blossom.vertices.forEach(vIdx=>{
      const v = data.vertices[vIdx];
      vertex(v.x, v.y);
    });
    endShape(CLOSE);
    // Super-vertex
    const top = data.vertices[step.blossom.topVertex];
    fill('#ffff00');
    stroke('#000');
    strokeWeight(1);
    ellipse(top.x, top.y, 16, 16);
  }

  // Draw vertices
  stroke(0);
  strokeWeight(1);
  fill(0);
  data.vertices.forEach(v=>{
    ellipse(v.x, v.y, 8, 8);
  });
}
</script>
</body>
</html>
