<script type="module">
  import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
  import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
  import { CSG } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

  // Global variables
  let scene, camera, renderer, controls;
  let mainCylinderMesh, drillMesh, resultMesh, frontTubeMesh, backTubeMesh, ghostMesh;
  const canvas = document.getElementById('main-canvas');

  // Helper: format two decimals
  const format = num => num.toFixed(2);

  // Update span text content
  const updateSpan = (id, value) => { document.getElementById(id).innerText = format(value); }

  // Create basic cylinder mesh
  const createCylinder = (radius, height, color, opacity = 1) => {
    const geometry = new THREE.CylinderGeometry(radius, radius, height, 64);
    const material = new THREE.MeshStandardMaterial({color, opacity, transparent: opacity < 1, roughness:0.5, metalness:0.1});
    return new THREE.Mesh(geometry, material);
  };

  // Create drill cylinder
  const createDrill = (radius, height) => {
    const geometry = new THREE.CylinderGeometry(radius, radius, height, 64);
    const material = new THREE.MeshStandardMaterial({color:0x555555, roughness:0.5, metalness:0.1});
    return new THREE.Mesh(geometry, material);
  };

  // Create boundary tubes (two curves)
  const createBoundaryTubes = (mainRadius, drill) => {
    const drillMat = drill.matrixWorld;
    const drillDir = new THREE.Vector3(0,1,0).applyQuaternion(drill.quaternion);
    const drillCenter = new THREE.Vector3();
    drillCenter.setFromMatrixPosition(drillMat);

    const frontPts = [];
    const backPts = [];

    const nSamples = 360;
    const rD = drill.geometry.parameters.radiusTop; // drill radius uniform
    for(let i=0; i<nSamples; i++){
      const theta = (i/nSamples)*Math.PI*2;
      const xLocal = rD*Math.cos(theta);
      const zLocal = rD*Math.sin(theta);

      const M = drillMat.elements;
      const a1 = M[4];
      const b1 = M[0]*xLocal + M[8]*zLocal + M[12];
      const a3 = M[6];
      const b3 = M[2]*xLocal + M[10]*zLocal + M[14];

      const A = a1*a1 + a3*a3;
      const B = 2*(a1*b1 + a3*b3);
      const C = b1*b1 + b3*b3 - mainRadius*mainRadius;

      const disc = B*B - 4*A*C;
      if(disc < 0) continue;
      const sqrtD = Math.sqrt(disc);
      const y1 = (-B + sqrtD) / (2*A);
      const y2 = (-B - sqrtD) / (2*A);

      [y1, y2].forEach(y=> {
        const local = new THREE.Vector3(xLocal, y, zLocal);
        const global = local.clone().applyMatrix4(drillMat);
        const dirVec = global.clone().sub(drillCenter);
        const side = dirVec.dot(drillDir) >= 0 ? frontPts : backPts;
        side.push(global);
      });
    }

    const tubeRad = 0.05;
    const frontCurve = new THREE.CatmullRomCurve3(frontPts, true, 'catmullrom', 0.1);
    const backCurve  = new THREE.CatmullRomCurve3(backPts, true, 'catmullrom', 0.1);
    const geometry1 = new THREE.TubeGeometry(frontCurve, 200, tubeRad, 8, false);
    const geometry2 = new THREE.TubeGeometry(backCurve, 200, tubeRad, 8, false);

    const matFront = new THREE.MeshStandardMaterial({color:0xFFA500});
    const matBack  = new THREE.MeshStandardMaterial({color:0x4169E1});
    const frontMesh = new THREE.Mesh(geometry1, matFront);
    const backMesh  = new THREE.Mesh(geometry2, matBack);

    return {frontMesh, backMesh};
  };

  // Remove given mesh from scene and dispose geometry/material
  const disposeMesh = (mesh) => {
    if(mesh){
      scene.remove(mesh);
      if(mesh.geometry) mesh.geometry.dispose();
      if(mesh.material){
        if(Array.isArray(mesh.material))
          mesh.material.forEach(m=>m.dispose());
        else
          mesh.material.dispose();
      }
    }
  };

  // Main scene update
  const updateScene = () => {
    // Remove old meshes
    disposeMesh(mainCylinderMesh);
    disposeMesh(drillMesh);
    disposeMesh(resultMesh);
    disposeMesh(frontTubeMesh);
    disposeMesh(backTubeMesh);
    disposeMesh(ghostMesh);

    const radiusMain = parseFloat(document.getElementById('slider-cylinder-radius').value);
    const opacity = parseFloat(document.getElementById('slider-opacity').value);
    const radiusDrill = parseFloat(document.getElementById('slider-drill-radius').value);
    const inclination = parseFloat(document.getElementById('slider-inclination').value);
    const rotation = parseFloat(document.getElementById('slider-rotation').value);
    const offset = parseFloat(document.getElementById('slider-offset').value);
    const boundaryOnly = document.getElementById('checkbox-boundary-only').checked;

    // Create meshes
    mainCylinderMesh = createCylinder(radiusMain, 8, 0xD2B48C, opacity);
    drillMesh = createDrill(radiusDrill, 10);

    // Orientations
    drillMesh.rotation.z = Math.PI / 2; // start horizontal along X
    drillMesh.rotation.x = inclination;
    drillMesh.rotation.y = rotation;
    drillMesh.position.y = offset;
    drillMesh.updateMatrixWorld();

    // Perform CSG subtraction
    const subMesh = CSG.subtract(mainCylinderMesh, drillMesh);
    subMesh.material = mainCylinderMesh.material.clone();
    resultMesh = subMesh;

    // Prepare boundary tubes
    const {frontMesh, backMesh} = createBoundaryTubes(radiusMain, drillMesh);
    frontTubeMesh = frontMesh;
    backTubeMesh = backMesh;

    // Add meshes to scene
    if(boundaryOnly){
      resultMesh.visible = false;
      if(!ghostMesh){
        ghostMesh = createCylinder(radiusMain, 8, 0xF5F5F5, 0.15);
        ghostMesh.material.transparent = true;
      }
      ghostMesh.visible = true;
      scene.add(ghostMesh);
    }else{
      resultMesh.visible = true;
      if(ghostMesh) ghostMesh.visible = false;
    }

    scene.add(resultMesh);
    scene.add(frontTubeMesh);
    scene.add(backTubeMesh);

    // Update opacity according to checkbox
    if(boundaryOnly){
      resultMesh.material.opacity = 0.15;
      resultMesh.material.transparent = true;
    }else{
      resultMesh.material.opacity = opacity;
      resultMesh.material.transparent = opacity < 1;
    }
  };

  // Initialize scene
  const init = () => {
    scene = new THREE.Scene();
    scene.background = new THREE.Color(0xE0E0E0);

    camera = new THREE.PerspectiveCamera(45, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
    camera.position.set(10,10,10);
    camera.lookAt(0,0,0);

    renderer = new THREE.WebGLRenderer({canvas, antialias:true});
    renderer.setSize(canvas.clientWidth, canvas.clientHeight);
    renderer.setPixelRatio(window.devicePixelRatio);

    controls = new OrbitControls(camera, renderer.domElement);
    controls.update();

    // Lights
    const ambLight = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambLight);
    const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
    dirLight.position.set(5,10,7.5);
    scene.add(dirLight);

    // Setup UI events
    const sliders = ['slider-cylinder-radius','slider-opacity','slider-drill-radius',
      'slider-inclination','slider-rotation','slider-offset'];
    sliders.forEach(id=>{
      const el = document.getElementById(id);
      updateSpan(`label-${id.split('-')[1]}`, parseFloat(el.value));
      el.addEventListener('input', ()=>{
        updateSpan(`label-${id.split('-')[1]}`, parseFloat(el.value));
        updateScene();
      });
    });

    document.getElementById('checkbox-boundary-only').addEventListener('change', () => updateScene());

    // Initial render
    updateScene();
    animate();
  };

  // Render loop
  const animate = () => {
    requestAnimationFrame(animate);
    controls.update();
    renderer.render(scene, camera);
  };

  // Handle resize
  window.addEventListener('resize', () => {
    const width = canvas.clientWidth;
    const height = canvas.clientHeight;
    camera.aspect = width / height;
    camera.updateProjectionMatrix();
    renderer.setSize(width, height);
  });

  init();
</script>
</body>
</html>
