      const bri = x/p.width;
      p.fill(hueVal, satVal, bri);
      p.rect(x,0,1,p.height);
    }
    p.stroke(0);
    p.strokeWeight(1);
    p.rect(0,0,p.width,p.height,5);
  };
});

// Result bar sketch
resSketchInst = new p5(function(p){
  p.setup = function(){
    const canvas = p.createCanvas(barWidth, barHeight);
    canvas.parent('canvas-result-parent');
    p.colorMode(p.HSB,1,1,1);
