</head>
<body>
<div id="app">
  <div id="controls-container">
    <div class="control-row">
      <label for="slider-alpha">α</label>
      <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
      <span id="value-alpha">0.0500</span>
    </div>
    <div class="control-row">
      <label for="slider-beta">β</label>
      <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
      <span id="value-beta">5.00</span>
    </div>
    <div class="control-row">
      <label for="slider-gamma">γ</label>
      <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
      <span id="value-gamma">500</span>
    </div>
  </div>
  <div id="plot-container"></div>
</div>

<script>
(function(){
  const sliderAlpha = document.getElementById('slider-alpha');
  const sliderBeta  = document.getElementById('slider-beta');
  const sliderGamma = document.getElementById('slider-gamma');

  const valueAlpha = document.getElementById('value-alpha');
  const valueBeta  = document.getElementById('value-beta');
  const valueGamma = document.getElementById('value-gamma');

  function formatValues(){ 
    const a = parseFloat(sliderAlpha.value);
    const b = parseFloat(sliderBeta.value);
    const g = parseFloat(sliderGamma.value);
    valueAlpha.textContent = a.toFixed(4);
    valueBeta.textContent  = b.toFixed(2);
    valueGamma.textContent = g.toFixed(0);
  }

  function f(y0,y1,alpha,beta,gamma){
    const expTerm = Math.exp(y0/(1 + y0/alpha));
    const dDeltaT = gamma*y1*expTerm - beta*y0;
    const dCx     = 1 - y1*expTerm;
    return [dDeltaT,dCx];
  }

  function runSimulation(alpha,beta,gamma){
    const h = 0.05;
    const steps = Math.floor(20/h);
    const t = new Array(steps+1);
    const dT = new Array(steps+1);
    const Cx = new Array(steps+1);
    let y0=0, y1=0;
    for(let i=0;i<=steps;i++){
      const current = i*h;
      t[i]   = current;
      dT[i]  = y0;
      Cx[i]  = y1;
      const k1 = f(y0,y1,alpha,beta,gamma);
      const k2 = f(y0 + h*k1[0]/2, y1 + h*k1[1]/2, alpha,beta,gamma);
      const k3 = f(y0 + h*k2[0]/2, y1 + h*k2[1]/2, alpha,beta,gamma);
      const k4 = f(y0 + h*k3[0], y1 + h*k3[1], alpha,beta,gamma);
      y0 += (h/6)*(k1[0]+2*k2[0]+2*k3[0]+k4[0]);
      y1 += (h/6)*(k1[1]+2*k2[1]+2*k3[1]+k4[1]);
    }
    const CxScaled = Cx.map(v=>v*1000);
    return {t,dT,CxScaled};
  }

  function plotData(data){
    const traceDeltaT = {
      x: data.t,
      y: data.dT,
      mode:'lines',
      line:{color:'orange',width:2},
      name:'ΔT'
    };
    const traceCx = {
      x: data.t,
      y: data.CxScaled,
      mode:'lines',
      line:{color:'steelblue',width:2},
      name:'1000 Cₓ'
    };
    const layout = {
      xaxis:{title:'',range:[0,20],zeroline:true},
      yaxis:{title:'',range:[0,100],zeroline:true},
      showlegend:false,
      margin:{l:50,r:20,b:30,t:20},
      plot_bgcolor:'#f0f0f0',
      paper_bgcolor:'white',
      annotations:[
        {x:-0.05,y:100,xref:'paper',yref:'y',xanchor:'right',yanchor:'top',
         showarrow:false,font:{color:'purple'},text:'ΔT'},
        {x:-0.05,y:95,xref:'paper',yref:'y',xanchor:'right',yanchor:'top',
         showarrow:false,font:{color:'purple'},text:'1000 C<sub>X</sub>'}
      ]
    };
    Plotly.newPlot('plot-container',[traceDeltaT,traceCx],layout,{responsive:true});
  }

  function update(){
    formatValues();
    const alpha = parseFloat(sliderAlpha.value);
    const beta  = parseFloat(sliderBeta.value);
    const gamma = parseFloat(sliderGamma.value);
    const data = runSimulation(alpha,beta,gamma);
    plotData(data);
  }

  // Event listeners
  sliderAlpha.addEventListener('input', update);
  sliderBeta.addEventListener('input', update);
  sliderGamma.addEventListener('input', update);

  // Initial plot
  formatValues();
  update();
})();
</script>
</body>
</html>
