</style>
</head>
<body>
<div id="main-container">
  <div id="controls-container">
    <div class="control-group">
      <label for="slider-time">time</label>
      <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
      <span id="value-time">7.00</span>
    </div>
    <div class="control-group">
      <label for="slider-wavelength">wavelength</label>
      <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
      <span id="value-wavelength">4.00</span>
    </div>
    <div class="control-group">
      <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
      <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
      <span id="value-longitudinal-amplitude">2.00</span>
    </div>
    <div class="control-group">
      <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
      <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
      <span id="value-latitudinal-amplitude">1.00</span>
    </div>
  </div>
  <div id="canvas-container"></div>
</div>

<script src="https://cdn.jsdelivr.net/npm/p5@1.6.0/lib/p5.min.js"></script>
<script>
  // Grab UI elements
  const sliderTime            = document.getElementById('slider-time');
  const sliderWavelength      = document.getElementById('slider-wavelength');
  const sliderLongitudinal    = document.getElementById('slider-longitudinal-amplitude');
  const sliderLatitudinal     = document.getElementById('slider-latitudinal-amplitude');

  const spanTimeHeat          = document.getElementById('value-time');
  const spanWavelengthHeat    = document.getElementById('value-wavelength');
  const spanLongAmplitude     = document.getElementById('value-longitudinal-amplitude');
  const spanLatAmplitude      = document.getElementById('value-latitudinal-amplitude');

  // Update spans whenever user changes sliders
  [sliderTime, sliderWavelength, sliderLongitudinal, sliderLatitudinal].forEach((slider, idx) => {
    const span = [spanTimeHeat, spanWavelengthHeat, spanLongAmplitude, spanLatAmplitude][idx];
    slider.addEventListener('input', () => {
      span.textContent = parseFloat(slider.value).toFixed(2);
    });
  });

  let isTimePaused = false; // true when user slides time slider

  sliderTime.addEventListener('mousedown', () => { isTimePaused = true; });
  sliderTime.addEventListener('mouseup', () => { isTimePaused = false; });

  // Main p5 sketch
  const sketch = (p) => {
    const canvasWidth = 700;
    const canvasHeight = 450;
    const cols = 10;
    const rows = 6;
    const particles = [];

    let time = parseFloat(sliderTime.value);

    p.setup = () => {
      p.createCanvas(canvasWidth, canvasHeight).parent('canvas-container');
      p.noLoop(); // We'll control rendering with p.drawLoop
      // Compute equilibrium positions
      const hSpacing = canvasWidth / (cols + 1);
      const vSpacing = canvasHeight / (rows + 1);
      for (let r = 0; r < rows; r++) {
        for (let c = 0; c < cols; c++) {
          const x0 = hSpacing * (c + 1);
          const y0 = vSpacing * (r + 1);
          particles.push({ x0, y0, row: r, col: c, x: x0, y: y0 });
        }
      }
      // Start animation loop
      p.frameRate(60);
      p.drawLoop();
    };

    p.drawLoop = function () {
      if (!isTimePaused) {
        time += 0.03;
      } else {
        time = parseFloat(sliderTime.value);
      }
      sliderTime.value = time.toFixed(2);
      spanTimeHeat.textContent = time.toFixed(2);

      // Read current slider values
      const wavelength = parseFloat(sliderWavelength.value);
      const ampLong = parseFloat(sliderLongitudinal.value);
      const ampLat = parseFloat(sliderLatitudinal.value);

      const k = 2 * Math.PI / wavelength; // wave number

      // Clear canvas
      p.background('#E0F0FF');

      // Draw grid of particles and elliptical paths
      particles.forEach(particle => {
        const rowIdx = particle.row;
        const ampDecay = Math.exp(- (rowIdx / (rows - 1)) * 1.5);
        const ampX = ampLong * ampDecay * 20;
        const ampY = ampLat * ampDecay * 20;
        const phase = k * particle.x0 - time;

        // Elliptical paths
        p.noFill();
        p.stroke('#8F998F');
        p.strokeWeight(2);
        p.ellipse(particle.x0, particle.y0, 2 * ampX, 2 * ampY);

        // Displacement
        const dx = ampX * Math.cos(phase);
        const dy = ampY * Math.sin(phase);
        // Store current positions for later
        particle.x = particle.x0 + dx;
        particle.y = particle.y0 + dy;
      });

      // Draw dashed horizontal lines connecting particles in the same row
      for (let r = 0; r < rows; r++) {
        const rowParticles = particles.filter(p => p.row === r);
        p.stroke('#0000FF');
        p.strokeWeight(2);
        p.drawingContext.setLineDash([10, 5]);
        p.beginShape();
        rowParticles.forEach(p => {
          p.vertex(p.x, p.y);
        });
        p.endShape();
        p.drawingContext.setLineDash([]);
      }

      // Draw particles
      particles.forEach(p => {
        p.noStroke();
        p.fill('#FF0000');
        p.ellipse(p.x, p.y, 8, 8);
      });

      p.loop(); // Schedule next frame
    };

    // Use p.draw to trigger our custom loop
    p.draw = () => {
      // No auto draw; handled by drawLoop
    };
  };

  new p5(sketch);
</script>
</body>
</html>
