      <label>Model parameters</label>
      <label for="slider-tref">T<sub>ref</sub> (°C)</label>
      <input type="range" id="slider-tref" min="0" max="100" step="1" value="35">
      <span id="value-tref" class="slider-value"></span>

      <label for="slider-c">c (°C<sup>-1</sup>)</label>
      <input type="range" id="slider-c" min="0.01" max="0.1" step="0.001" value="0.025">
      <span id="value-c" class="slider-value"></span>

      <label for="slider-b">b (°C)</label>
      <input type="range" id="slider-b" min="200" max="350" step="0.01" value="273.16">
      <span id="value-b" class="slider-value"></span>

      <label for="slider-a">a (°C)</label>
      <input type="range" id="slider-a" min="1000" max="10000" step="1" value="4000">
      <span id="value-a" class="slider-value"></span>
