</div>

<script>
  // Helper linear regression function
  function linearRegression(x, y) {
    const n = x.length;
    const sumX = x.reduce((a,b)=>a+b, 0);
    const sumY = y.reduce((a,b)=>a+b, 0);
    const sumXY = x.reduce((acc, xi, i)=>acc+xi*y[i], 0);
    const sumX2 = x.reduce((acc, xi)=>acc+xi*xi, 0);
    const meanX = sumX/n;
    const meanY = sumY/n;
    const slope = (sumXY - n*meanX*meanY)/(sumX2 - n*meanX*meanX);
    const intercept = meanY - slope*meanX;
    // R squared
    const ssTot = y.reduce((acc, yi)=>acc+(yi-meanY)*(yi-meanY), 0);
    const ssRes = y.reduce((acc, yi, i)=>acc+(yi-(slope*x[i]+intercept))*(yi-(slope*x[i]+intercept)), 0);
    const rSquared = ssTot===0 ? 1 : 1 - ssRes/ssTot;
    return {slope, intercept, rSquared};
  }

  // DOM elements
  const btnArrhenius = document.getElementById('btn-arrhenius');
  const btnExponential = document.getElementById('btn-exponential');
  const sliders = {
    n: document.getElementById('slider-n'),
    tmin: document.getElementById('slider-tmin'),
    tmax: document.getElementById('slider-tmax'),
    tref: document.getElementById('slider-tref'),
    c: document.getElementById('slider-c'),
    b: document.getElementById('slider-b'),
    a: document.getElementById('slider-a')
  };
  const spans = {
    n: document.getElementById('value-n'),
    tmin: document.getElementById('value-tmin'),
    tmax: document.getElementById('value-tmax'),
    tref: document.getElementById('value-tref'),
    c: document.getElementById('value-c'),
    b: document.getElementById('value-b'),
    a: document.getElementById('value-a')
  };
  let currentModel = 'Arrhenius';

  function setActiveButton(btn) {
    if (btn === 'Arrhenius') {
      btnArrhenius.classList.add('active-button');
      btnExponential.classList.remove('active-button');
    } else {
      btnExponential.classList.add('active-button');
      btnArrhenius.classList.remove('active-button');
    }
  }

  btnArrhenius.addEventListener('click', () => { currentModel='Arrhenius'; setActiveButton('Arrhenius'); updatePlot(); });
  btnExponential.addEventListener('click', () => { currentModel='Exponential'; setActiveButton('Exponential'); updatePlot(); });

  function updateSpans() {
    for (let key in spans) {
      spans[key].textContent = sliders[key].value;
    }
  }

  // Enforce T_max > T_min
  sliders.tmin.addEventListener('input', () => {
    const tmin = Number(sliders.tmin.value);
    const tmaxSlider = sliders.tmax;
    tmaxSlider.min = (tmin+1).toString();
    if (Number(tmaxSlider.value) <= tmin) tmaxSlider.value = tmin+1;
    updateSpans();
    updatePlot();
  });

  for (let key in sliders) {
    if (key !== 'tmin') {
      sliders[key].addEventListener('input', () => { updateSpans(); updatePlot(); });
    }
  }

  function generateTemperatures(n, tmin, tmax) {
    const temps = [];
    const step = (tmax - tmin)/(n-1);
    for (let i=0;i<n;i++) temps.push(tmin + step*i);
    return temps;
  }

  function updatePlot() {
    const n = Number(sliders.n.value);
    const tmin = Number(sliders.tmin.value);
    const tmax = Number(sliders.tmax.value);
    const tref = Number(sliders.tref.value);
    const c_param = Number(sliders.c.value);
    const b_param = Number(sliders.b.value);
    const a_param = Number(sliders.a.value);

    const temps = generateTemperatures(n, tmin, tmax);
    const y = [];
    if (currentModel=== 'Arrhenius') {
      const denom = Math.exp(-a_param/(tref+b_param));
      for (let T of temps) {
        y.push( Math.exp(-a_param/(T+b_param)) / denom );
      }
    } else { // Exponential
      for (let T of temps) {
        y.push( Math.exp(c_param*(T-tref)) );
      }
    }

    let fit, fitParams, rSqText, colors, xlabel, ylabel;
    let bottomData, bottomLayout;
    if (currentModel=== 'Arrhenius') {
      // Fit exponential to Arrhenius data
      const x_fit = temps.map(t=>t-tref);
      const y_fitlog = y.map(v=>Math.log(v));
      fit = linearRegression(x_fit, y_fitlog);
      const c_fit = fit.slope;
      const rSq = fit.rSquared;
      // Generate fitted curve
      const y_fit = temps.map(t=>Math.exp(c_fit*(t-tref)));

      // Top plot
      const traceData = {
        x: temps,
        y: y,
        mode: 'markers',
        marker: {color:'#d62728'},
        name: 'Data'
      };
      const traceFit = {
        x: temps,
        y: y_fit,
        mode: 'lines',
        line: {color:'#d62728'},
        name: `Fit : c=${c_fit.toFixed(4)}, R²=${rSq.toFixed(3)}`
      };
      Plotly.react('plot-top', [traceData, traceFit], {
        title: `exponential model fit to Arrhenius data<br>c = ${c_fit.toFixed(4)} R² = ${rSq.toFixed(3)}`,
        xaxis: {title:'T (°C)'},
        yaxis: {title:'k(T)/k(T_ref)'},
        margin:{t:60}
      });

      // Bottom plot
      const x_bot = x_fit;
      const y_bot = y_fitlog;
      const traceBotData = {
        x: x_bot,
        y: y_bot,
        mode: 'markers',
        marker:{color:'#2ca02c'},
        name:'Data'
      };
      const traceBotFit = {
        x: x_bot,
        y: x_bot.map(v=>c_fit*v),
        mode:'lines',
        line:{color:'#2ca02c'},
        name:`Fit : y = ${c_fit.toFixed(4)}x, R² = ${rSq.toFixed(3)}`
      };
      const yMinBot = Math.min(...y_bot)-1, yMaxBot = Math.max(...y_bot)+1;
      const xMinBot = Math.min(...x_bot)-0.5, xMaxBot = Math.max(...x_bot)+0.5;
      Plotly.react('plot-bottom', [traceBotData, traceBotFit], {
        title: `linear fit of the exponential model<br>R² = ${rSq.toFixed(3)}`,
        xaxis:{title:'T - T_ref'},
        yaxis:{title:'log(k(T)/k(T_ref))'},
        shapes:[
          {type:'line', x0:0,x1:0,y0:0,y1:yMaxBot,line:{dash:'dash',color:'#444'}},
          {type:'line', x0:xMinBot,x1:xMaxBot,y0:0,y1:0, line:{dash:'dash',color:'#444'}}
        ],
        margin:{t:60}
      });

    } else {
      // Fit Arrhenius to Exponential data
      const x_fit = temps.map(t=>1/(t+b_param));
      const y_fitlog = y.map(v=>Math.log(v));
      fit = linearRegression(x_fit, y_fitlog);
      const a_fit = -fit.slope; // because slope = -a
      const rSq = fit.rSquared;
      // Generate fitted curve
      const denomRef = Math.exp(-a_fit/(tref+b_param));
      const y_fit = temps.map(t=> Math.exp(-a_fit/(t+b_param)) / denomRef );

      // Top plot
      const traceData = {
        x: temps,
        y: y,
        mode: 'markers',
        marker: {color:'#d62728'},
        name: 'Data'
      };
      const traceFit = {
        x: temps,
        y: y_fit,
        mode: 'lines',
        line: {color:'#d62728'},
        name: `Fit : a=${a_fit.toFixed(4)}, R²=${rSq.toFixed(3)}`
      };
      Plotly.react('plot-top', [traceData, traceFit], {
        title: `Arrhenius model fit to exponential data<br>a = ${a_fit.toFixed(4)} R² = ${rSq.toFixed(3)}`,
        xaxis: {title:'T (°C)'},
        yaxis: {title:'k(T)/k(T_ref)'},
        margin:{t:60}
      });

      // Bottom plot
      const x_bot = x_fit;
      const y_bot = y_fitlog;
      const traceBotData = {
        x: x_bot,
        y: y_bot,
        mode: 'markers',
        marker:{color:'#1f77b4'},
        name:'Data'
      };
      const traceBotFit = {
        x: x_bot,
        y: x_bot.map(v=>-a_fit*v + fit.intercept),
        mode:'lines',
        line:{color:'#1f77b4'},
        name:`Fit : y = -${a_fit.toFixed(4)}x + ${fit.intercept.toFixed(4)}, R²=${rSq.toFixed(3)}`
      };
      const yMinBot = Math.min(...y_bot)-1, yMaxBot = Math.max(...y_bot)+1;
      const xMinBot = Math.min(...x_bot)-0.5, xMaxBot = Math.max(...x_bot)+0.5;
      Plotly.react('plot-bottom', [traceBotData, traceBotFit], {
        title: `linear fit of the Arrhenius model<br>R² = ${rSq.toFixed(3)}`,
        xaxis:{title:'1/(T + b)'},
        yaxis:{title:'log(k(T)/k(T_ref))'},
        shapes:[
          {type:'line', x0:0,x1:0,y0:0,y1:yMaxBot,line:{dash:'dash',color:'#444'}},
          {type:'line', x0:xMinBot,x1:xMaxBot,y0:0,y1:0, line:{dash:'dash',color:'#444'}}
        ],
        margin:{t:60}
      });
    }
  }

  // Initialize
  updateSpans();
  updatePlot();
</script>
</body>
