  // Message data
  const MESSAGES = {
    DeclarationOfIndependence: `When in the Course of human events, it becomes necessary for one people to dissolve every Dependence upon any other and to apply concerns to itself, it should seek from that moment to create an balanced and Unfree circle. By seizing no inforce they never have the or not and the ground to keep onto sign and we want knows and in Listen that the sun you, but by imperative it, and quite so lighting unt expectively that sec salt meat no, noble in doing mus more as a half.
    `,
    GettysburgAddress: `Four score and seven years ago our ancestors at the more which, and care siswa not Butter, we now, to the fairness of a were now mus it and as we pale 4 hist vam for med see, from to careful one of the partial, the stir state, glaring meet of important, lay this some, the and imagine, they from our palace only.
    `
  };

  // Utility: Get processed text (lowercase letters only)
  function preprocess(text) {
    return text.toLowerCase().replace(/[^a-z]/g, '');
  }

  // Frequency calculation
  function letterFrequency(text) {
    const freq = new Array(26).fill(0);
    const total = text.length;
    for (const ch of text) {
      freq[ch.charCodeAt(0) - 97]++;
    }
    const letters = [];
    const percentages = [];
    for (let i = 0; i < 26; i++) {
      letters.push(String.fromCharCode(97 + i));
      percentages.push((freq[i] / total) * 100);
    }
    // Sort descending by frequency
    const combined = letters.map((l, i) => ({ letter: l, percent: percentages[i] }));
    combined.sort((a, b) => b.percent - a.percent);
    return {
      letters: combined.map(c => c.letter),
      percentages: combined.map(c => c.percent)
    };
  }

  // Plotly chart rendering
  function renderChart(containerId, freqData) {
    const layout = {
      yaxis: {
        title: "frequency(%)",
        range: [0, 14],
        tickmode: 'linear',
        tick0: 0,
        dtick: 2
      },
      xaxis: {
        tickangle: 0
      },
      margin: { l: 40, r: 20, b: 30, t: 20 },
      bargap: 0.05,
      shapes: [{
        type: 'line',
        x0: -0.5,
        y0: 3.846,
        x1: 25.5,
        y1: 3.846,
        line: { color: 'red', width: 2, dash: 'dash' }
      }]
    };
    const data = [{
      type: 'bar',
      x: freqData.letters,
      y: freqData.percentages,
      marker: { color: 'orange' }
    }];
    Plotly.newPlot(containerId, data, layout, {displayModeBar: false});
  }

  // Encryption: Caesar
  function caesarEncrypt(text, offset) {
    const res = [];
    for (const ch of text) {
      const code = (ch.charCodeAt(0) - 97 + offset) % 26;
      res.push(String.fromCharCode(97 + code));
    }
    return res.join('');
  }

  // Encryption: Vigenere
  function vigenereEncrypt(text, keyword) {
    keyword = sanitizeKeyword(keyword);
    const keyLen = keyword.length;
    const res = [];
    for (let i = 0; i < text.length; i++) {
      const shift = keyword.charCodeAt(i % keyLen) - 97;
      const code = (text.charCodeAt(i) - 97 + shift) % 26;
      res.push(String.fromCharCode(97 + code));
    }
    return res.join('');
  }

  // Encryption: Autokey
  function autokeyEncrypt(text, keyword) {
    keyword = sanitizeKeyword(keyword);
    let extendedKey = keyword;
    const res = [];
    for (let i = 0; i < text.length; i++) {
      if (i < extendedKey.length) {
        // use existing key
      } else {
        extendedKey += text[i - keyword.length];
      }
      const shift = extendedKey.charCodeAt(i) - 97;
      const code = (text.charCodeAt(i) - 97 + shift) % 26;
      res.push(String.fromCharCode(97 + code));
    }
    return res.join('');
  }

  // Sanitize keyword: keep only lowercase a-z
  function sanitizeKeyword(key) {
    const sanitized = key.toLowerCase().replace(/[^a-z]/g, '');
    return sanitized.length ? sanitized : 'a';
  }

  // Update visualization
  function updateVisualization() {
    const msgSelect = document.getElementById('select-message');
    const cipherSelect = document.getElementById('select-cipher');
    const keywordInput = document.getElementById('input-keyword');
    const sliderOffset = document.getElementById('slider-offset');
    const offsetValue = document.getElementById('offset-value');

    const msgId = msgSelect.value;
    const cipher = cipherSelect.value;
    const rawText = MESSAGES[msgId];
    const processedText = preprocess(rawText);

    // UI state update
    if (cipher === 'Caesar') {
      sliderOffset.disabled = false;
      offsetValue.style.opacity = 1;
      keywordInput.disabled = true;
    } else {
      sliderOffset.disabled = true;
      offsetValue.style.opacity = 0.5;
      keywordInput.disabled = false;
    }

    // Original message display
    const originalSnippet = rawText.slice(0, 80);
    document.getElementById('original-text').textContent = originalSnippet;
    const originalFreq = letterFrequency(processedText);
    renderChart('original-chart', originalFreq);

    // Encryption
    const offset = parseInt(sliderOffset.value, 10);
    const offsetDisp = document.getElementById('offset-value');
    offsetDisp.textContent = offset.toString();

    let encryptedText = '';
    if (cipher === 'Caesar') {
      encryptedText = caesarEncrypt(processedText, offset);
    } else if (cipher === 'De Vigenere') {
      encryptedText = vigenereEncrypt(processedText, keywordInput.value);
    } else if (cipher === 'Autokey') {
      encryptedText = autokeyEncrypt(processedText, keywordInput.value);
    }

    document.getElementById('encrypted-text').textContent = encryptedText;
    const encryptedFreq = letterFrequency(encryptedText);
    renderChart('encrypted-chart', encryptedFreq);
  }

  // Event listeners
  document.getElementById('select-message').addEventListener('change', updateVisualization);
  document.getElementById('select-cipher').addEventListener('change', updateVisualization);
  document.getElementById('input-keyword').addEventListener('input', updateVisualization);
  document.getElementById('slider-offset').addEventListener('input', function(){
    document.getElementById('offset-value').textContent = this.value;
    updateVisualization();
  });

  // Initial load
  document.addEventListener('DOMContentLoaded', function(){
    // Set default states
    document.getElementById('role-message').value = 'DeclarationOfIndependence';
    updateVisualization();
  });
</script>
</body>
</html>
