</head>
<body>
<div id="app-container">
  <!-- Control Panel -->
  <div id="control-panel">
    <div>
      <label for="slider-lambda">permeability ratio, λ</label>
      <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
      <span id="value-lambda">0.00001</span>
    </div>
    <div>
      <label for="slider-omega">storativity ratio, ω</label>
      <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
      <span id="value-omega">0.01</span>
    </div>
    <div>
      <label>option</label>
      <button id="btn-pressure-drop" class="active">pressure drop</button>
      <button id="btn-difference">difference</button>
    </div>
  </div>

  <!-- Plot Area -->
  <div id="plot-container">
    <div id="plot"></div>
  </div>
</div>

<script>
  // ---------- Scientific Calculation Placeholder ----------
  // The following functions provide a very rough analytic approximation
  // to the pressure drop solution. They do not perform a full
  // numerical inverse Laplace transform but are sufficient for
  // demonstration of interactivity.
  // -------------------------------------------------------------

  // Generate a log‐spaced array of tau values: 200 points from 1 to 1e10
  function generateTauArray() {
    const tau = [];
    const n = 200;
    for (let i = 0; i < n; i++) {
      const exp = i * (10 / (n - 1)); // 0 to 10
      tau.push(Math.pow(10, exp));
    }
    return tau;
  }

  // Approximate pressure drop.  The correction term introduces
  // λ and ω dependence in a simple way.
  function calculatePressureDrop(tau, lambda, omega) {
    const asym = Math.log(tau) + 0.8091;           // asymptotic term
    const corr = lambda * Math.exp(-omega * Math.log(tau));
    return asym + corr;
  }

  // Produce y values for the selected plot option
  function generateData(lambda, omega, option) {
    const tau = generateTauArray();
    const y = tau.map(t => {
      const dp = calculatePressureDrop(t, lambda, omega);
      if (option === 'pressure-drop') {
        return dp;
      } else { // difference
        return dp - 0.5 * (Math.log(t) + 0.8091);
      }
    });
    return { tau, y };
  }

  // ---------- Plotting ----------
  const plotDiv = document.getElementById('plot');

  const layoutPressureDrop = {
    title: { text: 'pressure drawdown at the wellbore', font: {size: 18} },
    xaxis:  { title: 'dimensionless time, τ', type: 'log',
              range: [0, 10], dtick: 2, gridcolor: '#ddd' },
    yaxis:  { title: 'dimensionless pressure drop, ΔP',
              range: [0, 12], gridcolor: '#ddd' },
    margin: { l: 80, r: 50, b: 60, t: 60 },
    paper_bgcolor: '#fff',
    plot_bgcolor: '#fff'
  };

  const layoutDifference = {
    title: { text: 'deviation from the asymptote', font: {size: 18} },
    xaxis:  { title: 'dimensionless time, τ', type: 'log',
              range: [0, 10], dtick: 2, gridcolor: '#ddd' },
    yaxis:  { title: 'dimensionless difference, ΔP − ½ (ln(τ) + 0.8091)',
              range: [0, 3.5], gridcolor: '#ddd' },
    margin: { l: 80, r: 50, b: 60, t: 60 },
    paper_bgcolor: '#fff',
    plot_bgcolor: '#fff'
  };

  function plotData(trace, layout, useReact) {
    if (useReact) {
      Plotly.react('plot', [trace], layout, {responsive: true});
    } else {
      Plotly.newPlot('plot', [trace], layout, {responsive: true});
    }
  }

  // ---------- UI state ----------
  const sliderLambda = document.getElementById('slider-lambda');
  const sliderOmega = document.getElementById('slider-omega');
  const spanLambda   = document.getElementById('value-lambda');
  const spanOmega    = document.getElementById('value-omega');
  const btnPressureDrop = document.getElementById('btn-pressure-drop');
  const btnDifference   = document.getElementById('btn-difference');
  let activePlot = 'pressure-drop'; // 'pressure-drop' or 'difference'

  // Format number: scientific when < 1e-4
  function formatNumber(val) {
    if (val < 1e-4) return val.toExponential(5);
    return val.toPrecision(5);
  }

  // Update slider displays
  function updateSliderDisplays() {
    const lambdaExp = parseFloat(sliderLambda.value);
    const omegaExp  = parseFloat(sliderOmega.value);
    const lambdaVal = Math.pow(10, lambdaExp);
    const omegaVal  = Math.pow(10, omegaExp);
    spanLambda.textContent = formatNumber(lambdaVal);
    spanOmega.textContent  = formatNumber(omegaVal);
    return { lambda: lambdaVal, omega: omegaVal };
  }

  // Main update function
  function updatePlot() {
    const params = updateSliderDisplays();
    const data = generateData(params.lambda, params.omega, activePlot);
    const trace = {
      x: data.tau,
      y: data.y,
      mode: 'lines',
      line: { color: '#ff0000', width: 2 }
    };
    const layout = activePlot === 'pressure-drop' ? layoutPressureDrop : layoutDifference;
    const useReact = true; // Whenever only data changes we can use react
    plotData(trace, layout, useReact);
  }

  // ---------- Event Listeners ----------
  sliderLambda.addEventListener('input', updatePlot);
  sliderOmega.addEventListener('input', updatePlot);

  btnPressureDrop.addEventListener('click', () => {
    if (activePlot !== 'pressure-drop') {
      btnPressureDrop.classList.add('active');
      btnDifference.classList.remove('active');
      activePlot = 'pressure-drop';
      updatePlot();
    }
  });

  btnDifference.addEventListener('click', () => {
    if (activePlot !== 'difference') {
      btnDifference.classList.add('active');
      btnPressureDrop.classList.remove('active');
      activePlot = 'difference';
      // Full new plot to update layout
      updatePlot();
    }
  });

  // ---------- Initial Render ----------
  // The initial state is automatically set by the markup
  // Render the default plot
  updatePlot();
</script>
</body>
</html>
