<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Forest Growth Curve</title>
<style>
  body{
    display:flex;
    font-family:Arial, sans-serif;
    margin:0;
    height:100vh;
  }
  #left-column{
    flex:2;
    padding:20px;
    display:flex;
    flex-direction:column;
    align-items:center;
    justify-content:flex-start;
    background:#FCF8E3;
  }
  #right-column{
    flex:1;
    padding:20px;
    background:#F8F9FA;
  }
  #canvas-container{
    width:500px;
    height:500px;
  }
  #results-container p{
    margin:5px 0;
    font-size:1.1em;
  }
  #data-table{
    border-collapse:collapse;
    width:100%;
  }
  #data-table th, #data-table td{
    border:1px solid #000;
    padding:6px;
    text-align:center;
  }
  #data-table th{
    background:#e9ecef;
  }
</style>
<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
  <div id="left-column">
    <div id="canvas-container"></div>
    <div id="results-container">
      <p>rejuvenation bias <i>u</i> = <span id="results-u">22.7319</span></p>
      <p>site productivity <i>p</i> = <span id="results-p">58.2729</span></p>
    </div>
  </div>
  <div id="right-column">
    <table id="data-table">
      <thead>
        <tr>
          <th>age</th>
          <th>volume</th>
        </tr>
      </thead>
      <tbody id="data-table-body"></tbody>
    </table>
  </div>

<script>
/* Global state */
let dataPoints = [
  {age:30, volume:289},
  {age:35, volume:445},
  {age:40, volume:565},
  {age:45, volume:704},
  {age:50, volume:833},
  {age:55, volume:924}
];
let pVal = 58.2729;
let uVal = 22.7319;
let selectedIndex = null;
const pointRadius = 8;
const minAge = 20, maxAge = 80;
const minVol = 0, maxVol = 1500;
const canvasSize = 500;
const margin = {top:50, right:60, bottom:60, left:80};
const innerW = canvasSize - margin.left - margin.right;
const innerH = canvasSize - margin.top - margin.bottom;

/* Helper conversion functions */
function ageToX(age){
  return map(age, minAge, maxAge, margin.left, margin.left+innerW);
}
function volumeToY(vol){
  return map(vol, minVol, maxVol, margin.top+innerH, margin.top); // y grows up
}
function xToAge(x){
  return map(x, margin.left, margin.left+innerW, minAge, maxAge);
}
function yToVolume(y){
  return map(y, margin.top+innerH, margin.top, minVol, maxVol);
}

/* Gradient descent fit */
function fitModel(){
  let p = 50.0;
  let u = 1.0;
  const lr = 0.00002;
  const maxIter = 600;
  const epsilon = 1e-6;
  const minU = minAge - 1; // keep u < min age
  for(let iter=0; iter<maxIter; iter++){
    let gradP = 0;
    let gradU = 0;
    let errorSum = 0;
    for(let d of dataPoints){
      let base = Math.max(d.age - u, epsilon);
      let pred = p * Math.pow(base, 0.8);
      let e = d.volume - pred;
      errorSum += e*e;
      gradP += -2*e * Math.pow(base,0.8);
      gradU += -2*p*0.8*e*Math.pow(base,-0.2);
    }
    p -= lr * gradP;
    u -= lr * gradU;
    if(u > minU) u = minU;
    if(Math.abs(gradP)+Math.abs(gradU) < epsilon) break;
  }
  pVal = p;
  uVal = u;
}

/* UI update */
function updateResults(){
  document.getElementById('results-u').textContent = uVal.toFixed(4);
  document.getElementById('results-p').textContent = pVal.toFixed(4);
  let tbody = document.getElementById('data-table-body');
  tbody.innerHTML = '';
  dataPoints.forEach(d=>{
    let tr = document.createElement('tr');
    let td1 = document.createElement('td');
    td1.textContent = d.age.toFixed(2);
    let td2 = document.createElement('td');
    td2.textContent = d.volume.toFixed(2);
    tr.append(td1,td2);
    tbody.appendChild(tr);
  });
}

/* p5 setup and draw */
function setup(){
  let cnv = createCanvas(canvasSize, canvasSize);
  cnv.parent('canvas-container');
  noLoop(); // we redraw only after changes
  fitModel();
  updateResults();
}

function draw(){
  background('#FFFFE0');
  drawGrid();
  drawAxes();
  drawLabels();
  drawCurve();
  drawPoints();
}

/* Grid drawing with dashed lines */
function drawGrid(){
  stroke('#D3D3D3');
  strokeWeight(1);
  let stepsX = 10;
  let stepsY = 200;
  for(let x=minAge; x<=maxAge; x+=stepsX){
    let px = ageToX(x);
    drawDashedLine(px, margin.top, px, margin.top+innerH);
  }
  for(let y=minVol; y<=maxVol; y+=stepsY){
    let py = volumeToY(y);
    drawDashedLine(margin.left, py, margin.left+innerW, py);
  }
}
function drawDashedLine(x1,y1,x2,y2){
  let id = createVector(x2-x1, y2-y1);
  let len = id.mag();
  let step = 4;
  let dash = 3;
  let n = floor(len / (dash + step));
  let px = 0;
  for(let i=0;i<n;i++){
    let start = p5.Vector.add(createVector(x1,y1), id.copy().setMag(px));
    let end = p5.Vector.add(createVector(x1,y1), id.copy().setMag(px+dash));
    line(start.x, start.y, end.x, end.y);
    px += dash + step;
  }
}

/* Axes */
function drawAxes(){
  stroke('#000');
  strokeWeight(2);
  // Y-axis
  line(margin.left, margin.top, margin.left, margin.top+innerH);
  // X-axis
  line(margin.left, margin.top+innerH, margin.left+innerW, margin.top+innerH);
}

/* Labels */
function drawLabels(){
  fill('#000');
  noStroke();
  textSize(18);
  textAlign(CENTER, CENTER);
  // Title
  text("Forest Growth Curve", margin.left+innerW/2, margin.top/2);
  // X label
  textSize(14);
  text("Stand age, yr", margin.left+innerW/2, margin.top+innerH + 40);
  // Y label
  push();
  translate(margin.left - 40, margin.top+innerH/2);
  rotate(-HALF_PI);
  text("Wood volume, m³ ha⁻¹",0,0);
  pop();
  // 4/5 law rotated
  push();
  translate(margin.left+innerW+20, margin.top+innerH/2);
  // rotate 90 degrees counter-clockwise
  rotate(-HALF_PI);
  textAlign(CENTER, CENTER);
  text("4/5 law",0,0);
  pop();
}

/* Points */
function drawPoints(){
  noStroke();
  fill('#0000CD');
  for(let i=0;i<dataPoints.length;i++){
    let d = dataPoints[i];
    let px = ageToX(d.age);
    let py = volumeToY(d.volume);
    ellipse(px,py,pointRadius*2,pointRadius*2);
  }
}

/* Curve */
function drawCurve(){
  stroke('#0000CD');
  strokeWeight(2);
  noFill();
  beginShape();
  let startAge = Math.max(uVal, minAge);
  let step = 0.5;
  for(let age=startAge; age<=maxAge; age+=step){
    let vol = pVal * Math.pow(age - uVal, 0.8);
    if(vol<minVol) vol=minVol;
    if(vol>maxVol) vol=maxVol;
    let px = ageToX(age);
    let py = volumeToY(vol);
    vertex(px, py);
  }
  endShape();
}

/* Mouse interaction */
function mousePressed(){
  if(mouseX < margin.left || mouseX > margin.left+innerW || mouseY < margin.top || mouseY > margin.top+innerH) return;
  // check if clicking on point
  let found = false;
  for(let i=0;i<dataPoints.length;i++){
    let d=dataPoints[i];
    let px = ageToX(d.age);
    let py = volumeToY(d.volume);
    if(dist(mouseX,mouseY,px,py) < pointRadius+3){
      selectedIndex = i;
      found = true;
      break;
    }
  }
  if(!found){
    // add point
    let age = xToAge(mouseX);
    let vol = yToVolume(mouseY);
    dataPoints.push({age:age, volume:vol});
    recalc();
  }
}
function mouseDragged(){
  if(selectedIndex !== null){
    let age = xToAge(mouseX);
    let vol = yToVolume(mouseY);
    // constrain to plot area
    age = constrain(age, minAge, maxAge);
    vol = constrain(vol, minVol, maxVol);
    dataPoints[selectedIndex] = {age:age, volume:vol};
    redraw();
  }
}
function mouseReleased(){
  selectedIndex = null;
}
function doubleClicked(){
  // delete point if double clicked on it
  for(let i=0;i<dataPoints.length;i++){
    let d=dataPoints[i];
    let px = ageToX(d.age);
    let py = volumeToY(d.volume);
    if(dist(mouseX,mouseY,px,py) < pointRadius+3){
      dataPoints.splice(i,1);
      recalc();
      break;
    }
  }
}
function recalc(){
  fitModel();
  updateResults();
  redraw();
}
</script>
</body>
</html>
