const outer = [[50, 50], [550, 50], [550, 550], [50, 550]];
const holes = [
  [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
  [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
  [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
  [[300, 400], [450, 400], [450, 500], [300, 500]],
  [[300, 300], [350, 300], [350, 350], [300, 350]]
];

const allPolys = [outer, ...holes];

/* ---------- Helper functions ---------- */
function cross(ax, ay, bx, by){ return ax*by - ay*bx; }
function equal(a,b,eps=1e-6){return Math.abs(a-b)<eps;}
function pointInPolygon(pt, poly){
  let inside=false;
  for(let i=0,j=poly.length-1;i<poly.length;j=i++){
    const xi=poly[i][0], yi=poly[i][1];
    const xj=poly[j][0], yj=poly[j][1];
    const intersect = ((yi>pt.y)!=(yj>pt.y)) &&
                      (pt.x < (xj-xi)*(pt.y-yi)/(yj-yi)+xi);
    if(intersect) inside=!inside;
  }
  return inside;
}
function pointInAnyHole(pt){
  for(const hole of holes) if(pointInPolygon(pt, hole)) return true;
  return false;
}
function pointInsideOuter(pt){ return pointInPolygon(pt, outer);}

/* ---------- Intersection between ray and segment ---------- */
function intersectRaySegment(S, dir, a, b){
  // Ray: S + t*dir   t>0
  // Segment: a + u*(b-a) 0<=u<=1
  const ax = a[0], ay = a[1];
  const bx = b[0], by = b[1];
  const dx = dir[0], dy = dir[1];
  const sx = S[0], sy = S[1];
  const segx = bx - ax, segy = by - ay;
  const denom = cross(dx, dy, segx, segy);
  if(equal(denom,0)) return null; // parallel
  const t = cross(ax-sx, ay-sy, segx, segy) / denom;
  const u = cross(ax-sx, ay-sy, dx, dy) / denom;
  if(t>1e-6 && u>=-1e-6 && u<=1+1e-6){
    return [sx + t*dx, sy + t*dy, t, u];
  }
  return null;
}

/* ---------- Build edges ---------- */
const vertices = []; // list of {x,y}
const edges = []; // list of [ [x1,y1], [x2,y2] ]

allPolys.forEach(poly=>{
  for(let i=0;i<poly.length;i++){
    vertices.push({x:poly[i][0], y:poly[i][1]});
    const next = poly[(i+1)%poly.length];
    edges.push([[poly[i][0], poly[i][1]], [next[0], next[1]]]);
  }
});
const vertexCount = vertices.length; // should be 38

/* ---------- Variables ----------- */
let source = {x:275, y:275};
let dragging = false;
let sliderValue = 3; // default
let sliderElem = document.getElementById('angle-slider');
let angleValElem = document.getElementById('angle-value');
angleValueUpdated(3);

/* ---------- Slider event ---------- */
sliderElem.addEventListener('input', ()=>{
  sliderValue = parseInt(sliderElem.value,10);
  angleValElem.textContent = sliderValue;
});

/* ---------- Reset button ---------- */
document.getElementById('reset-button').addEventListener('click', ()=>{
  source = {x:275, y:275};
  sliderElem.value = 3;
  sliderValue = 3;
  angleValElem.textContent = 3;
});

/* ---------- p5.js sketch ---------- */
function setup(){
  const canvas = createCanvas(600,600);
  canvas.parent('canvas-container');
  angleMode(RADIANS);
  noLoop(); // redraw only on data change
  // Register drawing callbacks
  redraw(); // first draw
}

/* ---------- P5 draw loop (we use redraw only when needed) ---------- */
function draw(){
  background(255);
  // Draw polygons
  stroke('#AAAAAA');
  strokeWeight(1);
  fill('#FFFFFF');
  beginShape();
  verticesInOrder(outer).forEach(v=>vertex(v.x,v.y));
  holes.forEach(h=>{ beginContour(); verticesInOrder(h).forEach(v=>vertex(v.x,v.y)); });
  endShape(CLOSE);

  // Draw vertices
  noStroke();
  fill('#FF0000');
  vertices.forEach(v=>ellipse(v.x,v.y,4,4));

  // Draw source point
  stroke('#0000FF');
  strokeWeight(1);
  fill('#0000FF');
  ellipse(source.x, source.y, 8, 8);

  // Compute visibility
  const sorted = getSortedVertices(); // array of indices
  const intersections = []; // {point:[x,y], edgeIdx:Int}
  for(const idx of sorted){
    const v = vertices[idx];
    const dir = [v.x - source.x, v.y - source.y];
    const norm = Math.hypot(dir[0], dir[1]);
    if(norm===0) continue;
    const dirNorm = [dir[0]/norm, dir[1]/norm];
    let best=null;
    let bestT=Infinity;
    let bestEdge=null;
    edges.forEach((edge, ei)=>{
      const res = intersectRaySegment(source, dirNorm, edge[0], edge[1]);
      if(res){
        const t=res[2];
        if(t<bestT){
          bestT=t;
          best=[res[0],res[1], t];
          bestEdge=ei;
        }
      }
    });
    if(best){
      intersections.push({point:[best[0],best[1]], edge:bestEdge});
    }
  }

  // Partial / full drawing
  if(sliderValue<vertexCount){
    const committed = intersections.slice(0, sliderValue-1);
    const activeIdx = sliderValue-1;
    const active = intersections[activeIdx];

    // light yellow polygon
    if(committed.length>0){
      fill('#FFFF99');
      stroke('rgba(170,170,170,0.5)');
      strokeWeight(1);
      beginShape();
      committed.forEach(p=>vertex(p.point[0], p.point[1]));
      endShape(CLOSE);
    }

    // active triangle
    if(activeIdx>0){
      const prev = intersections[activeIdx-1];
      fill('#FFCC66');
      stroke('rgba(170,170,170,0.5)');
      strokeWeight(1);
      beginShape();
      vertex(source.x, source.y);
      vertex(prev.point[0], prev.point[1]);
      vertex(active.point[0], active.point[1]);
      endShape(CLOSE);
    }

    // helper ray
    const sortedVertices = sorted.map(i=>vertices[i]);
    const currentVertex = sortedVertices[activeIdx];
    stroke('#0000FF');
    strokeWeight(1);
    line(source.x, source.y, currentVertex.x, currentVertex.y);

    // sweep arc
    const firstAngle = Math.atan2(sortedVertices[0].y - source.y, sortedVertices[0].x - source.x);
    const currentAngle = Math.atan2(currentVertex.y - source.y, currentVertex.x - source.x);
    noFill();
    stroke('#0000FF');
    strokeWeight(1);
    push();
    translate(source.x, source.y);
    arc(0,0,30,30, firstAngle, currentAngle, OPEN);
    pop();

    // highlight edge
    if(active){
      const e = edges[active.edge];
      stroke('#00CC00');
      strokeWeight(3);
      line(e[0][0],e[0][1], e[1][0],e[1][1]);
    }
  } else {
    // full visibility polygon
    fill('#FFCC66');
    stroke('rgba(170,170,170,0.5)');
    strokeWeight(1);
    beginShape();
    intersections.forEach(p=>vertex(p.point[0], p.point[1]));
    endShape(CLOSE);
  }
}

/* ---------- Helper Functions for p5 ---------- */
function verticesInOrder(poly){
  return poly.map(p=>({x:p[0], y:p[1]}));
}

function getSortedVertices(){
  // returns array of vertex indices sorted by angle around source
  const angs = vertices.map((v,i)=>{
    const ang= Math.atan2(v.y - source.y, v.x - source.x);
    return {i,ang};
  });
  angs.sort((a,b)=>a.ang-b.ang);
  return angs.map(o=>o.i);
}

/* ---------- Mouse interaction ---------- */
function mousePressed(){
  if(dist(mouseX, mouseY, source.x, source.y)<=5){
    dragging=true;
  }
}
function mouseDragged(){
  if(dragging){
    const newPos = {x:mouseX, y:mouseY};
    if(pointInsideOuter(newPos) && !pointInAnyHole(newPos)){
      source=newPos;
      redraw();
    }
  }
}
function mouseReleased(){ dragging=false; }
function angleValueUpdated(val){ /* placeholder if needed */ }

/* ---------- Ensure redrawing on slider or reset ---------- */
sliderElem.addEventListener('input', redraw);
document.getElementById('reset-button').addEventListener('click', redraw);
</script>
</body>
</html>
