<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<title>Power Series vs. Numerical ODE Solver</title>
<style>
  body, html { margin: 0; height: 100%; font-family: Arial, sans-serif; }
  #main-container {
    display: flex;
    height: 100vh;
  }
  #control-panel {
    flex: 0 0 250px;
    padding: 20px;
    box-sizing: border-box;
    background: #f9f9f9;
    border-right: 1px solid #ddd;
    overflow-y: auto;
  }
  #control-panel div { margin-bottom: 20px; }
  #control-panel label { display: block; margin-bottom: 4px; font-weight: bold; }
  #control-panel span { margin-left: 8px; font-weight: bold; }

  #visualization-area {
    flex: 1;
    padding: 20px;
    box-sizing: border-box;
  }
</style>
<script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
<div id="main-container">
  <div id="control-panel">
    <div>
      <label for="slider-order">expansion order</label>
      <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
      <span id="value-order">5</span>
    </div>
    <div>
      <label for="slider-a">f(0)</label>
      <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0" step="0.1">
      <span id="value-a">2.0</span>
    </div>
    <div>
      <label for="slider-b">f'(0)</label>
      <input type="range" id="slider-b" min="-2" max="2" step="0.1" value="0.0" step="0.1">
      <span id="value-b">0.0</span>
    </div>
  </div>

  <div id="visualization-area">
    <div id="plot-container"></div>
  </div>
</div>

<script>
(() => {
  const sliderOrder = document.getElementById('slider-order');
  const valueOrder = document.getElementById('value-order');
  const sliderA = document.getElementById('slider-a');
  const valueA = document.getElementById('value-a');
  const sliderB = document.getElementById('slider-b');
  const valueB = document.getElementById('value-b');

  // Utility: factorial cache
  const fact = [1];
  const factorial = (n) => {
    while (fact.length <= n) fact.push(fact[fact.length-1] * fact.length);
    return fact[n];
  };

  // Build polynomial string
  const formatPolyString = (coeffs) => {
    const terms = [];
    for (let k = 0; k < coeffs.length; ++k) {
      const c = coeffs[k];
      if (Math.abs(c) < 5e-8) continue;
      const absC = Math.abs(c);
      const formatted = absC.toFixed(5);
      let term;
      if (k === 0) term = `${formatted}`;
      else if (k === 1) term = `${formatted} x`;
      else term = `${formatted} x^${k}`;
      terms.push(c < 0 ? `- ${term}` : `+ ${term}`);
    }
    if (terms.length === 0) return '0';
    // Fix first term sign
    let first = terms[0];
    if (first.startsWith('+ ')) first = first.slice(2);
    const rest = terms.slice(1).map(t => ' ' + t);
    return first + rest.join('');
  };

  // RK4 solver for given functions
  const rk4Step = (f1, f2, y1, y2, h) => {
    const k1f1 = h * f1(y1, y2);
    const k1f2 = h * f2(y1, y2);
    const k2f1 = h * f1(y1 + k1f1/2, y2 + k1f2/2);
    const k2f2 = h * f2(y1 + k1f1/2, y2 + k1f2/2);
    const k3f1 = h * f1(y1 + k2f1/2, y2 + k2f2/2);
    const k3f2 = h * f2(y1 + k2f1/2, y2 + k2f2/2);
    const k4f1 = h * f1(y1 + k3f1, y2 + k3f2);
    const k4f2 = h * f2(y1 + k3f1, y2 + k3f2);

    const y1Next = y1 + (k1f1 + 2*k2f1 + 2*k3f1 + k4f1)/6;
    const y2Next = y2 + (k1f2 + 2*k2f2 + 2*k3f2 + k4f2)/6;
    return [y1Next, y2Next];
  };

  // Main update function
  const updatePlot = () => {
    const order = Number(sliderOrder.value);
    const a = Number(sliderA.value);
    const b = Number(sliderB.value);

    valueOrder.textContent = order;
    valueA.textContent = a.toFixed(1);
    valueB.textContent = b.toFixed(1);

    // Compute derivatives at 0
    const fDerivs = [];
    fDerivs[0] = a;
    fDerivs[1] = b;
    // f''(0)
    fDerivs[2] = -(fDerivs[1] + 1.5 * fDerivs[0]) / fDerivs[0];
    // f'''(0)
    fDerivs[3] = -(fDerivs[2]*fDerivs[1] + fDerivs[2] + 1.5*fDerivs[1]) / fDerivs[0];
    // f''''(0)
    fDerivs[4] = -(2*fDerivs[3]*fDerivs[1] + Math.pow(fDerivs[2], 2) + fDerivs[3] + 1.5*fDerivs[2]) / fDerivs[0];
    // f'''''(0)
    fDerivs[5] = -(3*fDerivs[4]*fDerivs[1] + 4*fDerivs[3]*fDerivs[2] + fDerivs[4] + 1.5*fDerivs[3]) / fDerivs[0];

    const coeffs = [];
    for (let k=0; k<=5; ++k) {
      coeffs[k] = fDerivs[k] / factorial(k);
    }

    // Generate x values
    const NX = 200;
    const xVals = Array.from({length:NX}, (_,i)=> i*1.5/(NX-1));

    // Polynomial approximation
    const approxVals = xVals.map(x=>{
      let sum = 0;
      for (let k=0; k<=order; ++k) {
        sum += coeffs[k] * Math.pow(x, k);
      }
      return sum;
    });

    // Numerical solution via RK4
    const f1 = (y1, y2) => y2;
    const f2 = (y1, y2) => - (y2 + 1.5*y1) / y1;

    const h = 0.001;
    let y1 = a, y2 = b, x = 0;
    const numericVals = [a];
    let targetIndex = 1;
    while (targetIndex < xVals.length) {
      if (x < xVals[targetIndex]) {
        [y1, y2] = rk4Step(f1, f2, y1, y2, h);
        x += h;
      } else {
        numericVals.push(y1);
        targetIndex++;
      }
    }

    // Determine x_limit
    let xLimit = 1.5;
    for (let i=0; i<approxVals.length; ++i) {
      if (Math.abs(approxVals[i] - numericVals[i]) >= 0.01) {
        xLimit = xVals[i];
        break;
      }
    }

    // Shape for shaded region
    const shape = {
      type: 'rect',
      xref: 'x', yref: 'paper',
      x0: 0, y0: 0, x1: xLimit, y1: 1,
      fillcolor: 'lightblue',
      opacity: 0.4,
      layer: 'below',
      line: { width: 0 }
    };

    const layout = {
      title: { text: formatPolyString(coeffs.slice(0, order+1)), font: { size: 20 } },
      xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
      yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
      margin: { l: 50, r: 20, t: 50, b: 40 },
      shapes: [shape],
      showlegend: false
    };

    const traceRed = { x: xVals, y: approxVals, type: 'scatter', mode: 'lines',
      line: { color: 'red', width: 2 }, name: 'Power Series' };
    const traceGreen = { x: xVals, y: numericVals, type: 'scatter', mode: 'lines',
      line: { color: 'green', width: 3 }, name: 'Numerical (RK4)' };

    if (!Plotly.d3) {
      Plotly.newPlot('plot-container', [traceRed, traceGreen], layout);
    } else {
      Plotly.react('plot-container', [traceRed, traceGreen], layout);
    }
  };

  // Attach listeners
  [sliderOrder, sliderA, sliderB].forEach(s => s.addEventListener('input', updatePlot));

  // Initial rendering
  updatePlot();
})();
</script>
</body>
</html>
