      </select>
    </div>
    <div id="view-controls">
      <label>plot</label>
      <button id="btn-plot-2d" class="active">distribution 2D</button>
      <button id="btn-plot-3d">keyboard 3D</button>
      <label>keyboard</label>
      <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
      <button id="btn-keyboard-dvorak">Dvorak</button>
    </div>
  </div>
  <div id="canvas-container">
    <canvas id="visualization-canvas"></canvas>
  </div>
</div>

<script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
<script>
(() => {
  // --- UI Elements
  const canvas = document.getElementById('visualization-canvas');
  const selectText = document.getElementById('select-text');
  const btnPlot2D = document.getElementById('btn-plot-2d');
  const btnPlot3D = document.getElementById('btn-plot-3d');
  const btnKbdQW = document.getElementById('btn-keyboard-qwerty');
  const btnKbdDvorak = document.getElementById('btn-keyboard-dvorak');
  const controlsDiv = document.getElementById('controls');

  // --- Three.js Setup
  const renderer = new THREE.WebGLRenderer({canvas, antialias:true});
  const scene = new THREE.Scene();
  scene.background = new THREE.Color('#FFFFFF');
  const ambientLight = new THREE.AmbientLight(0xcccccc, 0.5);
  const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
  directionalLight.position.set(0,100,100);
  scene.add(ambientLight, directionalLight);

  let camera, controls;
  let currentView = '2d';      // '2d' | '3d'
  let currentLayout = 'qwerty'; // 'qwerty' | 'dvorak'
  let frequencyMap = {};
  let maxFrequency = 0;
  const maxHeight = 200;

  const keyboardLayouts = {
    qwerty: ['QWERTYUIOP', 'ASDFGHJKL', 'ZXCVBNM'],
    dvorak: ['PYFGCRL', 'AOEUIDHTNS', 'QJKXBMWVZ']
  };

  const keyColors = {
    top: 0x808000,    // olive green
    home: 0x00aaaa,   // cyan
    bottom: 0x800080  // purple
  };

  // Utility: Create texture from text
  function textTexture(text, font='20px Arial', color='#000', background=null) {
    const canvasTex = document.createElement('canvas');
    const ctx = canvasTex.getContext('2d');
    ctx.font = font;
    const metrics = ctx.measureText(text);
    const width = Math.ceil(metrics.width);
    const height = 30;
    canvasTex.width = width;
    canvasTex.height = height;
    ctx.font = font;
    if (background) ctx.fillStyle = background; else ctx.clearRect(0,0,width,height);
    ctx.fillStyle = color;
    ctx.textBaseline = 'middle';
    ctx.textAlign = 'center';
    ctx.fillText(text, width/2, height/2);
    return new THREE.CanvasTexture(canvasTex);
  }

  // Color mapping based on normalized frequency
  function frequencyColor(norm) {
    // 4 segment gradient
    if (norm < 0.25) return new THREE.Color(`hsl(240,100%,${50 + 30*norm}%`) // blue -> purple
    if (norm < 0.5)  return new THREE.Color(`hsl(270,100%,${50 + 30*(norm-0.25)/0.25}%`) // purple -> orange
    if (norm < 0.75) return new THREE.Color(`hsl(30,100%,${50 + 30*(norm-0.5)/0.25}%`)  // orange -> red
    return new THREE.Color(`hsl(0,100%,${50 + 30*(norm-0.75)/0.25}%`)   // red
  }

  // Recaculate frequencies
  function calculateFrequency(text) {
    const map = {};
    for (let i=0; i<26; i++) map[String.fromCharCode(65+i)] = 0;
    for (const ch of text.toUpperCase()) if (ch >= 'A' && ch <= 'Z') map[ch]++;
    return map;
  }

  // Determine max frequency rounded up to nearest 50
  function computeMaxFrequencies() {
    const max = Math.max(...Object.values(frequencyMap));
    return Math.ceil(max/50)*50 || 50; // at least 50
  }

  // Create Y-axis grid
  function createYAxis(gridSize=50) {
    const maxY = computeMaxFrequencies();
    const axesGroup = new THREE.Group();
    const axisGeom = new THREE.Geometry();
    const lines = [];
    const material = new THREE.LineBasicMaterial({color:0x888888});
    // Y-axis line
    axisGeom.vertices.push(new THREE.Vector3(0,0,0), new THREE.Vector3(0,maxY,0));
    // Grid lines
    for (let y=0; y<=maxY; y+=gridSize) {
      axisGeom.vertices.push(new THREE.Vector3(-15, y, 0), new THREE.Vector3(15, y, 0));
    }
    const axisLine = new THREE.Line(axisGeom, material);
    axesGroup.add(axisLine);
    // Y labels
    for (let y=0; y<=maxY; y+=gridSize) {
      const sprite = new THREE.Sprite(new THREE.SpriteMaterial({map:textTexture(y.toString()), transparent:true}));
      sprite.scale.set(20,10,1);
      sprite.position.set(-20, y, 0);
      axesGroup.add(sprite);
    }
    return axesGroup;
  }

  // Clear scene
  function clearScene() {
    while(scene.children.length>0) scene.remove(scene.children[0]);
    scene.add(ambientLight, directionalLight);
  }

  // Render 2D distribution bars
  function renderDistribution2D() {
    clearScene();
    const barWidth = 15, barDepth=10, spacing = 12;
    const maxY = computeMaxFrequencies();
    const scaleY = maxHeight / maxY;
    const group = new THREE.Group();
    const letters = Object.keys(frequencyMap).sort();
    for (let i=0; i<letters.length; i++) {
      const freq = frequencyMap[letters[i]];
      const barHeight = Math.max(freq, 1)*scaleY;
      const geometry = new THREE.BoxGeometry(barWidth, barHeight, barDepth);
      const color = frequencyColor(freq / maxY);
      const material = new THREE.MeshLambertMaterial({color});
      const bar = new THREE.Mesh(geometry, material);
      bar.position.set(i*(barWidth+spacing), barHeight/2, 0);
      group.add(bar);
      // Letter sprite
      const sprite = new THREE.Sprite(new THREE.SpriteMaterial({map:textTexture(letters[i], '20px Arial', '#000'), transparent:true}));
      sprite.scale.set(18,12,1);
      sprite.position.set(i*(barWidth+spacing), barHeight + 5, 0);
      group.add(sprite);
    }
    scene.add(group);
    scene.add(createYAxis(50));
    // Camera
    const left = -10, right=10, top=maxHeight+50, bottom=-10, near=1, far=500;
    camera = new THREE.OrthographicCamera(left, right, top, bottom, near, far);
    camera.position.set(0, maxHeight/2, 400);
    camera.lookAt(new THREE.Vector3(0, maxHeight/2, 0));
  }

  // Render 3D keyboard
  function renderKeyboard3D() {
    clearScene();
    const layout = keyboardLayouts[currentLayout];
    const maxY = computeMaxFrequencies();
    const scaleY = maxHeight / maxY;
    const keyboardGroup = new THREE.Group();
    const rowY = [30, 0, -30];
    layout.forEach((row, rIndex)=>{
      const letters = row.split('');
      const startX = -(row.length-1)*30/2;
      letters.forEach((l, i)=>{
        const freq = frequencyMap[l] || 0;
        const height = Math.max(freq, 1)*scaleY;
        const geometry = new THREE.CylinderGeometry(20, 20, height, 32, 1, true);
        // Flatten top
        geometry.translate(0/2, 0);
        const material = new THREE.MeshStandardMaterial({color: keyColors[getKeyRow(currentLayout, rIndex), false]});
        const key = new THREE.Mesh(geometry, material);
        key.position.set(startX + i*30, rowY[rIndex], 0);
        keyboardGroup.add(key);
        // Letter label
        const sprite = new THREE.Sprite(new THREE.SpriteMaterial({map:textTexture(l, '20px Arial', '#fff'), transparent:true}));
        sprite.scale.set(30,15,1);
        sprite.position.set(startX + i*30, rowY[rIndex]+height/2+5, 0);
        keyboardGroup.add(sprite);
      });
    });
    scene.add(keyboardGroup);
    scene.add(createYAxis(50));
    camera = new THREE.PerspectiveCamera(50, canvas.clientWidth/canvas.clientHeight, 1, 1000);
    camera.position.set(0, 250, 500);
    camera.lookAt(new THREE.Vector3(0, 0, 0));
  }

  function getKeyRow(layout, rowIndex) {
    if (layout === 'qwerty') {
      if (rowIndex===0) return 'top';
      if (rowIndex===1) return 'home';
      return 'bottom';
    } else {
      if (rowIndex===0) return 'top';
      if (rowIndex===1) return 'home';
      return 'bottom';
    }
  }

  // Update visualization based on current state
  function updateVisualization() {
    if (currentView === '2d') renderDistribution2D();
    else renderKeyboard3D();
    renderer.setSize(canvas.clientWidth, canvas.clientHeight);
  }

  // Event Listeners
  selectText.addEventListener('change', ()=>{
    frequencyMap = calculateFrequency(selectText.value);
    updateVisualization();
  });

  const toggleButtons = (btnOn, btnOff)=>{
    btnOn.classList.add('active'); btnOff.classList.remove('active');
  };

  btnPlot2D.addEventListener('click', ()=>{
    if (currentView !== '2d') {
      currentView = '2d';
      toggleButtons(btnPlot2D, btnPlot3D);
      updateVisualization();
    }
  });

  btnPlot3D.addEventListener('click', ()=>{
    if (currentView !== '3d') {
      currentView = '3d';
      toggleButtons(btnPlot3D, btnPlot2D);
      updateVisualization();
    }
  });

  btnKbdQW.addEventListener('click', ()=>{
    if (currentLayout !== 'qwerty') {
      currentLayout = 'qwerty';
      toggleButtons(btnKbdQW, btnKbdDvorak);
      if (currentView === '3d') updateVisualization();
    }
  });

  btnKbdDvorak.addEventListener('click', ()=>{
    if (currentLayout !== 'dvorak') {
      currentLayout = 'dvorak';
      toggleButtons(btnKbdDvorak, btnKbdQW);
      if (currentView === '3d') updateVisualization();
    }
  });

  // Resize handling
  window.addEventListener('resize', ()=>{
    renderer.setSize(canvas.clientWidth, canvas.clientHeight);
    if (camera) {
      if (camera.isOrthographicCamera) {
        const aspect = canvas.clientWidth / canvas.clientHeight;
        camera.left = -maxHeight*aspect / 2;
        camera.right = maxHeight*aspect / 2;
        camera.updateProjectionMatrix();
      } else {
        camera.aspect = canvas.clientWidth / canvas.clientHeight;
        camera.updateProjectionMatrix();
      }
    }
  });

  // Initial Setup
  frequencyMap = calculateFrequency(selectText.value);
  updateVisualization();
})();
</script>
</body>
</html>
