<script>
let particles = [];
let isPlaying = false;

const slidersConfig = [
  {id:'#slider-time', min:0, max:5, step:0.01, val:0, label:'#label-time', decimals:2},
  {id:'#slider-trail', min:0, max:100, step:1, val:5, label:'#label-trail', decimals:0},
  {id:'#slider-points', min:10, max:150, step:1, val:50, label:'#label-points', decimals:0},
  {id:'#slider-p-amp', min:0, max:0.4, step:0.001, val:0.2, label:'#label-p-amp', decimals:3},
