  {id:'#slider-s-amp', min:0, max:0.4, step:0.001, val:0.1, label:'#label-s-amp', decimals:3},
  {id:'#slider-s-wl', min:0.5, max:5, step:0.01, val:1, label:'#label-s-wl', decimals:2},
];

function setup() {
  const cnv = createCanvas(800,400);
  cnv.parent('canvas-container');
  noStroke();
  fill('#6A5ACD');

  // Initialize sliders
  slidersConfig.forEach(cfg => {
    let s = select(cfg.id);
    s.attribute('min', cfg.min);
    s.attribute('max', cfg.max);
    s.attribute('step', cfg.step);
    s.attribute('value', cfg.val);
    select(cfg.label).html(formatFloat(cfg.val, cfg.decimals));
    s.input(()=>{ // Update label on change
      const val = Number(s.value());
      select(cfg.label).html(formatFloat(val, cfg.decimals));
      if(cfg.id=='#slider-points') initParticles(); // Reinitialize points
    });
  });

  // Button listeners
  select('#btn-play-pause').mousePressed(togglePlayPause);
  select('#btn-reset').mousePressed(resetToDefaults);

  initParticles();
}

function draw() {
  background('#FFFFFF');
  const tSlider = select('#slider-time');
  let t = Number(tSlider.value());
  if(isPlaying){
    t += 0.02; // increments by 0.02 each frame (~50 fps)
    if(t > 5) t -= 6;
    tSlider.value(t.toFixed(2));
    select('#label-time').html(t.toFixed(2));
  }
  const p_amp = Number(select('#slider-p-amp').value());
  const p_wl = Number(select('#slider-p-wl').value());
  const s_amp = Number(select('#slider-s-amp').value());
  const s_wl = Number(select('#slider-s-wl').value());
  const trailLen = Number(select('#slider-trail').value());
  // Draw axes
  stroke(0); strokeWeight(1);
  const margin=40;
  const xMin=0, xMax=5; const yMin=-0.5, yMax=0.5;
  const xScale = (width-2*margin)/(xMax-xMin);
  const yScale = (height-2*margin)/(yMax-yMin);
  const toX = x => margin + (x - xMin) * xScale;
  const toY = y => height - margin - (y - yMin) * yScale;
  // X axis
  line(toX(xMin), toY(0), toX(xMax), toY(0));
  // Y axis
  line(toX(0), toY(yMin), toX(0), toY(yMax));
  // X ticks
  stroke(0);
  for(let xi=0; xi<=5; xi+=1){
    const x = toX(xi);
    line(x, toY(0)-5, x, toY(0)+5);
    noStroke(); textAlign(CENTER, TOP); fill(0); text(xi, x, toY(0)+8);
    stroke(0);
  }
  // Y ticks
  for(let yi=-0.5; yi<=0.5; yi+=0.1){
    const y = toY(yi);
    line(toX(0)-5, y, toX(0)+5, y);
    noStroke(); textAlign(LEFT, CENTER); fill(0); text(yi.toFixed(1), toX(0)+8, y);
    stroke(0);
  }
  noStroke();

  particles.forEach(p=>{
    const dx = p_amp * Math.sin( TWO_PI * (p.x0 / p_wl - t) );
    const dy = s_amp * Math.sin( TWO_PI * (p.x0 / s_wl - t) );
    const xFinal = p.x0 + dx;
    const yFinal = dy;
    // Update history
    p.history.push({x:xFinal,y:yFinal});
    if(p.history.length>trailLen) p.history.shift();
    // Draw vertical line
    stroke('#D3D3D3');
    line(toX(xFinal), toY(yMin), toX(xFinal), toY(yMax));
    // Draw trail
    if(trailLen>0){
      p.history.forEach(h=>{
        stroke(70,70,70,120);
        point(toX(h.x), toY(h.y));
      });
    }
    // Draw particle
    fill('#6A5ACD');
    ellipse(toX(xFinal), toY(yFinal), 8,8);
  });
}

function initParticles(){
  const n = int(Number(select('#slider-points').value()));
  particles = [];
  for(let i=0;i<n;i++){
    const x0 = map(i,0,n-1,0,5);
    particles.push({x0:x0, history:[]});
  }
}

function togglePlayPause(){
  isPlaying = !isPlaying;
  const btn = select('#btn-play-pause');
  btn.html(isPlaying?'Pause':'Play');
}

function resetToDefaults(){
  slidersConfig.forEach(cfg=>{
    let s=select(cfg.id);
    s.value(cfg.val);
    select(cfg.label).html(formatFloat(cfg.val,cfg.decimals));
  });
  isPlaying=false;
  select('#btn-play-pause').html('Play');
  initParticles();
}

function formatFloat(val,dec){
  return Number(val).toFixed(dec);
}
</script>
