      <label>displacement component</label>
      <div class="btn-group" id="btn-group-component">
        <button id="btn-component-x">X</button>
        <button id="btn-component-y">Y</button>
        <button id="btn-component-z">Z</button>
      </div>
    </div>
    <div>
      <label>fault type</label>
      <div class="btn-group" id="btn-group-fault-type">
        <button id="btn-fault-tensile">tensile</button>
        <button id="btn-fault-strike-slip">strike-slip</button>
        <button id="btn-fault-normal">normal</button>
      </div>
    </div>
  </div>
  <div id="plot-container">
    <div id="plot"></div>
  </div>
</div>
<script>
document.addEventListener('DOMContentLoaded', () => {
  const sliderDip = document.getElementById('slider-dip');
  const sliderDepth = document.getElementById('slider-depth');
  const sliderLimit = document.getElementById('slider-limit');
  const valueDip = document.getElementById('value-dip');
  const valueDepth = document.getElementById('value-depth');
  const valueLimit = document.getElementById('value-limit');
  const componentButtons = [
    document.getElementById('btn-component-x'),
    document.getElementById('btn-component-y'),
    document.getElementById('btn-component-z')
  ];
  const faultButtons = [
    document.getElementById('btn-fault-tensile'),
    document.getElementById('btn-fault-strike-slip'),
    document.getElementById('btn-fault-normal')
  ];
  let selectedComponent = 'Z';
  let selectedFault = 'tensile';
  const nu = 0.25; // Poisson's ratio
  const b = 1; // Dislocation magnitude (m)

  // Helper: set active class on button groups
  function setActiveButton(buttonGroup, button) {
    buttonGroup.forEach(btn => btn.classList.remove('active'));
    button.classList.add('active');
  }

  // Initialize defaults
  sliderDip.value = 0; sliderDepth.value = 500; sliderLimit.value = 1000;
  valueDip.textContent = sliderDip.value;
  valueDepth.textContent = sliderDepth.value;
  valueLimit.textContent = sliderLimit.value;

  setActiveButton(componentButtons, componentButtons[2]); // Z
  setActiveButton(faultButtons, faultButtons[0]); // tensile

  // Event listeners for sliders
  sliderDip.addEventListener('input', () => {
    valueDip.textContent = sliderDip.value;
    plotSurface();
  });
  sliderDepth.addEventListener('input', () => {
    valueDepth.textContent = sliderDepth.value;
    plotSurface();
  });
  sliderLimit.addEventListener('input', () => {
    valueLimit.textContent = sliderLimit.value;
    plotSurface();
  });

  // Event listeners for component buttons
  componentButtons.forEach(btn => {
    btn.addEventListener('click', () => {
      selectedComponent = btn.textContent;
      setActiveButton(componentButtons, btn);
      plotSurface();
    });
  });

  // Event listeners for fault buttons
  faultButtons.forEach(btn => {
    btn.addEventListener('click', () => {
      selectedFault = btn.textContent;
      setActiveButton(faultButtons, btn);
      plotSurface();
    });
  });

  // Core displacement calculation
  function calculateDisplacements(x, y, dipRad, depth, faultType) {
    const r = Math.sqrt(x*x + y*y + depth*depth);
    const sinDip = Math.sin(dipRad);
    const cosDip = Math.cos(dipRad);

    // Simplified model: radial displacement decreasing with distance
    let ux = 0, uy = 0, uz = 0;
    switch(faultType) {
      case 'tensile':
        uz = (b / (Math.PI * r)) * (depth / r); // downward/ upward depending on sign
        break;
      case 'strike-slip':
        ux = (b * sinDip * y) / (Math.PI * r * r);
        uy = (b * sinDip * x) / (Math.PI * r * r);
        break;
      case 'normal':
        uz = -(b / (Math.PI * r)) * (depth / r);
        break;
    }
    return {ux, uy, uz};
  }

  // Plotting function
  function plotSurface() {
    const dipDeg = parseFloat(sliderDip.value);
    const dipRad = dipDeg * Math.PI / 180;
    const depth = parseFloat(sliderDepth.value);
    const limit = parseFloat(sliderLimit.value);
    const numPoints = 50;
    const xs = Array.from({length:numPoints}, (_,i)=> -limit + i*(2*limit)/(numPoints-1));
    const ys = Array.from({length:numPoints}, (_,i)=> -limit + i*(2*limit)/(numPoints-1));
    const z = Array.from({length:numPoints}, () => new Array(numPoints).fill(0));

    for(let i=0;i<numPoints;i++){
      for(let j=0;j<numPoints;j++){
        const {ux, uy, uz} = calculateDisplacements(xs[i], ys[j], dipRad, depth, selectedFault);
        let val;
        switch(selectedComponent){
          case 'X': val = ux; break;
          case 'Y': val = uy; break;
          case 'Z': val = uz; break;
        }
        z[i][j] = val * 1000; // mm
      }
    }

    const surfaceData = [{
      type: 'surface',
      x: xs,
      y: ys,
      z: z,
      colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
      showscale: false,
      contours: {
        x: { show: true, color: 'black', width: 0.5 },
        y: { show: true, color: 'black', width: 0.5 },
        z: { show: false }
      },
      lighting: {ambient:0.8,diffuse:0.8, specular:0.2}
    }];

    const layout = {
      title: '',
      scene: {
        xaxis: {title: 'x (m)', range:[-limit, limit]},
        yaxis: {title: 'y (m)', range:[-limit, limit]},
        zaxis: {title: 'z (mm)', autorange:true},
        camera: {eye: {x:1.8, y:1.8, z:0.8}}
      },
      margin:{l:20,r:20,b:20,t:20}
    };

    Plotly.react('plot', surfaceData, layout);
  }

  // Initial plot
  plotSurface();
});
</script>
</body>
</html>
