<script>
const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

function besselJ0(x){
  const ax = Math.abs(x);
  if (ax < 8){
    const y = x*x;
    return 1.0 + y*(-0.1098628627e-1 + y*(0.2734510407e-2 + y*(-0.2073370639e-3 +
           y*(0.2093887211e-4 + y*(-0.2610621369e-5 + y*0.5228899218e-6)))));
  } else {
    const z = 8.0 / ax;
    return Math.sqrt(2/(Math.PI*ax)) * Math.cos(ax - Math.PI/4);
  }
}

const tSlider = document.getElementById('slider-t');
const kSlider = document.getElementById('slider-k');
const axesChk  = document.getElementById('checkbox-axes');
const tDisplay = document.getElementById('t-value-display');
const kDisplay = document.getElementById('k-value-display');
const formulaDiv = document.getElementById('formula-display');

function updateTDisplay(){
  tDisplay.textContent = parseFloat(tSlider.value).toFixed(2);
}
function updateKDisplay(){
  kDisplay.textContent = parseInt(kSlider.value,10);
}
function updateFormula(){
  const k = parseInt(kSlider.value,10);
  const z_k = zeros[k-1];
  const omega_k = z_k/2;
  formulaDiv.innerHTML = `\\\\(\\\\omega_{${k}} = ${omega_k.toFixed(2)} \\\\sqrt{g/l}\\)`;
  MathJax.typesetPromise([formulaDiv]);
}

tSlider.addEventListener('input', ()=>{
  updateTDisplay();
});
kSlider.addEventListener('input', ()=>{
  updateKDisplay();
  updateFormula();
});

axesChk.addEventListener('change', ()=>{
  // force redraw on next frame
});

updateTDisplay();
updateKDisplay();
updateFormula();

const sketch = (p)=>{
  let canvas;
  p.setup = ()=>{
    canvas = p.createCanvas(p.windowWidth*0.7-30, p.windowHeight-30);
    canvas.parent('canvas-container');
    p.noFill();
  };

  p.draw = ()=>{
    // set logical dims
    const xMin = -2, xMax = 2, yMin = -1.2, yMax = 0;
    const width = canvas.width, height = canvas.height;
    const scaleX = width/(xMax - xMin);
    const scaleY = height/(yMax - yMin);

    // background
    p.background('#f0f0f0');

    // axes
    if (axesChk.checked){
      p.stroke('#888888');
      p.strokeWeight(1);
      p.drawingContext.setLineDash([5,5]);

      // x axis
      const yZero = (0 - yMin)*scaleY;
      p.line(0, yZero, width, yZero);
      for(let i=-2;i<=2;i+=1){
        const x = (i - xMin)*scaleX;
        p.line(x, yZero-5, x, yZero+5);
        if(i!==0){
          p.textSize(12); p.fill(0);
          p.text(i, x-5, yZero+15);
        }
      }
      // y axis
      const xZero = (0 - xMin)*scaleX;
      p.line(xZero, 0, xZero, height);
      for(let i=-1;i<=0;i+=0.2){
        const y = (i - yMin)*scaleY;
        p.line(xZero-5, y, xZero+5, y);
        if(i!==0){
          p.textSize(12); p.fill(0);
          p.text(i.toFixed(1), xZero-15, y+5);
        }
      }
      p.drawingContext.setLineDash([]);
    }

    // parameters
    const t = parseFloat(tSlider.value);
    const k = parseInt(kSlider.value,10);
    const z_k = zeros[k-1];
    const omega_k = z_k/2;
    const timeFactor = Math.cos(omega_k * t);
    const amplitude = 0.8;

    // draw string
    p.stroke('#0066cc');
    p.strokeWeight(2);
    p.beginShape();
    for(let y_norm=0; y_norm<=1; y_norm+=0.01){
      const bessel_arg = z_k * Math.sqrt(y_norm);
      const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
      const y_pos = -y_norm;
      const px = (x_pos - xMin)*scaleX;
      const py = (y_pos - yMin)*scaleY;
      p.vertex(px, py);
    }
    p.endShape();
  };

  p.windowResized = ()=>{
    const newWidth = p.windowWidth*0.7-30;
    const newHeight = p.windowHeight-30;
    p.resizeCanvas(newWidth, newHeight);
  };
};

new p5(sketch, 'canvas-container');
</script>
</body>
</html>
