<script>
  // Global state
  const layeringPermutations = [
    [1, 2, 3, 4],
    [4, 3, 2, 1],
    [1, 3, 2, 4],
    [2, 4, 1, 3],
    [3, 1, 4, 2]
  ];
  let layeringOrderIndex = 0;

  // Helper to convert hex to {r,g,b}
  function hexToRgb(hex) {
    // Remove '#' if present
    hex = hex.replace(/^#/, '');
    if (hex.length === 3) {
      hex = hex.split('').map(char => char + char).join('');
    }
    const num = parseInt(hex, 16);
    return {
      r: (num >> 16) & 255,
      g: (num >> 8) & 255,
      b: num & 255
    };
  }

  // Update layering display
  function updateLayeringDisplay() {
    const order = layeringPermutations[layeringOrderIndex];
    document.getElementById('layering-display').textContent = order.join(', ');
  }

  // Reset to defaults
  function resetAll() {
    document.getElementById('slider-opacity1').value = 0.7;
    document.getElementById('slider-opacity2').value = 0.7;
    document.getElementById('slider-opacity3').value = 0.7;
    document.getElementById('slider-opacity4').value = 0.7;

    document.getElementById('color-picker1').value = '#00ff00';
    document.getElementById('color-picker2').value = '#0000ff';
    document.getElementById('color-picker3').value = '#ff0000';
    document.getElementById('color-picker4').value = '#000000';

    layeringOrderIndex = 0;
    updateLayeringDisplay();

    // Trigger redraw
    sketch.redraw();
  }

  // Event listeners
  document.getElementById('btn-reset').addEventListener('click', resetAll);

  document.getElementById('btn-layer-prev').addEventListener('click', () => {
    layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
    updateLayeringDisplay();
    sketch.redraw();
  });

  document.getElementById('btn-layer-next').addEventListener('click', () => {
    layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
    updateLayeringDisplay();
    sketch.redraw();
  });

  const opacitySliders = ['slider-opacity1', 'slider-opacity2', 'slider-opacity3', 'slider-opacity4'];
  opacitySliders.forEach(id => {
    document.getElementById(id).addEventListener('input', () => sketch.redraw());
  });

  const colorPickers = ['color-picker1', 'color-picker2', 'color-picker3', 'color-picker4'];
  colorPickers.forEach(id => {
    document.getElementById(id).addEventListener('input', () => sketch.redraw());
  });

  // p5.js sketch in instance mode
  const sketch = new p5(p => {
    let canvas;
    p.setup = () => {
      canvas = p.createCanvas(p.windowWidth - 210, p.windowHeight - p.select('#top-controls').size().height);
      canvas.parent('canvas-container');
      p.noLoop();
      updateLayeringDisplay();
    };

    p.windowResized = () => {
      p.resizeCanvas(p.windowWidth - 210, p.windowHeight - p.select('#top-controls').size().height);
      p.redraw();
    };

    p.draw = () => {
      const w = p.width;
      const h = p.height;
      p.background('#f0f0f0');

      const order = layeringPermutations[layeringOrderIndex];
      const circleData = {
        1: { cx: w * 0.35, cy: h * 0.4, d: w * 0.4 },
        2: { cx: w * 0.65, cy: h * 0.4, d: w * 0.4 },
        3: { cx: w * 0.5,  cy: h * 0.65, d: w * 0.4 },
        4: { cx: w * 0.5,  cy: h * 0.45, d: w * 0.3 }
      };

      // Draw circles in layering order
      order.forEach(num => {
        const data = circleData[num];
        const hex = document.getElementById(`color-picker${num}`).value;
        const rgb = hexToRgb(hex);
        const opacity = parseFloat(document.getElementById(`slider-opacity${num}`).value);
        p.noStroke();
        p.fill(rgb.r, rgb.g, rgb.b, opacity * 255);
        p.ellipse(data.cx, data.cy, data.d);
      });

      // Draw labels
      p.fill(0);
      p.textSize(32);
      p.textAlign(p.CENTER, p.CENTER);
      order.forEach(num => {
        const data = circleData[num];
        p.text(num.toString(), data.cx, data.cy);
      });
    };
  });
</script>
