<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Propylene Fugacity Viewer</title>
  <style>
    body{
      font-family: Arial, sans-serif;
      margin: 0;
      padding: 0;
      background:#f9f9f9;
      display:flex;
      flex-direction:column;
      align-items:center;
    }
    #app-container{
      width: 90%;
      max-width:1200px;
      margin-top:20px;
    }
    #control-panel{
      display:flex;
      flex-wrap:wrap;
      align-items:center;
      margin-bottom:20px;
    }
    #control-panel > div{
      margin-right:20px;
      margin-bottom:10px;
    }
    button{
      padding:6px 12px;
      margin-right:5px;
      background:#e0e0e0;
      border:none;
      cursor:pointer;
      border-radius:4px;
      font-size:14px;
    }
    button.selected{
      border:2px solid red;
    }
    label{
      font-size:14px;
    }
    #slider-pressure{
      margin-left:10px;
    }
    #plot-container{
      width:100%;
      height:600px;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <div id="control-panel">
      <div>
        <button id="btn-srk" class="selected">SRK</button>
        <button id="btn-pr">PR</button>
      </div>
      <div>
        <input type="checkbox" id="checkbox-aspen" checked>
        <label for="checkbox-aspen">comparison with Aspen Plus</label>
      </div>
      <div>
        <label for="slider-pressure">pressure P</label>
        <input type="range" id="slider-pressure" min="1" max="20" step=" value="10">
        <span id="display-pressure">10</span>
      </div>
    </div>

    <div id="plot-container"></div>
  </div>

  <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
  <script>
    // ---------- Constants ----------
    const Tc = 365.57;          // K
    const Pc = 46.65;           // bar
    const omega = 0.144;
    const R = 0.0831446;        // L*bar/(mol*K)

    const aspenX = [300,310,320,330,340,350,360,370,380,390,400];
    const aspenY = [0.868,0.881,0.891,0.900,0.909,0.917,0.924,0.930,0.937,0.942,0.946];

    // ---------- State ----------
    let selectedEos = 'SRK'; // 'SRK' or 'PR'
    let pressure = 10; // in bar
    let showAspen = true; // depends on pressure==10 && checkbox

    // ---------- Helper Functions ----------
    function cbrt(x){return Math.sign(x)*Math.pow(Math.abs(x),1/3);}
    function solveCubic(a,b,c){ // solves z^3 + a*z^2 + b*z + c = 0
      // Depress cubic: z = x - a/3
      const p = b - a*a/3;
      const q = 2*a*a*a/27 - a*b/3 + c;
      const discriminant = q*q/4 + p*p*p/27;
      let roots=[];
      if(discriminant>0){
        const sqrtD=Math.sqrt(discriminant);
        const s=cbrt(-q/2 + sqrtD);
        const t=cbrt(-q/2 - sqrtD);
        roots.push(s+t - a/3);
      }else{
        const r=Math.sqrt(-p/3);
        const phi=Math.acos(-q/(2*r*r*r));
        for(let k=0;k<3;k++){
          const angle=(phi+2*Math.PI*k)/3;
          roots.push(2*r*Math.cos(angle) - a/3);
        }
      }
      return roots.filter(r=>!isNaN(r));
    }
    function largestRealRoot(a,b,c){ // returns largest real root
      const roots=solveCubic(a,b,c).filter(r=>!isNaN(r));
      return Math.max(...roots);
    }

    function computeFugacity(eos, P, Tvals){
      const X=[];
      const Y=[];
      Tvals.forEach(T=>{
        const Tr = T/Tc;
        let a,b,m,alpha,a_eff,A,B;
        if(eos==='SRK'){
          a = 0.42748 * R*R * Tc*Tc / Pc;
          b = 0.08664 * R * Tc / Pc;
          m = 0.480 + 1.574*omega - 0.176*omega*omega;
          alpha = Math.pow(1 + m*(1-Math.sqrt(Tr)),2);
          a_eff = a*alpha;
          A = a_eff * P / (R*T*R*T);
          B = b * P / (R*T);
          const coeffs=[-1, (A - B - B*B), -A*B]; // z^3 - z^2 + ...
          const Z=largestRealRoot(...coeffs);
          const lnphi = Z -1 - Math.log(Z-B) - (A/B)*Math.log(1+B/Z);
          const phi=Math.exp(lnphi);
          X.push(T); Y.push(phi);
        }else{ // PR
          a = 0.45724 * R*R * Tc*Tc / Pc;
          b = 0.07780 * R * Tc / Pc;
          m = 0.37464 + 1.54226*omega - 0.26992*omega*omega;
          alpha = Math.pow(1 + m*(1-Math.sqrt(Tr)),2);
          a_eff = a*alpha;
          A = a_eff * P / (R*T*R*T);
          B = b * P / (R*T);
          const coeffs=[-(1-B), (A -3*B*B -2*B), -(A*B - B*B - B*B*B)];
          const Z=largestRealRoot(...coeffs);
          const lnphi = Z -1 - Math.log(Z-B) - (A/(2*Math.SQRT2*B))*Math.log((Z+(1+Math.SQRT2)*B)/(Z+(1-Math.SQRT2)*B));
          const phi=Math.exp(lnphi);
          X.push(T); Y.push(phi);
        }
      });
      return {x:X,y:Y};
    }

    // ---------- Plot ----------
    const tempRange = [];
    for(let t=300;t<=500;t+=1) tempRange.push(t);

    function initPlot(){
      const srkData = computeFugacity('SRK', pressure, tempRange);
      const prData  = computeFugacity('PR',  pressure, tempRange);
      const traceEos = {
        x:srkData.x,
        y:srkData.y,
        mode:'lines',
        name: 'SRK',
        line:{color:'red',width:2},
        visible: selectedEos==='SRK'
      };
      const tracePR = {
        x:prData.x,
        y:prData.y,
        mode:'lines',
        name: 'PR',
        line:{color:'blue',width:2},
        visible: selectedEos==='PR'
      };
      const traceAspen = {
        x:aspenX,
        y:aspenY,
        mode:'markers',
        name:'Aspen Plus',
        marker:{color:'green',symbol:'square',size:8},
        visible: showAspen
      };

      const data = [traceEos, tracePR, traceAspen];
      const layout = {
        title:'Propylene Gas-Phase Fugacity Coefficient',
        xaxis:{title:'temperature (K)', range:[295,505], showgrid:true, zeroline:false,
          gridcolor:'#d3d3d3', zerolinecolor:'#d3d3d3'},
        yaxis:{title:'gas-phase fugacity coefficient', range:[0.68,1.02],
          showgrid:true, zeroline:false, gridcolor:'#d3d3d3', zerolinecolor:'#d3d3d3'},
        paper_bgcolor:'#f0f0f0',
        plot_bgcolor:'#fff'
      };
      Plotly.newPlot('plot-container', data, layout);
    }

    // ---------- Update Functions ----------
    function updateEosTrace(){
      const newData = computeFugacity(selectedEos, pressure, tempRange);
      const update = {
        x: [newData.x, [...tempRange], [...tempRange]],
        y: [newData.y, [], []],
        visible: [selectedEos==='SRK', selectedEos==='PR', false]
      };
      Plotly.restyle('plot-container', update);
    }

    function updateAspenVisibility(){
      const visibility = (pressure===10 && document.getElementById('checkbox-aspen').checked);
      Plotly.restyle('plot-container', {visible: visibility}, [2]);
    }

    // ---------- Event Listeners ----------
    document.getElementById('btn-srk').addEventListener('click', ()=> {
      selectedEos='SRK';
      document.getElementById('btn-srk').classList.add('selected');
      document.getElementById('btn-pr').classList.remove('selected');
      updateEosTrace();
    });

    document.getElementById('btn-pr').addEventListener('click', ()=> {
      selectedEos='PR';
      document.getElementById('btn-pr').classList.add('selected');
      document.getElementById('btn-srk').classList.remove('selected');
      updateEosTrace();
    });

    const slider = document.getElementById('slider-pressure');
    slider.addEventListener('input', ()=>{
      pressure = parseInt(slider.value,10);
      document.getElementById('display-pressure').textContent = pressure;
      updateEosTrace();
      updateAspenVisibility();
    });

    document.getElementById('checkbox-aspen').addEventListener('change', ()=>{
      updateAspenVisibility();
    });

    // ---------- Initialize ----------
    initPlot();
  </script>
</body>
</html>
