  reaction: 0,         // 0-100
  erg: 0              // 0-100
};

/* --------------------- Utility Functions --------------------- */
function setMode(mode) {
  state.mode = mode;
  // Update button active class
  document.querySelectorAll('.mode-button').forEach(btn => btn.classList.remove('active'));
  document.querySelector('#btn-mode-' + mode).classList.add('active');
  // Update control visibility
  updateControlVisibility();
  // Reset sliders
  state.reaction = 0;
  state.erg = 0;
  document.getElementById('slider-reaction').value = 0;
  document.getElementById('slider-erg-ewg').value = 0;
}

function updateControlVisibility() {
  const inverseEl   = document.getElementById('inverse-container');
  const focusEl     = document.getElementById('focus-container');
  const reactionEl  = document.getElementById('reaction-slider-container');
  const ergEl       = document.getElementById('erg-ewg-slider-container');
  // Default hide all
  inverseEl.classList.add('hidden');
  focusEl.classList.add('hidden');
  reactionEl.classList.add('hidden');
  ergEl.classList.add('hidden');
  // Show based on mode
  switch(state.mode) {
    case 'da':
      focusEl.classList.remove('hidden');
      reactionEl.classList.remove('hidden');
      break;
    case 'regio':
      inverseEl.classList.remove('hidden');
      focusEl.classList.remove('hidden');
      reactionEl.classList.remove('hidden');
      ergEl.classList.remove('hidden');
      break;
    case 'stereo-phile':
    case 'stereo-diene':
      focusEl.classList.remove('hidden');
      break;
    case 'homo':
      inverseEl.classList.remove('hidden');
      ergEl.classList.remove('hidden');
      break;
  }
}

/* --------------------- Event Listeners --------------------- */
document.querySelectorAll('.mode-button').forEach(btn => {
  btn.addEventListener('click', () => setMode(btn.id.replace('btn-mode-','')));
});

document.getElementById('check-inverse').addEventListener('change', e => {
  state.inverse = e.target.checked;
});
document.getElementById('check-focus').addEventListener('change', e => {
  state.focus = e.target.checked;
});

document.getElementById('slider-reaction').addEventListener('input', e => {
  state.reaction = parseInt(e.target.value);
});
document.getElementById('slider-erg-ewg').addEventListener('input', e => {
  state.erg = parseInt(e.target.value);
});

function adjustSlider(id, delta) {
  const slider = document.getElementById(id);
  let val = parseInt(slider.value) + delta;
  if (val < 0) val = 0;
  if (val > 100) val = 100;
  slider.value = val;
  slider.dispatchEvent(new Event('input'));
}
document.getElementById('btn-reaction-minus').addEventListener('click', () => adjustSlider('slider-reaction', -1));
document.getElementById('btn-reaction-plus').addEventListener('click', () => adjustSlider('slider-reaction', 1));
document.getElementById('btn-erg-ewg-minus').addEventListener('click', () => adjustSlider('slider-erg-ewg', -1));
document.getElementById('btn-erg-ewg-plus').addEventListener('click', () => adjustSlider('slider-erg-ewg', 1));

/* --------------------- p5.js Sketch --------------------- */
let sketch = new p5((p) => {
  p.setup = function() {
    let cnv = p.createCanvas(900, 600);
    cnv.parent('canvas-container');
    cnv.id('viz-canvas');
    p.noLoop(); // we will manually call redraw()
  };
  p.draw = function() {
    p.background(255);
    p.textAlign(p.CENTER, p.CENTER);
    p.textSize(12);
    // Draw according to mode
    switch(state.mode) {
      case 'da':
        drawDielsAlder(p);
        break;
      case 'regio':
        drawRegioselectivity(p);
        break;
      case 'stereo-phile':
      case 'stereo-diene':
        drawStereospecificity(p);
        break;
      case 'homo':
        drawHomoLumo(p);
        break;
    }
  };

  function drawDielsAlder(p) {
    const step = state.reaction;
    const mid = p.width / 2;
    const y = p.height / 2;
    // factor for animation
    const posOffset = p.map(step, 0, 100, 200, 0);
    // Draw butadiene (left)
    p.push();
    p.translate(mid - 100 - posOffset, y);
    p.fill(200);
    p.stroke(0);
    p.rect(0, 0, 50, 20);
    p.text("Butadiene", 25, 10);
    p.pop();
    // Draw ethylene (right)
    p.push();
    p.translate(mid + 100 + posOffset, y);
    p.fill(200);
    p.stroke(0);
    p.rect(0, 0, 30, 10);
    p.text("Ethylene", 15, 5);
    p.pop();
    // If step > 50, show product
    if (step > 50) {
      const prog = p.map(step, 50, 100, 0, 1);
      const opacity = p.map(prog, 0, 1, 0, 255);
      // Cyclohexene
      p.push();
      p.translate(mid, y);
      p.fill(150, opacity);
      p.stroke(0, opacity);
      p.ellipse(0, 0, 80, 40);
      p.textSize(14);
      p.fill(0, opacity);
      p.noStroke();
      p.text("Cyclohexene", 0, 5);
      p.pop();
    }
    // Show focus highlight
    if (state.focus) {
      p.stroke(255, 0, 0, 150);
      p.strokeWeight(5);
      // Highlight potential new bonds
      p.beginShape();
      p.vertex(mid - 40, y);
      p.vertex(mid + 40, y);
      p.endShape();
      p.strokeWeight(1);
    }
  }

  function drawRegioselectivity(p) {
    const prog = p.map(state.reaction, 0, 100, 0, 1);
    const erg = p.map(state.erg, 0, 100, 0, 1);
    const ergOpp = 1 - erg;
    const inverse = state.inverse;
    // Locations for products
    const pos1 = {x: p.width / 4, y: p.height / 2};
    const pos2 = {x: 3 * p.width / 4, y: p.height / 2};
    // Determine which product is dominant
    const majorFactor = inverse ? ergOpp : erg;
    const minorFactor = 1 - majorFactor;
    // Draw product 1
    p.push();
    p.translate(pos1.x, pos1.y);
    p.fill(200, majorFactor * 255);
    p.stroke(0);
    p.rect(0, 0, 60, 30);
    p.text("Product 1", 30, 15);
    p.pop();
    // Draw product 2
    p.push();
    p.translate(pos2.x, pos2.y);
    p.fill(200, minorFactor * 255);
    p.stroke(0);
    p.rect(0, 0, 60, 30);
    p.text("Product 2", 30, 15);
    p.pop();

    // Show focus highlight
    if (state.focus) {
      p.stroke(255, 0, 0, 200);
      p.strokeWeight(4);
      // Highlight bonds (simplified)
      p.line(pos1.x, pos1.y + 20, pos2.x, pos2.y + 20);
      p.strokeWeight(1);
    }
  }

  function drawStereospecificity(p) {
    // Static drawing
    const centerX = p.width / 2;
    const centerY = p.height / 2;
    p.textSize(18);
    p.fill(0);
    p.text("Stereospecificity\n(Static View)", centerX, centerY-80);
    // Product shape
    p.push();
    p.translate(centerX, centerY);
    p.fill(180);
    p.stroke(0);
    p.ellipse(0, 0, 80, 60);
    if (state.focus) {
      p.stroke(255, 0, 0, 200);
      p.strokeWeight(4);
      p.line(-40,0,40,0);
      p.strokeWeight(1);
      p.stroke(0);
    }
    p.pop();
  }

  function drawHomoLumo(p) {
    const erg = p.map(state.erg, 0, 100, 0, 1);
    const inverse = state.inverse;
    const baseY = p.height / 2;
    // Energy levels
    const dieneHOMO = baseY - 80 + (inverse ? erg * 80 : 0);
    const dienophileHOMO = baseY + 80 - (inverse ? erg * 80 : 0);
    const dieneLUMO = baseY - 40 + (inverse ? erg * 40 : 0);
    const dienophileLUMO = baseY + 40 - (inverse ? erg * 40 : 0);
    // Draw horizontal lines
    p.stroke(0);
    p.line(100, dieneHOMO, 200, dieneHOMO);
    p.line(300, dienophileHOMO, 400, dienophileHOMO);
    p.line(100, dieneLUMO, 200, dieneLUMO);
    p.line(300, dienophileLUMO, 400, dienophileLUMO);
    // Labels
    p.noStroke();
    p.textSize(12);
    p.fill(0);
    p.text("Diene HOMO", 125, dieneHOMO - 10);
    p.text("Dienophile HOMO", 350, dienophileHOMO - 10);
    p.text("Diene LUMO", 125, dieneLUMO + 20);
    p.text("Dienophile LUMO", 350, dienophileLUMO + 20);
    // Interaction arrows
    const arrowColor = inverse ? 'red' : 'red';
    const arrowStroke = inverse ? 5 : 2;
    const arrowLength = inverse ? 60 : 120;
    const arrowStart = {x: 200, y: dieneHOMO};
    const arrowEnd = {x: 300, y: dienophileLUMO};
    p.stroke(arrowColor);
    p.strokeWeight(arrowStroke);
    p.line(arrowStart.x, arrowStart.y, arrowEnd.x, arrowEnd.y);
    // Draw arrowhead
    const angle = Math.atan2(arrowEnd.y - arrowStart.y, arrowEnd.x - arrowStart.x);
    p.push();
    p.translate(arrowEnd.x, arrowEnd.y);
    p.rotate(angle);
    p.line(0, 0, -10, 5);
    p.line(0, 0, -10, -5);
    p.pop();

    // Secondary arrow
    const arrowStart2 = {x: 200, y: dienophileHOMO};
    const arrowEnd2 = {x: 300, y: dieneLUMO};
    p.stroke(inverse ? 'black' : 'red');
    p.strokeWeight(inverse ? 2 : 5);
    p.line(arrowStart2.x, arrowStart2.y, arrowEnd2.x, arrowEnd2.y);
    // Arrowhead
    const angle2 = Math.atan2(arrowEnd2.y - arrowStart2.y, arrowEnd2.x - arrowStart2.x);
    p.push();
    p.translate(arrowEnd2.x, arrowEnd2.y);
    p.rotate(angle2);
    p.line(0, 0, -10, 5);
    p.line(0, 0, -10, -5);
    p.pop();

    // Show focus highlight
    if (state.focus) {
      p.stroke(255,0,0,200);
      p.strokeWeight(4);
      p.line(150, dieneHOMO, 250, dienophileLUMO);
      p.strokeWeight(1);
      p.stroke(0);
    }
  }
});

sketch.redraw();
</script>
</body>
</html>
