<title>A Visual Proof of Viviani's Theorem</title>
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
<style>
  body {
    margin: 0;
    font-family: Arial, Helvetica, sans-serif;
    background:#f8f8f8;
  }
  #demo-container {
    width: 640px;
    margin: 20px auto;
    background: #fff;
    padding: 10px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.15);
  }
  #control-panel {
    text-align: center;
    margin-bottom: 10px;
  }
  #control-panel label {
    margin-right: 8px;
    font-weight: bold;
  }
  #control-panel input[type="radio"] {
    margin: 0 4px;
  }
  #canvas-container {
    width: 600px;
    margin: 0 auto;
  }
</style>
</head>
<body>
<div id="demo-container">
  <div id="control-panel">
    <label>step</label>
    <input type="radio" id="step1" name="step-selector" value="1" checked>
    <label for="step1">1</label>
    <input type="radio" id="step2" name="step-selector" value="2">
    <label for="step2">2</label>
    <input type="radio" id="step3" name="step-selector" value="3">
    <label for="step3">3</label>
  </div>
  <div id="canvas-container"></div>
</div>

<script>
let currentStep = '1';
let P;
let dragging = false;
let radiusP = 12;

// Triangle vertices
let A, B, C;
// Direction vectors
let ABvec, BCvec, ACvec;
let ABunit, BCunit, ACunit;

// Precompute foot points
let footAB, footBC, footAC;
let dAB, dBC, dAC;

// Helper p5.js vector
function vec(x, y){ return createVector(x, y); }

function setup() {
  createCanvas(600, 520);
  canvas.parent('canvas-container');
  // Triangle coordinates
  let margin = 50;
  A = vec(margin, height - margin);
  B = vec(width - margin, height - margin);
  C = vec(width/2, height - margin - (width - 2*margin) * sqrt(3)/2);
  ABvec = B.copy().sub(A);
  BCvec = C.copy().sub(B);
  ACvec = C.copy().sub(A);
  ABunit = ABvec.copy().normalize();
  BCunit = BCvec.copy().normalize();
  ACunit = ACvec.copy().normalize();

  // Initial P
  P = vec(width*0.4, height*0.6);
  constrainP();

  // Radio button events
  selectAll('input[name="step-selector"]').forEach(r => {
    r.changed(() => { currentStep = r.value(); redraw(); });
  });

  noLoop();
}

function draw() {
  background(255);
  // Draw main triangle
  stroke(0);
  strokeWeight(2);
  noFill();
  triangle(A.x, A.y, B.x, B.y, C.x, C.y);
  // Label vertices
  fill(0); noStroke();
  textSize(14);
  text('A', A.x-15, A.y-5);
  text('B', B.x+5, B.y-5);
  text('C', C.x-10, C.y-5);

  // Altitude from C to AB (simple line)
  let midAB = vec((A.x+B.x)/2, (A.y+B.y)/2);
  stroke(173, 216, 230); strokeWeight(1);
  drawDashedLine(C.x, C.y, midAB.x, midAB.y, 5);

  // Compute feet and distances
  let resAB = footOnLineSegment(P, A, B); footAB = resAB.foot; dAB = resAB.dist;
  let resBC = footOnLineSegment(P, B, C); footBC = resBC.foot; dBC = resBC.dist;
  let resAC = footOnLineSegment(P, A, C); footAC = resAC.foot; dAC = resAC.dist;

  // Draw P
  if (currentStep === '3') {
    // Grey translucent marker for original location
    drawPoint(P, 10, color(200,200,200,150));
  } else {
    drawPoint(P, 12, color(120));
  }

  // Step-specific drawings
  if (currentStep === '1') {
    // Perpendiculars & feet
    stroke(0); strokeWeight(1);
    line(P.x, P.y, footAB.x, footAB.y);
    line(P.x, P.y, footBC.x, footBC.y);
    line(P.x, P.y, footAC.x, footAC.y);
    noStroke();
    fill(0,0,255);
    ellipse(footAB.x, footAB.y, 6,6);
    ellipse(footBC.x, footBC.y, 6,6);
    ellipse(footAC.x, footAC.y, 6,6);
  } else if (currentStep === '2') {
    let sideR = (2*dAB)/sqrt(3);
    let sideG = (2*dAC)/sqrt(3);
    let sideB = (2*dBC)/sqrt(3);
    // Red triangle (base on AB)
    fill(255,0,0,128); noStroke();
    let baseL_R = footAB.copy().add(ABunit.copy().mult(sideR/2));
    let baseR_R = footAB.copy().sub(ABunit.copy().mult(sideR/2));
    triangle(P.x, P.y, baseL_R.x, baseL_R.y, baseR_R.x, baseR_R.y);
    // Green triangle (base on AC)
    fill(0,255,0,128); noStroke();
    let baseL_G = footAC.copy().add(ACunit.copy().mult(sideG/2));
    let baseR_G = footAC.copy().sub(ACunit.copy().mult(sideG/2));
    triangle(P.x, P.y, baseL_G.x, baseL_G.y, baseR_G.x, baseR_G.y);
    // Blue triangle (base on BC)
    fill(0,0,255,128); noStroke();
    let baseL_B = footBC.copy().add(BCunit.copy().mult(sideB/2));
    let baseR_B = footBC.copy().sub(BCunit.copy().mult(sideB/2));
    triangle(P.x, P.y, baseL_B.x, baseL_B.y, baseR_B.x, baseR_B.y);
    // Labels on foot points
    fill(0); noStroke();
    textSize(12);
    text('x', footAC.x - 8, footAC.y - 8);
    text('y', footBC.x - 8, footBC.y - 8);
  } else if (currentStep === '3') {
    let sideR = (2*dAB)/sqrt(3);
    let sideG = (2*dAC)/sqrt(3);
    let sideB = (2*dBC)/sqrt(3);
    // Red triangle at bottom
    fill(255,0,0,128); noStroke();
    let baseL_R = vec(width/2, height - margin).add(ABunit.copy().mult(-sideR/2));
    let baseR_R = vec(width/2, height - margin).add(ABunit.copy().mult(sideR/2));
    triangle(baseL_R.x, baseL_R.y, baseR_R.x, baseR_R.y, width/2, height - margin - dAB);
    // Blue triangle on top of red
    fill(0,0,255,128); noStroke();
    let topBlue = vec(width/2, height - margin - dAB);
    let baseL_B = topBlue.copy().add(ABunit.copy().mult(-sideB/2));
    let baseR_B = topBlue.copy().add(ABunit.copy().mult(sideB/2));
    triangle(baseL_B.x, baseL_B.y, baseR_B.x, baseR_B.y, topBlue.x, topBlue.y);
    // Green triangle on top of blue
    fill(0,255,0,128); noStroke();
    let topGreen = vec(width/2, height - margin - dAB - dBC);
    let baseL_G = topGreen.copy().add(ABunit.copy().mult(-sideG/2));
    let baseR_G = topGreen.copy().add(ABunit.copy().mult(sideG/2));
    triangle(baseL_G.x, baseL_G.y, baseR_G.x, baseR_G.y, topGreen.x, topGreen.y);
    // Label y at top vertex of blue
    fill(0); noStroke();
    textSize(12);
    text('y', topBlue.x + 5, topBlue.y - 5);
  }
}

// Draw a circle point with optional label
function drawPoint(pt, rad, clr){
  fill(clr);
  noStroke();
  ellipse(pt.x, pt.y, rad*2, rad*2);
  fill(0);
  stroke(0);
  strokeWeight(1);
  line(pt.x - rad/2, pt.y, pt.x + rad/2, pt.y);
  line(pt.x, pt.y - rad/2, pt.x, pt.y + rad/2);
  fill(0);
  noStroke();
  textSize(12);
  text('P', pt.x + rad + 2, pt.y + 4);
}

// Return foot point and distance to line segment AB
function footOnLineSegment(P, A, B){
  let AB = B.copy().sub(A);
  let t = AB.dot(P.copy().sub(A)) / AB.dot(AB);
  t = constrain(t, 0, 1);
  let foot = A.copy().add(AB.copy().mult(t));
  let d = dist(P.x, P.y, foot.x, foot.y);
  return {foot: foot, dist: d};
}

function mousePressed(){
  if (dist(mouseX, mouseY, P.x, P.y) <= radiusP){
    dragging = true;
  }
}

function mouseDragged(){
  if (dragging){
    P.x = mouseX;
    P.y = mouseY;
    constrainP();
    redraw();
  }
}

function mouseReleased(){
  dragging = false;
}

// Keep P inside triangle ABC
function constrainP(){
  // If in triangle, do nothing
  if (pointInTriangle(P, A, B, C)) return;
  // Else find closest point on triangle edges
  let p1 = closestPointOnSegment(P, A, B);
  let p2 = closestPointOnSegment(P, B, C);
  let p3 = closestPointOnSegment(P, C, A);
  let d1 = dist(P.x, P.y, p1.x, p1.y);
  let d2 = dist(P.x, P.y, p2.x, p2.y);
  let d3 = dist(P.x, P.y, p3.x, p3.y);
  let minD = min(d1, d2, d3);
  if (minD === d1) P = p1;
  else if (minD === d2) P = p2;
  else P = p3;
}

// Barycentric coordinate test
function pointInTriangle(p, a, b, c){
  let v0 = vec(c.x - a.x, c.y - a.y);
  let v1 = vec(b.x - a.x, b.y - a.y);
  let v2 = vec(p.x - a.x, p.y - a.y);
  let dot00 = v0.dot(v0);
  let dot01 = v0.dot(v1);
  let dot02 = v0.dot(v2);
  let dot11 = v1.dot(v1);
  let dot12 = v1.dot(v2);
  let invDenom = 1 / (dot00 * dot11 - dot01 * dot01);
  let u = (dot11 * dot02 - dot01 * dot12) * invDenom;
  let v = (dot00 * dot12 - dot01 * dot02) * invDenom;
  return u >= 0 && v >= 0 && (u + v) <= 1;
}

function closestPointOnSegment(p, a, b){
  let ab = vec(b.x - a.x, b.y - a.y);
  let t = ab.dot(p.copy().sub(a)) / ab.dot(ab);
  t = constrain(t, 0, 1);
  return a.copy().add(ab.mult(t));
}

// Draw dashed line (utility)
function drawDashedLine(x1, y1, x2, y2, len){
  let dx = x2 - x1;
  let dy = y2 - y1;
  let distLine = dist(x1, y1, x2, y2);
  let dashCount = floor(distLine / len);
  let dash = createVector(dx/ dashCount, dy/ dashCount);
  for (let i = 0; i < dashCount; i++){
    if (i % 2 === 0){
      line(x1 + dash.x * i, y1 + dash.y * i, x1 + dash.x * (i+1), y1 + dash.y * (i+1));
    }
  }
}
</script>
</body>
</html>
