</div>

<script>
(() => {
  // ---------- GLOBALS ----------
  const orbitalElements = {
    Mercury: { a: 0.3871, e: 0.2056, i: 7.005, Ω:48.331, ω:29.124, M:174.796 },
    Venus:   { a: 0.7233, e: 0.0068, i: 3.395, Ω:76.680, ω:54.884, M:50.416 },
    Earth:   { a: 1.0000, e: 0.0167, i: 0.0000, Ω:-11.260 , ω:114.208, M:358.617 },
    Mars:    { a: 1.5237, e: 0.0934, i: 1.850, Ω:49.562, ω:286.502, M:19.412 },
    Ceres:   { a: 2.7691, e: 0.0760, i:10.593, Ω:80.393, ω:73.598, M:77.372 },
    Pluto:   { a: 39.482,  e: 0.2488, i:17.160, Ω:110.299, ω:113.834, M:14.882 },
    Haumea:  { a: 43.132,  e: 0.1912, i:28.220, Ω:122.155, ω:239.544, M:204.030 },
    Makemake:{ a: 45.791,  e: 0.1559, i:29.006, Ω:79.623, ω:294.821, M:95.895 },
    Eris:    { a: 67.668,  e: 0.4418, i:44.187, Ω:35.951, ω:151.291, M:206.510 }
  };
  const bodyColors = {
    Mercury:'#a8a8a8', Venus:'#ffe5ab', Earth:'#88c', Mars:'#e44',
    dwarf:'#9013fe'
  };
  const bodyRadii = {Mercury:0.05, Venus:0.08, Earth:0.09, Mars:0.07, dwarf:0.12};

  const J2000 = Date.UTC(2000,0,1,12,0,0);

  let selectedDwarf = 'Pluto';
  let isPlaying = false;

  // ---------- THREE.JS SETUP ----------
  const canvas = document.getElementById('3d-canvas');
  const renderer = new THREE.WebGLRenderer({canvas, antialias:true, alpha:true});
  renderer.setPixelRatio(window.devicePixelRatio);
  const scene = new THREE.Scene();
  scene.background = new THREE.Color(0x222222);

  const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 500);
  camera.position.set(0,15,30);
  camera.lookAt(0,0,0);

  const controls = new THREE.OrbitControls(camera, canvas);
  controls.enableDamping = true;

  const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
  scene.add(ambientLight);
  const pointLight = new THREE.PointLight(0xffffff, 1, 0);
  pointLight.position.set(0,0,0);
  scene.add(pointLight);

  // Sun
  const sunGeom = new THREE.SphereGeometry(0.5, 32, 32);
  const sunMat = new THREE.MeshBasicMaterial({color:0xffff00});
  const sunMesh = new THREE.Mesh(sunGeom, sunMat);
  scene.add(sunMesh);

  // Main planets
  const planetMeshes = {};
  const planetOrbitLines = {};

  function createPlanet(name){
    const geom = new THREE.SphereGeometry(bodyRadii[name], 16, 16);
    const mat = new THREE.MeshLambertMaterial({color:bodyColors[name]});
    const mesh = new THREE.Mesh(geom, mat);
    scene.add(mesh);
    planetMeshes[name] = mesh;
  }
  ['Mercury','Venus','Earth','Mars'].forEach(createPlanet);

  // Function to create orbit line
  function createOrbitLine(el, color){
    const points = [];
    const n = 500;
    for(let i=0;i<=n;i++){
      const theta = (i/n)*2*Math.PI;
      const r = el.a*(1-el.e*el.e)/(1+el.e*Math.cos(theta));
      let x = r*Math.cos(theta), y = r*Math.sin(theta), z = 0;
      // rotate: Ω -> i -> ω
      let vec = new THREE.Vector3(x,y,z);
      vec.applyAxisAngle(new THREE.Vector3(0,0,1), degToRad(el.Ω));
      vec.applyAxisAngle(new THREE.Vector3(1,0,0), degToRad(el.i));
      vec.applyAxisAngle(new THREE.Vector3(0,0,1), degToRad(el.ω));
      points.push(vec);
    }
    const curve = new THREE.BufferGeometry().setFromPoints(points);
    const mat = new THREE.LineBasicMaterial({color});
    return new THREE.Line(curve, mat);
  }

  function initMainOrbits(){
    ['Mercury','Venus','Earth','Mars'].forEach(name=>{
      const line = createOrbitLine(orbitalElements[name], 0x00bfff);
      scene.add(line);
      planetOrbitLines[name] = line;
    });
  }
  initMainOrbits();

  // Dwarf planet
  let dwarfMesh = null;
  let dwarfOrbit = null;

  function updateDwarf(){
    // Remove previous
    if(dwarfMesh) scene.remove(dwarfMesh);
    if(dwarfOrbit) scene.remove(dwarfOrbit);
    const el = orbitalElements[selectedDwarf];
    // Mesh
    const geom = new THREE.SphereGeometry(objectRadius(dwarf), 16, 16);
    const mat = new THREE.MeshLambertMaterial({color:bodyColors.dwarf});
    dwarfMesh = new THREE.Mesh(geom, mat);
    scene.add(dwarfMesh);
    // Orbit
    dwarfOrbit = createOrbitLine(el, 0xff0000);
    scene.add(dwarfOrbit);
    updateAllPositions(); // update position instantly
  }
  function objectRadius(name){
    return bodyRadii[name] || bodyRadii['dwarf'];
  }

  // ---------- UTILS ----------
  function degToRad(d){return d*Math.PI/180;}
  function radToDeg(r){return r*180/Math.PI;}

  function calculatePosition(date, el){
    const d = (date.getTime()-J2000)/86400000; // days since epoch
    const a = el.a;
    const e = el.e;
    const i = degToRad(el.i);
    const Ω = degToRad(el.Ω);
    const ω = degToRad(el.ω);
    const M0 = degToRad(el.M);

    // Orbital period
    const P = 365.25 * Math.pow(a,1.5);
    // Mean anomaly
    let M = (M0 + degToRad(360)*(d/P)) % (2*Math.PI);
    // Kepler's equation: M = E - e*sin(E)
    let E = M; // initial guess
    for(let j=0;j<10;j++){
      E = E - (E - e*Math.sin(E) - M)/(1 - e*Math.cos(E));
    }
    const nu = 2*Math.atan2(Math.sqrt(1+e)*Math.sin(E/2),
                            Math.sqrt(1-e)*Math.cos(E/2));
    const r = a*(1 - e*Math.cos(E));

    const x_orb = r*Math.cos(nu);
    const y_orb = r*Math.sin(nu);
    let vec = new THREE.Vector3(x_orb, y_orb, 0);
    // rotate
    vec.applyAxisAngle(new THREE.Vector3(0,0,1), Ω);
    vec.applyAxisAngle(new THREE.Vector3(1,0,0), i);
    vec.applyAxisAngle(new THREE.Vector3(0,0,1), ω);

    return vec;
  }

  function formatDate(date){
    const days = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];
    const months= ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    const dw = days[date.getUTCDay()];
    const d = date.getUTCDate();
    const m = months[date.getUTCMonth()];
    const y = date.getUTCFullYear();
    return `${dw} ${d} ${m} ${y} 00:00:00`;
  }

  function updateDateDisplay(){
    const days = Number(slider.value);
    const date = new Date(Date.UTC(2000,0,1) + days*86400000);
    document.getElementById('date-display').textContent = formatDate(date);
  }

  function updateAllPositions(){
    const days = Number(slider.value);
    const date = new Date(Date.UTC(2000,0,1)+days*86400000);
    // Planets
    ['Mercury','Venus','Earth','Mars'].forEach(name=>{
      const pos = calculatePosition(date, orbitalElements[name]);
      planetMeshes[name].position.copy(pos);
    });
    // Dwarf
    if(dwarfMesh){
      const pos = calculatePosition(date, orbitalElements[selectedDwarf]);
      dwarfMesh.position.copy(pos);
    }
  }

  // ---------- INTERACTIONS ----------
  const slider = document.getElementById('time-slider');
  const playBtn = document.getElementById('play-pause-button');
  const dwarfButtons = document.querySelectorAll('#dwarf-planet-selector button');

  slider.addEventListener('input', ()=>{
    updateDateDisplay();
    updateAllPositions();
    renderer.render(scene, camera);
  });
  slider.dispatchEvent(new Event('input')); // init

  dwarfButtons.forEach(btn=>{
    btn.addEventListener('click',()=>{
      dwarfButtons.forEach(b=>b.classList.remove('active'));
      btn.classList.add('active');
      selectedDwarf = btn.getAttribute('data-planet');
      updateDwarf();
    });
  });

  playBtn.addEventListener('click',()=>{
    isPlaying = !isPlaying;
    playBtn.textContent = isPlaying ? '⏸' : '▶';
  });

  // ---------- ANIMATION LOOP ----------
  function animate(){
    requestAnimationFrame(animate);
    if(isPlaying){
      slider.value = (Number(slider.value)+2)% (Number(slider.max)+1);
      slider.dispatchEvent(new Event('input'));
    }
    controls.update();
    renderer.render(scene, camera);
  }
  animate();

  // ---------- RESIZE ----------
  window.addEventListener('resize',()=>{
    const rect = document.getElementById('visualization-container').getBoundingClientRect();
    renderer.setSize(rect.width, rect.height);
    camera.aspect = rect.width/rect.height;
    camera.updateProjectionMatrix();
  });
  window.dispatchEvent(new Event('resize'));

  // Initialize dwarf
  updateDwarf();
})();
</script>
</body>
</html>
