function interpolateCFC(cfc, year) {
    const points = cfcData[cfc].data;
    if (year <= points[0][0]) return points[0][1];
    if (year >= points[points.length - 1][0]) return points[points.length - 1][1];
    for (let i = 0; i < points.length - 1; i++) {
